              try {
                console.log(`üíÄ –£–±–∏–≤–∞–µ–º —Å—Ç–∞—Ä—ã–π –ø—Ä–æ—Ü–µ—Å—Å ${pid} –¥–ª—è —Ç–æ–∫–µ–Ω–∞ ${tokenId}`);
                execSync(`kill -9 ${pid}`, { encoding: 'utf8' });
