            });
            continue;
          }

          // –°–æ–∑–¥–∞–µ–º URL –¥–ª—è –¥–æ—Å—Ç—É–ø–∞ –∫ —Ñ–∞–π–ª—É
          const fileUrl = `/uploads/${file.filename}`;

          // –°–æ—Ö—Ä–∞–Ω—è–µ–º –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é –æ —Ñ–∞–π–ª–µ –≤ –±–∞–∑–µ –¥–∞–Ω–Ω—ã—Ö
          const mediaFile = await storage.createMediaFile({
