import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Switch } from '@/components/ui/switch';
import { Separator } from '@/components/ui/separator';
import { ScrollArea } from '@/components/ui/scroll-area';
import { 
  Tooltip, 
  TooltipContent, 
  TooltipProvider, 
  TooltipTrigger 
} from '@/components/ui/tooltip';
import { 
  AlertCircle, 
  ArrowRight, 
  CheckCircle, 
  Link2, 
  Settings, 
  Sparkles, 
  Zap 
} from 'lucide-react';
import { Node, Connection } from '@/types/bot';
import { ConnectionManager, ConnectionSuggestion } from '@/utils/connection-manager';

interface AutoConnectionPanelProps {
  nodes: Node[];
  connections: Connection[];
  onConnectionAdd: (connection: Connection) => void;
  onNodesUpdate: (nodes: Node[]) => void;
  autoButtonCreation: boolean;
  onAutoButtonCreationChange: (enabled: boolean) => void;
}

export function AutoConnectionPanel({
  nodes,
  connections,
  onConnectionAdd,
  onNodesUpdate,
  autoButtonCreation,
  onAutoButtonCreationChange
}: AutoConnectionPanelProps) {
  const [suggestions, setSuggestions] = useState<ConnectionSuggestion[]>([]);
  const [isGenerating, setIsGenerating] = useState(false);
  const [appliedSuggestions, setAppliedSuggestions] = useState<Set<string>>(new Set());

  const connectionManager = new ConnectionManager({
    nodes,
    connections,
    autoButtonCreation
  });

  useEffect(() => {
    generateSuggestions();
  }, [nodes, connections]);

  const generateSuggestions = () => {
    setIsGenerating(true);
    try {
      const newSuggestions = connectionManager.generateConnectionSuggestions();
      setSuggestions(newSuggestions);
    } finally {
      setIsGenerating(false);
    }
  };

  const applySuggestion = (suggestion: ConnectionSuggestion) => {
    try {
      const { connection, updatedNodes } = connectionManager.createConnection(
        suggestion.connection.source,
        suggestion.connection.target,
        {
          autoCreateButton: autoButtonCreation,
          buttonText: suggestion.suggestedButton.text,
          buttonAction: suggestion.suggestedButton.action
        }
      );

      onConnectionAdd(connection);
      onNodesUpdate(updatedNodes);
      setAppliedSuggestions(prev => new Set([...prev, suggestion.id]));
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –ø—Ä–∏–º–µ–Ω–µ–Ω–∏–∏ –ø—Ä–µ–¥–ª–æ–∂–µ–Ω–∏—è:', error);
    }
  };

  const applyAllHighConfidence = () => {
    const highConfidenceSuggestions = suggestions.filter(s => s.confidence > 0.8);

    highConfidenceSuggestions.forEach(suggestion => {
      if (!appliedSuggestions.has(suggestion.id)) {
        applySuggestion(suggestion);
      }
    });
  };

  const syncButtonsWithConnections = () => {
    const updatedNodes = connectionManager.syncButtonsWithConnections();
    onNodesUpdate(updatedNodes);
  };

  const cleanupOrphanedButtons = () => {
    const updatedNodes = connectionManager.cleanupOrphanedButtons();
    onNodesUpdate(updatedNodes);
  };

  const getNodeName = (nodeId: string) => {
    const node = nodes.find(n => n.id === nodeId);
    if (!node) return '–ù–µ–∏–∑–≤–µ—Å—Ç–Ω—ã–π —É–∑–µ–ª';

    switch (node.type) {
      case 'start': return '–°—Ç–∞—Ä—Ç';
      case 'command': return node.data.command || '–ö–æ–º–∞–Ω–¥–∞';
      case 'message': return node.data.messageText?.slice(0, 20) + '...' || '–°–æ–æ–±—â–µ–Ω–∏–µ';
      case 'keyboard': return '–ö–ª–∞–≤–∏–∞—Ç—É—Ä–∞';
      case 'photo': return '–§–æ—Ç–æ';
      case 'condition': return '–£—Å–ª–æ–≤–∏–µ';
      case 'input': return '–í–≤–æ–¥';
      default: return node.type;
    }
  };

  const getConfidenceColor = (confidence: number) => {
    if (confidence >= 0.8) return 'bg-green-500';
    if (confidence >= 0.6) return 'bg-yellow-500';
    return 'bg-red-500';
  };

  const getConfidenceText = (confidence: number) => {
    if (confidence >= 0.8) return '–í—ã—Å–æ–∫–∞—è';
    if (confidence >= 0.6) return '–°—Ä–µ–¥–Ω—è—è';
    return '–ù–∏–∑–∫–∞—è';
  };

  return (
    <Card className="w-full max-w-md">
      <CardHeader className="pb-4">
        <div className="flex items-center justify-between">
          <CardTitle className="text-lg flex items-center gap-2">
            <Link2 className="h-5 w-5" />
            –ê–≤—Ç–æ—Å–æ–µ–¥–∏–Ω–µ–Ω–∏—è
          </CardTitle>
          <Button
            variant="outline"
            size="sm"
            onClick={generateSuggestions}
            disabled={isGenerating}
          >
            {isGenerating ? (
              <Settings className="h-4 w-4 animate-spin" />
            ) : (
              <Sparkles className="h-4 w-4" />
            )}
          </Button>
        </div>
        <CardDescription>
          –ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–æ–µ —Å–æ–∑–¥–∞–Ω–∏–µ —Å–≤—è–∑–µ–π –º–µ–∂–¥—É —É–∑–ª–∞–º–∏
        </CardDescription>
      </CardHeader>

      <CardContent className="space-y-4">
        {/* –ù–∞—Å—Ç—Ä–æ–π–∫–∏ */}
        <div className="flex items-center justify-between">
          <div className="space-y-1">
            <label className="text-sm font-medium">–ê–≤—Ç–æ—Å–æ–∑–¥–∞–Ω–∏–µ –∫–Ω–æ–ø–æ–∫</label>
            <p className="text-xs text-muted-foreground">
              –°–æ–∑–¥–∞–≤–∞—Ç—å –∫–Ω–æ–ø–∫–∏ –ø—Ä–∏ –¥–æ–±–∞–≤–ª–µ–Ω–∏–∏ —Å–≤—è–∑–µ–π
            </p>
          </div>
          <Switch
            checked={autoButtonCreation}
            onCheckedChange={onAutoButtonCreationChange}
          />
        </div>

        <Separator />

        {/* –ë—ã—Å—Ç—Ä—ã–µ –¥–µ–π—Å—Ç–≤–∏—è */}
        <div className="space-y-2">
          <h4 className="text-sm font-medium">–ë—ã—Å—Ç—Ä—ã–µ –¥–µ–π—Å—Ç–≤–∏—è</h4>
          <div className="grid grid-cols-2 gap-2">
            <Button
              variant="outline"
              size="sm"
              onClick={applyAllHighConfidence}
              disabled={suggestions.filter(s => s.confidence > 0.8).length === 0}
            >
              <Zap className="h-4 w-4 mr-1" />
              –ü—Ä–∏–º–µ–Ω–∏—Ç—å –ª—É—á—à–∏–µ
            </Button>
            <Button
              variant="outline"
              size="sm"
              onClick={syncButtonsWithConnections}
            >
              <CheckCircle className="h-4 w-4 mr-1" />
              –°–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∏—Ä–æ–≤–∞—Ç—å
            </Button>
          </div>
          <Button
            variant="outline"
            size="sm"
            onClick={cleanupOrphanedButtons}
            className="w-full"
          >
            <AlertCircle className="h-4 w-4 mr-1" />
            –û—á–∏—Å—Ç–∏—Ç—å –ª–∏—à–Ω–∏–µ –∫–Ω–æ–ø–∫–∏
          </Button>
        </div>

        <Separator />

        {/* –ü—Ä–µ–¥–ª–æ–∂–µ–Ω–∏—è */}
        <div className="space-y-2">
          <div className="flex items-center justify-between">
            <h4 className="text-sm font-medium">–ü—Ä–µ–¥–ª–æ–∂–µ–Ω–∏—è</h4>
            <Badge variant="secondary">{suggestions.length}</Badge>
          </div>

          {suggestions.length === 0 ? (
            <div className="text-center py-6 text-muted-foreground">
              <Link2 className="h-8 w-8 mx-auto mb-2 opacity-50" />
              <p className="text-sm">–ù–µ—Ç –ø—Ä–µ–¥–ª–æ–∂–µ–Ω–∏–π –¥–ª—è —Å–æ–µ–¥–∏–Ω–µ–Ω–∏—è</p>
            </div>
          ) : (
            <ScrollArea className="h-80">
              <div className="space-y-2">
                {suggestions.map((suggestion) => (
                  <Card
                    key={suggestion.id}
                    className={`p-3 ${appliedSuggestions.has(suggestion.id) ? 'opacity-50' : ''}`}
                  >
                    <div className="space-y-2">
                      <div className="flex items-center justify-between">
                        <div className="flex items-center gap-2">
                          <Badge
                            variant="secondary"
                            className={`text-xs ${getConfidenceColor(suggestion.confidence)} text-white`}
                          >
                            {getConfidenceText(suggestion.confidence)}
                          </Badge>
                          {suggestion.autoCreate && (
                            <TooltipProvider>
                              <Tooltip>
                                <TooltipTrigger>
                                  <Sparkles className="h-4 w-4 text-yellow-500" />
                                </TooltipTrigger>
                                <TooltipContent>
                                  <p>–†–µ–∫–æ–º–µ–Ω–¥—É–µ—Ç—Å—è –∫ –∞–≤—Ç–æ–ø—Ä–∏–º–µ–Ω–µ–Ω–∏—é</p>
                                </TooltipContent>
                              </Tooltip>
                            </TooltipProvider>
                          )}
                        </div>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => applySuggestion(suggestion)}
                          disabled={appliedSuggestions.has(suggestion.id)}
                        >
                          {appliedSuggestions.has(suggestion.id) ? (
                            <CheckCircle className="h-4 w-4 text-green-500" />
                          ) : (
                            '–ü—Ä–∏–º–µ–Ω–∏—Ç—å'
                          )}
                        </Button>
                      </div>

                      <div className="flex items-center gap-2 text-sm">
                        <span className="font-medium truncate">
                          {getNodeName(suggestion.connection.source)}
                        </span>
                        <ArrowRight className="h-4 w-4 text-muted-foreground" />
                        <span className="font-medium truncate">
                          {getNodeName(suggestion.connection.target)}
                        </span>
                      </div>

                      <div className="text-xs text-muted-foreground">
                        {suggestion.reason}
                      </div>

                      {autoButtonCreation && (
                        <div className="bg-muted p-2 rounded text-xs">
                          <div className="font-medium">–°–æ–∑–¥–∞–≤–∞–µ–º–∞—è –∫–Ω–æ–ø–∫–∞:</div>
                          <div className="flex items-center gap-1 mt-1">
                            <span className="bg-background px-2 py-1 rounded">
                              {suggestion.suggestedButton.text}
                            </span>
                            <Badge variant="outline" className="text-xs">
                              {suggestion.suggestedButton.action}
                            </Badge>
                          </div>
                        </div>
                      )}
                    </div>
                  </Card>
                ))}
              </div>
            </ScrollArea>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

