}

function App() {
  useEffect(() => {
    // –°–ª—É—à–∞–µ–º –Ω–∞ —Å–æ–±—ã—Ç–∏–µ –∞–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏–∏ –∏–∑ –æ–∫–Ω–∞ Telegram Login
    const handleAuthMessage = async (event: MessageEvent) => {
      if (event.data && event.data.type === 'telegram-auth' && event.data.user) {
        try {
          // –ö–†–ò–¢–ò–ß–ù–û: –û—Ç–ø—Ä–∞–≤–ª—è–µ–º POST –∑–∞–ø—Ä–æ—Å –Ω–∞ –±—ç–∫ –¥–ª—è —Å–æ–∑–¥–∞–Ω–∏—è —Å–µ—Å—Å–∏–∏
          const telegramUser = event.data.user;
          const response = await fetch('/api/auth/telegram', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            credentials: 'include', // –ö–†–ò–¢–ò–ß–ù–û: –æ—Ç–ø—Ä–∞–≤–ª—è–µ–º cookies –¥–ª—è —Å–µ—Å—Å–∏–∏
            body: JSON.stringify({
              id: telegramUser.id,
              first_name: telegramUser.first_name,
              last_name: telegramUser.last_name,
              username: telegramUser.username,
              photo_url: telegramUser.photo_url,
              auth_date: telegramUser.auth_date,
              hash: telegramUser.hash,
            }),
          });

          if (response.ok) {
            console.log('‚úÖ Auth POST successful - session created on server');
            // –°–æ—Ö—Ä–∞–Ω—è–µ–º –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –≤ localStorage –¥–ª—è –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è hook'–∞
            localStorage.setItem('telegramUser', JSON.stringify(event.data.user));
            // –ö–†–ò–¢–ò–ß–ù–û: –Ø–≤–Ω–æ –∏–Ω–≤–∞–ª–∏–¥–∏—Ä—É–µ–º –í–°–ï –∫–µ—à–∏ –∏ —Ä–µ—Ñ—Ä–µ—à–∏–º –∑–∞–ø—Ä–æ—Å—ã
            setTimeout(() => {
              // –û—Ç–ø—Ä–∞–≤–ª—è–µ–º custom event –¥–ª—è –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è –≤—Å–µ—Ö –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–æ–≤
              window.dispatchEvent(new CustomEvent('telegram-auth-change', {
                detail: { user: event.data.user }
              }));
              // –ò–Ω–≤–∞–ª–∏–¥–∏—Ä—É–µ–º –∏ —Ä–µ—Ñ—Ä–µ—à–∏–º –∑–∞–ø—Ä–æ—Å—ã
              queryClient.removeQueries({ queryKey: ['/api/templates/category/custom', 'guest'] });
              queryClient.invalidateQueries({ queryKey: ['/api/templates/category/custom'] });
              queryClient.invalidateQueries({ queryKey: ['/api/templates'] });
              queryClient.invalidateQueries({ queryKey: ['/api/projects'] });
              // –Ø–≤–Ω–æ —Ä–µ—Ñ—Ä–µ—à–∏–º –¥–ª—è –Ω–µ–º–µ–¥–ª–µ–Ω–Ω–æ–π –∑–∞–≥—Ä—É–∑–∫–∏
              queryClient.refetchQueries({ queryKey: ['/api/templates/category/custom'] });
              queryClient.refetchQueries({ queryKey: ['/api/projects'] });
            }, 100);
          } else {
            console.error('‚ùå Auth POST failed:', response.status);
          }
        } catch (e) {
          console.error('Error in auth flow:', e);
        }
      }
    };

    window.addEventListener('message', handleAuthMessage);
    return () => {
      window.removeEventListener('message', handleAuthMessage);
    };
  }, []);

  return (
    <ThemeProvider defaultTheme="dark" storageKey="telegram-bot-builder-theme">
      <QueryClientProvider client={queryClient}>
        <TooltipProvider>
          <ServerStatus />
          <Toaster />
          <Router />
        </TooltipProvider>
      </QueryClientProvider>
    </ThemeProvider>
  );
}

export default App;

