import React, { useState, useCallback } from 'react';
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";
import { Switch } from "@/components/ui/switch";
import { Label } from "@/components/ui/label";
import { Slider } from "@/components/ui/slider";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { Zap, Image, FileVideo, FileAudio, Download, Settings, CheckCircle2 } from "lucide-react";

interface FileOptimizerProps {
  files: File[];
  onOptimizedFiles?: (files: File[]) => void;
  onClose?: () => void;
}

interface OptimizationSettings {
  images: {
    compress: boolean;
    quality: number;
    maxWidth: number;
    format: 'original' | 'jpeg' | 'webp';
  };
  videos: {
    compress: boolean;
    quality: 'low' | 'medium' | 'high';
    maxSize: number;
  };
  audio: {
    compress: boolean;
    bitrate: number;
  };
}

export function FileOptimizer({ files, onOptimizedFiles, onClose }: FileOptimizerProps) {
  const { toast } = useToast();
  const [isOptimizing, setIsOptimizing] = useState(false);
  const [optimizationProgress, setOptimizationProgress] = useState(0);
  const [optimizedFiles, setOptimizedFiles] = useState<File[]>([]);
  const [settings, setSettings] = useState<OptimizationSettings>({
    images: {
      compress: true,
      quality: 85,
      maxWidth: 1920,
      format: 'original'
    },
    videos: {
      compress: false,
      quality: 'medium',
      maxSize: 50
    },
    audio: {
      compress: false,
      bitrate: 128
    }
  });

  // –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ —Ñ–∞–π–ª–æ–≤
  const fileStats = files.reduce((acc, file) => {
    const type = file.type.split('/')[0];
    acc[type] = (acc[type] || 0) + 1;
    acc.totalSize = (acc.totalSize || 0) + file.size;
    return acc;
  }, {} as Record<string, number>);

  // –°–∂–∞—Ç–∏–µ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–π
  const compressImage = useCallback(async (file: File): Promise<File> => {
    return new Promise((resolve) => {
      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d');
      const img = new Image();

      img.onload = () => {
        // –í—ã—á–∏—Å–ª—è–µ–º –Ω–æ–≤—ã–µ —Ä–∞–∑–º–µ—Ä—ã
        let { width, height } = img;
        const maxWidth = settings.images.maxWidth;

        if (width > maxWidth) {
          height = (height * maxWidth) / width;
          width = maxWidth;
        }

        canvas.width = width;
        canvas.height = height;

        // –†–∏—Å—É–µ–º —Å–∂–∞—Ç–æ–µ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ
        ctx?.drawImage(img, 0, 0, width, height);

        // –ö–æ–Ω–≤–µ—Ä—Ç–∏—Ä—É–µ–º –≤ Blob
        canvas.toBlob((blob) => {
          if (blob) {
            const compressedFile = new File([blob], file.name, {
              type: settings.images.format === 'original' ? file.type : `image/${settings.images.format}`,
              lastModified: Date.now()
            });
            resolve(compressedFile);
          } else {
            resolve(file);
          }
        }, settings.images.format === 'original' ? file.type : `image/${settings.images.format}`, settings.images.quality / 100);
      };

      img.src = URL.createObjectURL(file);
    });
  }, [settings.images]);

  // –û–ø—Ç–∏–º–∏–∑–∞—Ü–∏—è —Ñ–∞–π–ª–æ–≤
  const optimizeFiles = useCallback(async () => {
    setIsOptimizing(true);
    setOptimizationProgress(0);

    try {
      const optimized: File[] = [];

      for (let i = 0; i < files.length; i++) {
        const file = files[i];
        let optimizedFile = file;

        // –û–ø—Ç–∏–º–∏–∑–∏—Ä—É–µ–º –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è
        if (file.type.startsWith('image/') && settings.images.compress) {
          optimizedFile = await compressImage(file);
        }

        optimized.push(optimizedFile);
        setOptimizationProgress(Math.round(((i + 1) / files.length) * 100));

        // –ù–µ–±–æ–ª—å—à–∞—è –∑–∞–¥–µ—Ä–∂–∫–∞ –¥–ª—è –ø–æ–∫–∞–∑–∞ –ø—Ä–æ–≥—Ä–µ—Å—Å–∞
        await new Promise(resolve => setTimeout(resolve, 100));
      }

      setOptimizedFiles(optimized);

      // –í—ã—á–∏—Å–ª—è–µ–º —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫—É —Å–∂–∞—Ç–∏—è
      const originalSize = files.reduce((sum, file) => sum + file.size, 0);
      const optimizedSize = optimized.reduce((sum, file) => sum + file.size, 0);
      const savedPercentage = Math.round(((originalSize - optimizedSize) / originalSize) * 100);

      toast({
        title: "–û–ø—Ç–∏–º–∏–∑–∞—Ü–∏—è –∑–∞–≤–µ—Ä—à–µ–Ω–∞",
        description: `–°—ç–∫–æ–Ω–æ–º–ª–µ–Ω–æ ${savedPercentage}% –º–µ—Å—Ç–∞ (${formatFileSize(originalSize - optimizedSize)})`,
      });

    } catch (error) {
      console.error('Optimization error:', error);
      toast({
        title: "–û—à–∏–±–∫–∞ –æ–ø—Ç–∏–º–∏–∑–∞—Ü–∏–∏",
        description: "–ù–µ —É–¥–∞–ª–æ—Å—å –æ–ø—Ç–∏–º–∏–∑–∏—Ä–æ–≤–∞—Ç—å –Ω–µ–∫–æ—Ç–æ—Ä—ã–µ —Ñ–∞–π–ª—ã",
        variant: "destructive",
      });
    } finally {
      setIsOptimizing(false);
    }
  }, [files, settings, compressImage, toast]);

  // –§–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ —Ä–∞–∑–º–µ—Ä–∞ —Ñ–∞–π–ª–∞
  const formatFileSize = (bytes: number): string => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  // –ü—Ä–∏–º–µ–Ω–µ–Ω–∏–µ –æ–ø—Ç–∏–º–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω—ã—Ö —Ñ–∞–π–ª–æ–≤
  const applyOptimizedFiles = () => {
    onOptimizedFiles?.(optimizedFiles);
    onClose?.();
  };

  return (
    <div className="p-6 space-y-6">
      {/* –ó–∞–≥–æ–ª–æ–≤–æ–∫ */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-xl font-semibold flex items-center">
            <Zap className="w-5 h-5 mr-2 text-yellow-500" />
            –û–ø—Ç–∏–º–∏–∑–∞—Ü–∏—è —Ñ–∞–π–ª–æ–≤
          </h2>
          <p className="text-sm text-muted-foreground mt-1">
            –°–∂–∞—Ç–∏–µ –∏ –æ–ø—Ç–∏–º–∏–∑–∞—Ü–∏—è —Ñ–∞–π–ª–æ–≤ –¥–ª—è —É—Å–∫–æ—Ä–µ–Ω–∏—è –∑–∞–≥—Ä—É–∑–∫–∏
          </p>
        </div>
      </div>

      {/* –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ —Ñ–∞–π–ª–æ–≤ */}
      <Card>
        <CardHeader>
          <CardTitle className="text-base">–û–±–∑–æ—Ä —Ñ–∞–π–ª–æ–≤</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
            <div className="text-center">
              <div className="text-2xl font-bold text-blue-600">{files.length}</div>
              <div className="text-muted-foreground">–í—Å–µ–≥–æ —Ñ–∞–π–ª–æ–≤</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-green-600">{fileStats.image || 0}</div>
              <div className="text-muted-foreground">–ò–∑–æ–±—Ä–∞–∂–µ–Ω–∏–π</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-purple-600">{fileStats.video || 0}</div>
              <div className="text-muted-foreground">–í–∏–¥–µ–æ</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-orange-600">{formatFileSize(fileStats.totalSize || 0)}</div>
              <div className="text-muted-foreground">–û–±—â–∏–π —Ä–∞–∑–º–µ—Ä</div>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* –ù–∞—Å—Ç—Ä–æ–π–∫–∏ –æ–ø—Ç–∏–º–∏–∑–∞—Ü–∏–∏ */}
      <Tabs defaultValue="images">
