  const [selectedConnectionId, setSelectedConnectionId] = useState<string | null>(null);
  const [autoButtonCreation, setAutoButtonCreation] = useState(true);
  const [selectedConnection, setSelectedConnection] = useState<Connection | null>(null);

  // –ë–∞–∑–æ–≤–∞—è –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏—è –º–∞–∫–µ—Ç–∞
  const baseLayoutConfig: SimpleLayoutConfig = {
    elements: [
      {
        id: 'header',
        type: 'header',
        name: '–®–∞–ø–∫–∞',
        position: 'top',
        size: 64,
        visible: true
      },
      {
        id: 'sidebar',
        type: 'sidebar',
        name: '–ë–æ–∫–æ–≤–∞—è –ø–∞–Ω–µ–ª—å',
        position: 'left',
        size: 20,
        visible: true
      },
      {
        id: 'canvas',
        type: 'canvas',
        name: '–•–æ–ª—Å—Ç',
        position: 'center',
        size: 55,
        visible: true
      },
      {
        id: 'properties',
        type: 'properties',
        name: '–°–≤–æ–π—Å—Ç–≤–∞',
        position: 'right',
        size: 25,
        visible: true
      }
    ],
    compactMode: false,
    showGrid: true
  };

  const [flexibleLayoutConfig, setFlexibleLayoutConfig] = useState<SimpleLayoutConfig>(baseLayoutConfig);

  // –ò—Å–ø–æ–ª—å–∑—É–µ–º —Ö—É–∫ –¥–ª—è –æ—Ç—Å–ª–µ–∂–∏–≤–∞–Ω–∏—è —Ä–∞–∑–º–µ—Ä–∞ —ç–∫—Ä–∞–Ω–∞
  const isMobile = useMediaQuery('(max-width: 1200px)');

  // –°–æ–∑–¥–∞–µ–º –∞–¥–∞–ø—Ç–∏–≤–Ω—É—é –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏—é –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ –æ—Ç —Ç–µ–∫—É—â–µ–π –≤–∫–ª–∞–¥–∫–∏
  const adaptiveLayoutConfig = useMemo(() => {
    console.log('Adaptive Layout Config Update:', {
      currentTab,
      isMobile,
      shouldHidePanels: currentTab === 'bot' && isMobile
    });

    if (currentTab === 'bot' && isMobile) {
      // –ù–∞ —Å—Ç—Ä–∞–Ω–∏—Ü–µ –±–æ—Ç–∞ –Ω–∞ –º–æ–±–∏–ª—å–Ω—ã—Ö —É—Å—Ç—Ä–æ–π—Å—Ç–≤–∞—Ö —Å–∫—Ä—ã–≤–∞–µ–º –±–æ–∫–æ–≤—ã–µ –ø–∞–Ω–µ–ª–∏
      const newConfig = {
        ...flexibleLayoutConfig,
        elements: flexibleLayoutConfig.elements.map(el => ({
          ...el,
          visible: el.type === 'sidebar' || el.type === 'properties' ? false : el.visible
        }))
      };
      console.log('Hiding panels for mobile bot page', newConfig);
      return newConfig;
    }

    console.log('Using default layout', flexibleLayoutConfig);
    return flexibleLayoutConfig;
  }, [flexibleLayoutConfig, currentTab, isMobile]);

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Load the first project for demo
  const { data: projects } = useQuery<BotProject[]>({
    queryKey: ['/api/projects'],
  });

  const currentProject = projects?.[0];

  const {
    nodes,
    connections,
    selectedNode,
    selectedNodeId,
    setSelectedNodeId,
    addNode,
    updateNode,
    deleteNode,
    addConnection,
    deleteConnection,
    updateConnection,
    updateNodeData,
    addButton,
    updateButton,
    deleteButton,
    updateNodes,
    setBotData,
    getBotData
  } = useBotEditor(currentProject?.data as BotData);

  const updateProjectMutation = useMutation({
    mutationFn: async (data: any) => {
      if (!currentProject) return;
      return apiRequest('PUT', `/api/projects/${currentProject.id}`, {
        data: getBotData()
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/projects'] });
      toast({
        title: "–ü—Ä–æ–µ–∫—Ç —Å–æ—Ö—Ä–∞–Ω–µ–Ω",
        description: "–ò–∑–º–µ–Ω–µ–Ω–∏—è —É—Å–ø–µ—à–Ω–æ —Å–æ—Ö—Ä–∞–Ω–µ–Ω—ã",
      });
    },
    onError: () => {
      toast({
        title: "–û—à–∏–±–∫–∞ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è",
        description: "–ù–µ —É–¥–∞–ª–æ—Å—å —Å–æ—Ö—Ä–∞–Ω–∏—Ç—å –ø—Ä–æ–µ–∫—Ç",
        variant: "destructive",
      });
    }
  });

  const handleSave = useCallback(() => {
    updateProjectMutation.mutate({});
  }, [updateProjectMutation]);

  const handleTabChange = useCallback((tab: 'editor' | 'preview' | 'export' | 'bot' | 'connections' | 'database' | 'responses') => {
    setCurrentTab(tab);
    if (tab === 'preview') {
      updateProjectMutation.mutate({});
      setShowPreview(true);
    } else if (tab === 'export') {
      setShowExport(true);
    } else if (tab === 'bot') {
      updateProjectMutation.mutate({});
    } else if (tab === 'connections') {
      updateProjectMutation.mutate({});
    } else if (tab === 'database') {
      // –ü–µ—Ä–µ–Ω–∞–ø—Ä–∞–≤–ª—è–µ–º –Ω–∞ —Å—Ç—Ä–∞–Ω–∏—Ü—É –±–∞–∑—ã –¥–∞–Ω–Ω—ã—Ö
      setLocation('/database');
    } else if (tab === 'responses') {
      updateProjectMutation.mutate({});
    }
  }, [updateProjectMutation, setLocation]);

  const handleNodeMove = useCallback((nodeId: string, position: { x: number; y: number }) => {
    updateNode(nodeId, { position });
  }, [updateNode]);

  const handleComponentDrag = useCallback((component: ComponentDefinition) => {
    // Handle component drag start if needed
  }, []);

  const handleSaveAsTemplate = useCallback(() => {
    setShowSaveTemplate(true);
  }, []);

  const handleLoadTemplate = useCallback(() => {
    setLocation('/templates');
  }, [setLocation]);

  const handleSelectTemplate = useCallback((template: any) => {
    try {
      const templateData = template.data;
      if (!templateData || !templateData.nodes) {
        throw new Error('–ù–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ —à–∞–±–ª–æ–Ω–∞');
      }

      setBotData(templateData);
      updateProjectMutation.mutate({
        data: templateData
      });

      toast({
        title: '–®–∞–±–ª–æ–Ω –ø—Ä–∏–º–µ–Ω–µ–Ω',
        description: `–®–∞–±–ª–æ–Ω "${template.name}" —É—Å–ø–µ—à–Ω–æ –∑–∞–≥—Ä—É–∂–µ–Ω –Ω–∞ —Ö–æ–ª—Å—Ç`,
      });
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏–º–µ–Ω–µ–Ω–∏—è —à–∞–±–ª–æ–Ω–∞:', error);
      toast({
        title: '–û—à–∏–±–∫–∞',
        description: '–ù–µ —É–¥–∞–ª–æ—Å—å –ø—Ä–∏–º–µ–Ω–∏—Ç—å —à–∞–±–ª–æ–Ω',
        variant: 'destructive',
      });
    }
  }, [setBotData, updateProjectMutation, toast]);

  // –ü—Ä–æ–≤–µ—Ä—è–µ–º –Ω–∞–ª–∏—á–∏–µ –≤—ã–±—Ä–∞–Ω–Ω–æ–≥–æ —à–∞–±–ª–æ–Ω–∞ –≤ localStorage –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ
  useEffect(() => {
    const selectedTemplateData = localStorage.getItem('selectedTemplate');
    if (selectedTemplateData && currentProject) {
      try {
        const template = JSON.parse(selectedTemplateData);
        handleSelectTemplate(template);
        localStorage.removeItem('selectedTemplate'); // –£–¥–∞–ª—è–µ–º –ø–æ—Å–ª–µ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è
      } catch (error) {
        console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏–º–µ–Ω–µ–Ω–∏—è —à–∞–±–ª–æ–Ω–∞ –∏–∑ localStorage:', error);
        localStorage.removeItem('selectedTemplate');
      }
    }
  }, [currentProject, handleSelectTemplate]);

  const handleConnectionSelect = useCallback((connection: Connection | null) => {
    setSelectedConnection(connection);
    setSelectedConnectionId(connection?.id || null);
  }, []);

  const handleConnectionEdit = useCallback((connection: Connection) => {
    setSelectedConnection(connection);
    setSelectedConnectionId(connection.id);
  }, []);

  const handleConnectionDelete = useCallback((connectionId: string) => {
    deleteConnection(connectionId);
    if (selectedConnectionId === connectionId) {
      setSelectedConnectionId(null);
      setSelectedConnection(null);
    }
  }, [deleteConnection, selectedConnectionId]);

  if (!currentProject) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="w-12 h-12 bg-gray-100 rounded-lg flex items-center justify-center mx-auto mb-4">
            <i className="fas fa-spinner fa-spin text-gray-400 text-xl"></i>
          </div>
          <p className="text-gray-600">–ó–∞–≥—Ä—É–∑–∫–∞ –ø—Ä–æ–µ–∫—Ç–∞...</p>
        </div>
      </div>
    );
  }

  // –°–æ–∑–¥–∞–µ–º —Å–æ–¥–µ—Ä–∂–∏–º–æ–µ –¥–ª—è –∫–∞–∂–¥–æ–≥–æ —ç–ª–µ–º–µ–Ω—Ç–∞ –º–∞–∫–µ—Ç–∞
  const headerContent = (
    <AdaptiveHeader
      config={{
        headerPosition: 'top',
        sidebarPosition: 'left',
        propertiesPosition: 'right',
        canvasFullscreen: false,
        compactMode: false,
        showGrid: true,
        panelSizes: {
          sidebar: 20,
          properties: 25,
          canvas: 55
        }
      }}
      projectName={currentProject.name}
      currentTab={currentTab}
      onTabChange={handleTabChange}
      onSave={handleSave}
      onExport={() => setShowExport(true)}
      onSaveAsTemplate={handleSaveAsTemplate}
      onLoadTemplate={handleLoadTemplate}
      onLayoutSettings={() => {}}
      isSaving={updateProjectMutation.isPending}
    />
  );

  const sidebarContent = (
    <ComponentsSidebar 
      onComponentDrag={handleComponentDrag} 
      onLoadTemplate={handleLoadTemplate}
    />
  );

  const canvasContent = (
    <div className="h-full">
      {currentTab === 'editor' ? (
        <Canvas
          nodes={nodes}
          connections={connections}
          selectedNodeId={selectedNodeId}
          selectedConnectionId={selectedConnectionId || undefined}
          onNodeSelect={setSelectedNodeId}
          onNodeAdd={addNode}
          onNodeDelete={deleteNode}
          onNodeMove={handleNodeMove}
          onConnectionSelect={setSelectedConnectionId}
          onConnectionDelete={deleteConnection}
          onConnectionAdd={addConnection}
          onNodesUpdate={updateNodes}
        />
      ) : currentTab === 'bot' ? (
        <div className="h-full p-6 bg-gray-50 overflow-auto">
          <div className="max-w-2xl mx-auto">
            <BotControl
              projectId={currentProject.id}
              projectName={currentProject.name}
            />
          </div>
        </div>
      ) : currentTab === 'connections' ? (
        <div className="h-full bg-background">
          <div className="h-full flex">
            <div className="w-1/2 p-4 space-y-4 overflow-auto">
              <SmartConnectionCreator
                nodes={nodes}
                connections={connections}
                onConnectionAdd={addConnection}
                onNodesChange={updateNodes}
                autoButtonCreation={autoButtonCreation}
                selectedNodeId={selectedNodeId || undefined}
              />

              <EnhancedConnectionControls
                nodes={nodes}
                connections={connections}
                onConnectionAdd={addConnection}
                onConnectionDelete={deleteConnection}
                onConnectionUpdate={updateConnection}
                onNodesChange={updateNodes}
                autoButtonCreation={autoButtonCreation}
                onAutoButtonCreationChange={setAutoButtonCreation}
                selectedConnection={selectedConnection}
                onConnectionSelect={setSelectedConnection}
              />
            </div>

            <div className="w-1/2 p-4 overflow-auto border-l">
              <ConnectionVisualization
                nodes={nodes}
                connections={connections}
                onConnectionSelect={handleConnectionSelect}
                onConnectionDelete={handleConnectionDelete}
                onConnectionEdit={handleConnectionEdit}
                selectedConnectionId={selectedConnectionId || undefined}
                showLabels={true}
                showMetrics={true}
                interactive={true}
              />
            </div>
          </div>
        </div>
      ) : currentTab === 'database' ? (
        <div className="h-full">
          <UserDatabasePanel
            projectId={currentProject.id}
            projectName={currentProject.name}
          />
        </div>
      ) : currentTab === 'responses' ? (
        <div className="h-full">
          <ResponsesPanel
            projectId={currentProject.id}
            projectName={currentProject.name}
          />
        </div>
      ) : null}
    </div>
  );

  const propertiesContent = (
    <PropertiesPanel
      projectId={currentProject.id}
      selectedNode={selectedNode}
      allNodes={nodes}
      onNodeUpdate={updateNodeData}
      onButtonAdd={addButton}
      onButtonUpdate={updateButton}
      onButtonDelete={deleteButton}
    />
  );

  return (
    <>
      <SimpleLayoutCustomizer
        config={flexibleLayoutConfig}
        onConfigChange={setFlexibleLayoutConfig}
      >
        <FlexibleLayout
          config={adaptiveLayoutConfig}
          headerContent={headerContent}
          sidebarContent={sidebarContent}
          canvasContent={canvasContent}
          propertiesContent={propertiesContent}
          onConfigChange={setFlexibleLayoutConfig}
        />
      </SimpleLayoutCustomizer>

      <PreviewModal
        isOpen={showPreview}
        onClose={() => {
          setShowPreview(false);
          setCurrentTab('editor');
        }}
        nodes={nodes}
        connections={connections}
        projectName={currentProject.name}
      />

      <ExportModal
        isOpen={showExport}
        onClose={() => {
          setShowExport(false);
          setCurrentTab('editor');
        }}
        botData={currentProject?.data as any}
        projectName={currentProject.name}
      />

      <SaveTemplateModal
        isOpen={showSaveTemplate}
        onClose={() => setShowSaveTemplate(false)}
        botData={currentProject?.data as any}
        projectName={currentProject.name}
      />


    </>
  );
}

