import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from '@/components/ui/dropdown-menu';
import { Textarea } from '@/components/ui/textarea';
import { Switch } from '@/components/ui/switch';
import { useToast } from '@/hooks/use-toast';
import { useTelegramAuth } from '@/hooks/use-telegram-auth';
import { useTokens, useCreateToken, useUpdateToken, useDeleteToken } from '@/hooks/use-user-data';
import { apiRequest } from '@/lib/queryClient';
import { Plus, MoreVertical, Star, StarOff, Edit, Trash2, Copy, Eye, EyeOff, Database, HardDrive } from 'lucide-react';

interface BotToken {
  id: number;
  projectId: number;
  name: string;
  token: string; // Partially hidden
  isDefault: number;
  isActive: number;
  description?: string;
  lastUsedAt?: string;
  createdAt: string;
  updatedAt: string;
}

interface TokenManagerProps {
  projectId: number;
  onTokenSelect?: (tokenId: number) => void;
  selectedTokenId?: number | null;
}

export function TokenManager({ projectId, onTokenSelect, selectedTokenId }: TokenManagerProps) {
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [editingToken, setEditingToken] = useState<BotToken | null>(null);
  const [newTokenData, setNewTokenData] = useState({
    name: '',
    token: '',
    description: '',
    isDefault: false
  });
  const [showTokenValues, setShowTokenValues] = useState<Record<number, boolean>>({});

  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { user } = useTelegramAuth();
  const isAuthenticated = user !== null;

  // –ü–æ–ª—É—á–∞–µ–º –≤—Å–µ —Ç–æ–∫–µ–Ω—ã –ø—Ä–æ–µ–∫—Ç–∞ (—Å –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏–º –ø–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏–µ–º –º–µ–∂–¥—É localStorage –∏ —Å–µ—Ä–≤–µ—Ä–æ–º)
  const { data: tokens = [], isLoading } = useTokens({
    isAuthenticated,
    userId: user?.id
  }, projectId);

  // –°–æ–∑–¥–∞–Ω–∏–µ —Ç–æ–∫–µ–Ω–∞
  const createTokenMutation = useMutation({
    mutationFn: async (tokenData: typeof newTokenData) => {
      return apiRequest('POST', `/api/projects/${projectId}/tokens`, {
        ...tokenData,
        isDefault: tokenData.isDefault ? 1 : 0
      });
    },
    onSuccess: () => {
      toast({
        title: "–¢–æ–∫–µ–Ω —Å–æ–∑–¥–∞–Ω",
        description: "–ù–æ–≤—ã–π —Ç–æ–∫–µ–Ω —É—Å–ø–µ—à–Ω–æ –¥–æ–±–∞–≤–ª–µ–Ω.",
      });
      queryClient.invalidateQueries({ queryKey: [`/api/projects/${projectId}/tokens`] });
      setIsAddDialogOpen(false);
      setNewTokenData({ name: '', token: '', description: '', isDefault: false });
    },
    onError: (error: any) => {
      toast({
        title: "–û—à–∏–±–∫–∞ —Å–æ–∑–¥–∞–Ω–∏—è",
        description: error.message || "–ù–µ —É–¥–∞–ª–æ—Å—å —Å–æ–∑–¥–∞—Ç—å —Ç–æ–∫–µ–Ω.",
        variant: "destructive",
      });
    },
  });

  // –û–±–Ω–æ–≤–ª–µ–Ω–∏–µ —Ç–æ–∫–µ–Ω–∞
  const updateTokenMutation = useMutation({
    mutationFn: async ({ id, data }: { id: number; data: Partial<BotToken> }) => {
      return apiRequest('PUT', `/api/tokens/${id}`, data);
    },
    onSuccess: () => {
      toast({
        title: "–¢–æ–∫–µ–Ω –æ–±–Ω–æ–≤–ª–µ–Ω",
        description: "–¢–æ–∫–µ–Ω —É—Å–ø–µ—à–Ω–æ –æ–±–Ω–æ–≤–ª–µ–Ω.",
      });
      queryClient.invalidateQueries({ queryKey: [`/api/projects/${projectId}/tokens`] });
      setIsEditDialogOpen(false);
      setEditingToken(null);
    },
    onError: (error: any) => {
      toast({
        title: "–û—à–∏–±–∫–∞ –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è",
        description: error.message || "–ù–µ —É–¥–∞–ª–æ—Å—å –æ–±–Ω–æ–≤–∏—Ç—å —Ç–æ–∫–µ–Ω.",
        variant: "destructive",
      });
    },
  });

  // –£–¥–∞–ª–µ–Ω–∏–µ —Ç–æ–∫–µ–Ω–∞
  const deleteTokenMutation = useMutation({
    mutationFn: async (id: number) => {
      return apiRequest('DELETE', `/api/tokens/${id}`);
    },
    onSuccess: () => {
      toast({
        title: "–¢–æ–∫–µ–Ω —É–¥–∞–ª–µ–Ω",
        description: "–¢–æ–∫–µ–Ω —É—Å–ø–µ—à–Ω–æ —É–¥–∞–ª–µ–Ω.",
      });
      queryClient.invalidateQueries({ queryKey: [`/api/projects/${projectId}/tokens`] });
    },
    onError: (error: any) => {
      toast({
        title: "–û—à–∏–±–∫–∞ —É–¥–∞–ª–µ–Ω–∏—è",
        description: error.message || "–ù–µ —É–¥–∞–ª–æ—Å—å —É–¥–∞–ª–∏—Ç—å —Ç–æ–∫–µ–Ω.",
        variant: "destructive",
      });
    },
  });

  // –£—Å—Ç–∞–Ω–æ–≤–∫–∞ —Ç–æ–∫–µ–Ω–∞ –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é
  const setDefaultMutation = useMutation({
    mutationFn: async (tokenId: number) => {
      return apiRequest('POST', `/api/projects/${projectId}/tokens/${tokenId}/set-default`);
    },
    onSuccess: () => {
      toast({
        title: "–¢–æ–∫–µ–Ω –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é",
        description: "–¢–æ–∫–µ–Ω —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é.",
      });
      queryClient.invalidateQueries({ queryKey: [`/api/projects/${projectId}/tokens`] });
    },
    onError: (error: any) => {
      toast({
        title: "–û—à–∏–±–∫–∞",
        description: error.message || "–ù–µ —É–¥–∞–ª–æ—Å—å —É—Å—Ç–∞–Ω–æ–≤–∏—Ç—å —Ç–æ–∫–µ–Ω –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é.",
        variant: "destructive",
      });
    },
  });

  const handleCreateToken = () => {
    createTokenMutation.mutate(newTokenData);
  };

  const handleEditToken = (token: BotToken) => {
    setEditingToken(token);
    setIsEditDialogOpen(true);
  };

  const handleUpdateToken = () => {
    if (!editingToken) return;

    updateTokenMutation.mutate({
      id: editingToken.id,
      data: {
        name: editingToken.name,
        description: editingToken.description,
        isActive: editingToken.isActive
      }
    });
  };

  const handleDeleteToken = (id: number) => {
