import { Router } from 'express';
import { storage } from './storage';
import { dbManager } from './db-utils';
import { dbCache } from './db-cache';
import { dbBackup } from './db-backup';
import multer from 'multer';
import { join } from 'path';

const router = Router();

// Database health and statistics endpoints
router.get('/health', async (req, res) => {
  try {
    const isHealthy = await dbManager.performHealthCheck();
    const stats = dbManager.getConnectionStats();

    res.json({
      healthy: isHealthy,
      stats,
      timestamp: new Date().toISOString()
    });
  } catch (error) {
    res.status(500).json({ error: 'Health check failed', message: error.message });
  }
});

// Database statistics
router.get('/stats', async (req, res) => {
  try {
    const stats = await (storage as any).getDetailedStats();
    res.json(stats);
  } catch (error) {
    res.status(500).json({ error: 'Failed to get stats', message: error.message });
  }
});

// Database metrics
router.get('/metrics', async (req, res) => {
  try {
    const metrics = await dbManager.getDatabaseMetrics();
    res.json(metrics);
  } catch (error) {
    res.status(500).json({ error: 'Failed to get metrics', message: error.message });
  }
});

// Cache statistics
router.get('/cache/stats', async (req, res) => {
  try {
    const stats = dbCache.getStats();
    res.json(stats);
  } catch (error) {
    res.status(500).json({ error: 'Failed to get cache stats', message: error.message });
  }
});

// Clear cache
router.post('/cache/clear', async (req, res) => {
  try {
    const { pattern } = req.body;

    if (pattern) {
      dbCache.clearByPattern(pattern);
    } else {
      dbCache.clear();
    }

    res.json({ message: 'Cache cleared successfully' });
  } catch (error) {
    res.status(500).json({ error: 'Failed to clear cache', message: error.message });
  }
});

// Database maintenance
router.post('/maintenance', async (req, res) => {
  try {
    await (storage as any).performMaintenance();
    res.json({ message: 'Database maintenance completed successfully' });
  } catch (error) {
    res.status(500).json({ error: 'Maintenance failed', message: error.message });
  }
});

// Database backup
router.post('/backup', async (req, res) => {
  try {
    const backupName = await (storage as any).createBackup();
    res.json({ message: 'Backup created successfully', backupName });
  } catch (error) {
    res.status(500).json({ error: 'Backup failed', message: error.message });
  }
});

// Database optimization
router.post('/optimize', async (req, res) => {
  try {
    await dbManager.optimizeConnections();
    res.json({ message: 'Database connections optimized' });
  } catch (error) {
    res.status(500).json({ error: 'Optimization failed', message: error.message });
  }
});

// Cleanup old data
router.post('/cleanup', async (req, res) => {
  try {
    const { days = 30 } = req.body;
    await dbManager.cleanupOldData(days);
    res.json({ message: `Cleaned up data older than ${days} days` });
  } catch (error) {
    res.status(500).json({ error: 'Cleanup failed', message: error.message });
  }
});

// Connection pool information
router.get('/pool', async (req, res) => {
  try {
    const stats = dbManager.getConnectionStats();
    res.json(stats.poolInfo);
  } catch (error) {
    res.status(500).json({ error: 'Failed to get pool info', message: error.message });
  }
});

// –ù–∞—Å—Ç—Ä–æ–π–∫–∞ multer –¥–ª—è –∑–∞–≥—Ä—É–∑–∫–∏ —Ä–µ–∑–µ—Ä–≤–Ω—ã—Ö –∫–æ–ø–∏–π
const upload = multer({
  dest: './backups',
  fileFilter: (req, file, cb) => {
    // –†–∞–∑—Ä–µ—à–∏—Ç—å —Ç–æ–ª—å–∫–æ JSON —Ñ–∞–π–ª—ã
    if (file.mimetype === 'application/json' || file.originalname.endsWith('.json')) {
      cb(null, true);
    } else {
      cb(new Error('–¢–æ–ª—å–∫–æ JSON —Ñ–∞–π–ª—ã —Ä–∞–∑—Ä–µ—à–µ–Ω—ã –¥–ª—è –∑–∞–≥—Ä—É–∑–∫–∏'));
    }
  },
  limits: {
    fileSize: 50 * 1024 * 1024 // 50MB –º–∞–∫—Å–∏–º–∞–ª—å–Ω—ã–π —Ä–∞–∑–º–µ—Ä —Ñ–∞–π–ª–∞
  }
});

// BACKUP AND RESTORE ENDPOINTS

// –°–æ–∑–¥–∞—Ç—å —Ä–µ–∑–µ—Ä–≤–Ω—É—é –∫–æ–ø–∏—é –±–∞–∑—ã –¥–∞–Ω–Ω—ã—Ö
router.post('/backup', async (req, res) => {
  try {
    const { description } = req.body;
    const filepath = await dbBackup.createFullBackup(description);

    res.json({
      success: true,
      message: '–†–µ–∑–µ—Ä–≤–Ω–∞—è –∫–æ–ø–∏—è —Å–æ–∑–¥–∞–Ω–∞ —É—Å–ø–µ—à–Ω–æ',
      filepath,
      timestamp: new Date().toISOString()
    });
  } catch (error) {
    res.status(500).json({ 
      success: false, 
      error: '–ù–µ —É–¥–∞–ª–æ—Å—å —Å–æ–∑–¥–∞—Ç—å —Ä–µ–∑–µ—Ä–≤–Ω—É—é –∫–æ–ø–∏—é', 
      message: error.message 
    });
  }
});

// –ü–æ–ª—É—á–∏—Ç—å —Å–ø–∏—Å–æ–∫ —Ä–µ–∑–µ—Ä–≤–Ω—ã—Ö –∫–æ–ø–∏–π
router.get('/backups', async (req, res) => {
  try {
    const backups = await dbBackup.listBackups();
    res.json({
      success: true,
      backups,
      count: backups.length
    });
  } catch (error) {
    res.status(500).json({ 
      success: false, 
      error: '–ù–µ —É–¥–∞–ª–æ—Å—å –ø–æ–ª—É—á–∏—Ç—å —Å–ø–∏—Å–æ–∫ —Ä–µ–∑–µ—Ä–≤–Ω—ã—Ö –∫–æ–ø–∏–π', 
      message: error.message 
    });
  }
});

// –°–∫–∞—á–∞—Ç—å —Ä–µ–∑–µ—Ä–≤–Ω—É—é –∫–æ–ø–∏—é
router.get('/backup/:filename', async (req, res) => {
  try {
    const { filename } = req.params;
    const filepath = join('./backups', filename);

    // –ü—Ä–æ–≤–µ—Ä–∏—Ç—å —á—Ç–æ —Ñ–∞–π–ª —Å—É—â–µ—Å—Ç–≤—É–µ—Ç
    const fs = await import('fs');
    if (!fs.existsSync(filepath)) {
      return res.status(404).json({ 
        success: false, 
        error: '–†–µ–∑–µ—Ä–≤–Ω–∞—è –∫–æ–ø–∏—è –Ω–µ –Ω–∞–π–¥–µ–Ω–∞' 
      });
    }

    // –û—Ç–ø—Ä–∞–≤–∏—Ç—å —Ñ–∞–π–ª –¥–ª—è —Å–∫–∞—á–∏–≤–∞–Ω–∏—è
    res.download(filepath, filename, (err) => {
      if (err) {
        console.error('Download error:', err);
        res.status(500).json({ 
          success: false, 
          error: '–ù–µ —É–¥–∞–ª–æ—Å—å —Å–∫–∞—á–∞—Ç—å —Ñ–∞–π–ª' 
        });
      }
    });
  } catch (error) {
    res.status(500).json({ 
      success: false, 
      error: '–ù–µ —É–¥–∞–ª–æ—Å—å —Å–∫–∞—á–∞—Ç—å —Ä–µ–∑–µ—Ä–≤–Ω—É—é –∫–æ–ø–∏—é', 
      message: error.message 
    });
  }
});

// –í–æ—Å—Å—Ç–∞–Ω–æ–≤–∏—Ç—å –∏–∑ —Ä–µ–∑–µ—Ä–≤–Ω–æ–π –∫–æ–ø–∏–∏
router.post('/restore', async (req, res) => {
  try {
    const { filename, options } = req.body;
    const filepath = join('./backups', filename);

    await dbBackup.restoreFromBackup(filepath, options);

    res.json({
      success: true,
      message: '–ë–∞–∑–∞ –¥–∞–Ω–Ω—ã—Ö –≤–æ—Å—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–∞ —É—Å–ø–µ—à–Ω–æ',
      timestamp: new Date().toISOString()
    });
  } catch (error) {
    res.status(500).json({ 
      success: false, 
      error: '–ù–µ —É–¥–∞–ª–æ—Å—å –≤–æ—Å—Å—Ç–∞–Ω–æ–≤–∏—Ç—å –±–∞–∑—É –¥–∞–Ω–Ω—ã—Ö', 
      message: error.message 
    });
  }
});

// –ó–∞–≥—Ä—É–∑–∏—Ç—å —Ä–µ–∑–µ—Ä–≤–Ω—É—é –∫–æ–ø–∏—é
router.post('/backup/upload', upload.single('backup'), async (req, res) => {
  try {
    if (!req.file) {
      return res.status(400).json({ 
        success: false, 
        error: '–§–∞–π–ª –Ω–µ –∑–∞–≥—Ä—É–∂–µ–Ω' 
      });
    }

    const { restoreImmediately, clearExisting } = req.body;
    const filepath = req.file.path;

    // –ü–µ—Ä–µ–∏–º–µ–Ω–æ–≤–∞—Ç—å —Ñ–∞–π–ª —á—Ç–æ–±—ã –∏–º–µ—Ç—å –ø—Ä–∞–≤–∏–ª—å–Ω–æ–µ —Ä–∞—Å—à–∏—Ä–µ–Ω–∏–µ
    const fs = await import('fs');
    const newFilepath = filepath + '.json';
    fs.renameSync(filepath, newFilepath);

    let result: any = {
      success: true,
      message: '–§–∞–π–ª –∑–∞–≥—Ä—É–∂–µ–Ω —É—Å–ø–µ—à–Ω–æ',
      filename: req.file.filename + '.json',
      size: req.file.size
    };

    // –ï—Å–ª–∏ —É–∫–∞–∑–∞–Ω–æ –≤–æ—Å—Å—Ç–∞–Ω–æ–≤–∏—Ç—å –Ω–µ–º–µ–¥–ª–µ–Ω–Ω–æ
    if (restoreImmediately === 'true') {
      try {
        await dbBackup.restoreFromBackup(newFilepath, {
          clearExisting: clearExisting === 'true'
        });
        result.message = '–§–∞–π–ª –∑–∞–≥—Ä—É–∂–µ–Ω –∏ –±–∞–∑–∞ –¥–∞–Ω–Ω—ã—Ö –≤–æ—Å—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–∞ —É—Å–ø–µ—à–Ω–æ';
        result.restored = true;
      } catch (restoreError) {
        result.warning = '–§–∞–π–ª –∑–∞–≥—Ä—É–∂–µ–Ω, –Ω–æ –≤–æ—Å—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–∏–µ –Ω–µ —É–¥–∞–ª–æ—Å—å: ' + restoreError.message;
      }
    }

    res.json(result);
  } catch (error) {
    res.status(500).json({ 
      success: false, 
      error: '–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å —Ñ–∞–π–ª', 
      message: error.message 
    });
  }
});

// –£–¥–∞–ª–∏—Ç—å —Ä–µ–∑–µ—Ä–≤–Ω—É—é –∫–æ–ø–∏—é
router.delete('/backup/:filename', async (req, res) => {
  try {
    const { filename } = req.params;
    await dbBackup.deleteBackup(filename);

    res.json({
      success: true,
      message: '–†–µ–∑–µ—Ä–≤–Ω–∞—è –∫–æ–ø–∏—è —É–¥–∞–ª–µ–Ω–∞ —É—Å–ø–µ—à–Ω–æ'
    });
  } catch (error) {
    res.status(500).json({ 
      success: false, 
      error: '–ù–µ —É–¥–∞–ª–æ—Å—å —É–¥–∞–ª–∏—Ç—å —Ä–µ–∑–µ—Ä–≤–Ω—É—é –∫–æ–ø–∏—é', 
      message: error.message 
    });
  }
});

// –ü–æ–ª—É—á–∏—Ç—å —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫—É –±–∞–∑—ã –¥–∞–Ω–Ω—ã—Ö
router.get('/stats/detailed', async (req, res) => {
  try {
    const stats = await dbBackup.getDatabaseStats();
    res.json({
      success: true,
      stats
    });
  } catch (error) {
    res.status(500).json({ 
      success: false, 
      error: '–ù–µ —É–¥–∞–ª–æ—Å—å –ø–æ–ª—É—á–∏—Ç—å —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫—É', 
      message: error.message 
    });
  }
});

export default router;

