  },
  {
    command: '/info',
    description: '–ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –±–æ—Ç–µ',
    category: 'system',
    showInMenu: true
  },
  {
    command: '/cancel',
    description: '–û—Ç–º–µ–Ω–∏—Ç—å —Ç–µ–∫—É—â–µ–µ –¥–µ–π—Å—Ç–≤–∏–µ',
    category: 'system',
    showInMenu: false
  },
  {
    command: '/back',
    description: '–í–µ—Ä–Ω—É—Ç—å—Å—è –Ω–∞–∑–∞–¥',
    category: 'navigation',
    showInMenu: false
  },
  {
    command: '/support',
    description: '–¢–µ—Ö–Ω–∏—á–µ—Å–∫–∞—è –ø–æ–¥–¥–µ—Ä–∂–∫–∞',
    category: 'system',
    showInMenu: true
  },
  {
    command: '/about',
    description: '–û –±–æ—Ç–µ',
    category: 'system',
    showInMenu: true
  },
  {
    command: '/subscribe',
    description: '–ü–æ–¥–ø–∏—Å–∞—Ç—å—Å—è –Ω–∞ —É–≤–µ–¥–æ–º–ª–µ–Ω–∏—è',
    category: 'user',
    showInMenu: true
  },
  {
    command: '/unsubscribe',
    description: '–û—Ç–ø–∏—Å–∞—Ç—å—Å—è –æ—Ç —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–π',
    category: 'user',
    showInMenu: false
  },
  {
    command: '/feedback',
