  }
];

export const COMMAND_CATEGORIES = {
  system: '–°–∏—Å—Ç–µ–º–Ω—ã–µ',
  user: '–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å—Å–∫–∏–µ',
  navigation: '–ù–∞–≤–∏–≥–∞—Ü–∏—è',
  admin: '–ê–¥–º–∏–Ω–∏—Å—Ç—Ä–∏—Ä–æ–≤–∞–Ω–∏–µ'
};

// –í–∞–ª–∏–¥–∞—Ü–∏—è –∫–æ–º–∞–Ω–¥—ã
export function validateCommand(command: string): { isValid: boolean; errors: string[] } {
  const errors: string[] = [];

  if (!command) {
    errors.push('–ö–æ–º–∞–Ω–¥–∞ –Ω–µ –º–æ–∂–µ—Ç –±—ã—Ç—å –ø—É—Å—Ç–æ–π');
    return { isValid: false, errors };
  }

  if (!command.startsWith('/')) {
    errors.push('–ö–æ–º–∞–Ω–¥–∞ –¥–æ–ª–∂–Ω–∞ –Ω–∞—á–∏–Ω–∞—Ç—å—Å—è —Å —Å–∏–º–≤–æ–ª–∞ "/"');
  }

  if (command.length < 2) {
    errors.push('–ö–æ–º–∞–Ω–¥–∞ –¥–æ–ª–∂–Ω–∞ —Å–æ–¥–µ—Ä–∂–∞—Ç—å —Ö–æ—Ç—è –±—ã –æ–¥–∏–Ω —Å–∏–º–≤–æ–ª –ø–æ—Å–ª–µ "/"');
  }

  if (command.length > 32) {
    errors.push('–ö–æ–º–∞–Ω–¥–∞ –Ω–µ –º–æ–∂–µ—Ç –±—ã—Ç—å –¥–ª–∏–Ω–Ω–µ–µ 32 —Å–∏–º–≤–æ–ª–æ–≤');
  }

  // –ü—Ä–æ–≤–µ—Ä–∫–∞ –Ω–∞ –¥–æ–ø—É—Å—Ç–∏–º—ã–µ —Å–∏–º–≤–æ–ª—ã
  const validPattern = /^\/[a-zA-Z][a-zA-Z0-9_]*$/;
  if (!validPattern.test(command)) {
    errors.push('–ö–æ–º–∞–Ω–¥–∞ –º–æ–∂–µ—Ç —Å–æ–¥–µ—Ä–∂–∞—Ç—å —Ç–æ–ª—å–∫–æ –ª–∞—Ç–∏–Ω—Å–∫–∏–µ –±—É–∫–≤—ã, —Ü–∏—Ñ—Ä—ã –∏ –ø–æ–¥—á—ë—Ä–∫–∏–≤–∞–Ω–∏—è');
  }

  // –ü—Ä–æ–≤–µ—Ä–∫–∞ –Ω–∞ –∑–∞—Ä–µ–∑–µ—Ä–≤–∏—Ä–æ–≤–∞–Ω–Ω—ã–µ –∫–æ–º–∞–Ω–¥—ã Telegram
  const reservedCommands = ['/newbot', '/mybots', '/setname', '/setdescription', '/setabouttext', '/setuserpic', '/setinline', '/setjoingroups', '/setprivacy'];
  if (reservedCommands.includes(command.toLowerCase())) {
    errors.push('–≠—Ç–∞ –∫–æ–º–∞–Ω–¥–∞ –∑–∞—Ä–µ–∑–µ—Ä–≤–∏—Ä–æ–≤–∞–Ω–∞ —Å–∏—Å—Ç–µ–º–æ–π Telegram');
  }

  return {
    isValid: errors.length === 0,
    errors
  };
}

// –ì–µ–Ω–µ—Ä–∞—Ü–∏—è –∞–≤—Ç–æ–¥–æ–ø–æ–ª–Ω–µ–Ω–∏—è –∫–æ–º–∞–Ω–¥
export function getCommandSuggestions(input: string): typeof STANDARD_COMMANDS {
  if (!input.startsWith('/')) {
    return [];
  }

  const query = input.toLowerCase();
  return STANDARD_COMMANDS.filter(cmd => 
    cmd.command.toLowerCase().includes(query) ||
    cmd.description.toLowerCase().includes(query.slice(1))
  ).slice(0, 10);
}

// –ü–æ–ª—É—á–µ–Ω–∏–µ –∫–æ–º–∞–Ω–¥—ã –ø–æ —Ç–µ–∫—Å—Ç—É
export function parseCommandFromText(text: string): string | null {
  if (!text || !text.startsWith('/')) {
    return null;
  }

  const match = text.match(/^\/([a-zA-Z][a-zA-Z0-9_]*)/);
  return match ? `/${match[1]}` : null;
}

// –ì–µ–Ω–µ—Ä–∞—Ü–∏—è BotFather –∫–æ–º–∞–Ω–¥ –¥–ª—è –Ω–∞—Å—Ç—Ä–æ–π–∫–∏ –º–µ–Ω—é
export function generateBotFatherCommands(nodes: any[]): string {
  if (!nodes || !Array.isArray(nodes)) {
    return '';
  }

  const commandNodes = nodes.filter(node => 
    (node.type === 'start' || node.type === 'command') && 
    node.data?.command &&
    (node.data?.showInMenu !== false) // –í–∫–ª—é—á–∞–µ–º –∫–æ–º–∞–Ω–¥—ã –≥–¥–µ showInMenu = true, undefined –∏–ª–∏ –Ω–µ —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–æ
  );

  if (commandNodes.length === 0) {
    return '';
  }

  let botFatherCommands = '';

  commandNodes.forEach(node => {
    const command = node.data.command.replace('/', '');
    const description = node.data.description || '–ö–æ–º–∞–Ω–¥–∞ –±–æ—Ç–∞';
    botFatherCommands += `${command} - ${description}\n`;
  });

  return botFatherCommands.trim();
}

