                  {/* –ü—Ä–æ–≥—Ä–µ—Å—Å –∑–∞–≥—Ä—É–∑–∫–∏ */}
                  {file.uploadStatus === 'uploading' && (
                    <Progress value={file.uploadProgress || 0} className="mt-1" />
                  )}

                  {/* –°—Ç–∞—Ç—É—Å */}
                  {file.uploadStatus === 'success' && (
                    <div className="flex items-center mt-1 text-green-600 text-xs">
                      <CheckCircle2 className="w-3 h-3 mr-1" />
