  const [sortBy, setSortBy] = useState<'popular' | 'rating' | 'recent' | 'name'>('popular');

  const [flexibleLayoutConfig, setFlexibleLayoutConfig] = useState<SimpleLayoutConfig>({
    elements: [
      {
        id: 'header',
        type: 'header',
