        size: 20,
        visible: true
      },
      {
        id: 'canvas',
        type: 'canvas',
        name: '–°–æ–¥–µ—Ä–∂–∏–º–æ–µ',
        position: 'center',
        size: 60,
        visible: true
      },
      {
        id: 'properties',
        type: 'properties',
        name: '–§–∏–ª—å—Ç—Ä—ã',
