import { useState, useEffect } from 'react';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Button } from '@/components/ui/button';
import { RefreshCw, CheckCircle, AlertCircle, Loader2 } from 'lucide-react';

interface ServerHealthStatus {
  database: boolean;
  templates: boolean;
  telegram: boolean;
  ready: boolean;
}

export function ServerStatus() {
  const [status, setStatus] = useState<ServerHealthStatus | null>(null);
  const [isVisible, setIsVisible] = useState(true);

  const checkHealth = async () => {
    try {
      const response = await fetch('/api/health');
      const data = await response.json();
      setStatus(data);

      // –ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ —Å–∫—Ä—ã–≤–∞–µ–º –∏–Ω–¥–∏–∫–∞—Ç–æ—Ä –∫–æ–≥–¥–∞ –≤—Å–µ –≥–æ—Ç–æ–≤–æ
      if (data.ready) {
        setTimeout(() => setIsVisible(false), 2000);
      }
    } catch (error) {
      console.error('Health check failed:', error);
      // –ü–æ–≤—Ç–æ—Ä—è–µ–º –ø—Ä–æ–≤–µ—Ä–∫—É —á–µ—Ä–µ–∑ 2 —Å–µ–∫—É–Ω–¥—ã –ø—Ä–∏ –æ—à–∏–±–∫–µ
      setTimeout(checkHealth, 2000);
    }
  };

  useEffect(() => {
    checkHealth();

    // –¢–æ–ª—å–∫–æ –¥–µ–ª–∞–µ–º –æ–¥–∏–Ω –∑–∞–ø—Ä–æ—Å –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ, –¥–∞–ª—å—à–µ –ø–æ–ª–∞–≥–∞–µ–º—Å—è –Ω–∞ –∫–µ—à–∏—Ä–æ–≤–∞–Ω–∏–µ
    let interval: NodeJS.Timeout | null = null;

    if (!status?.ready) {
      // –ü—Ä–æ–≤–µ—Ä—è–µ–º –∫–∞–∂–¥—ã–µ 5 —Å–µ–∫—É–Ω–¥ —Ç–æ–ª—å–∫–æ –µ—Å–ª–∏ —Å–µ—Ä–≤–µ—Ä –Ω–µ –≥–æ—Ç–æ–≤
      interval = setInterval(checkHealth, 5000);
    }

    return () => {
      if (interval) {
        clearInterval(interval);
      }
    };
  }, [status?.ready]);

  // –ù–µ –ø–æ–∫–∞–∑—ã–≤–∞–µ–º –∏–Ω–¥–∏–∫–∞—Ç–æ—Ä –µ—Å–ª–∏ —Å—Ç–∞—Ç—É—Å –Ω–µ–∏–∑–≤–µ—Å—Ç–µ–Ω –∏–ª–∏ —Å–∏—Å—Ç–µ–º–∞ –≥–æ—Ç–æ–≤–∞ –∏ –ø—Ä–æ—à–ª–æ –≤—Ä–µ–º—è
  if (!status || (!isVisible && status.ready)) {
    return null;
  }

  // –ù–µ –ø–æ–∫–∞–∑—ã–≤–∞–µ–º –µ—Å–ª–∏ —É–∂–µ –≤—Å–µ –≥–æ—Ç–æ–≤–æ
  if (status.ready && isVisible) {
    return (
      <Alert className="fixed top-4 right-4 w-80 z-50 border-green-200 bg-green-50 dark:bg-green-950">
        <CheckCircle className="h-4 w-4 text-green-600" />
        <AlertDescription className="text-green-800 dark:text-green-200">
          –°–µ—Ä–≤–µ—Ä –≥–æ—Ç–æ–≤ –∫ —Ä–∞–±–æ—Ç–µ!
        </AlertDescription>
      </Alert>
    );
  }

  return (
    <Alert className="fixed top-4 right-4 w-80 z-50">
      <Loader2 className="h-4 w-4 animate-spin" />
      <AlertDescription>
        <div className="space-y-2">
          <div className="flex items-center justify-between">
            <span className="font-medium">–ó–∞–≥—Ä—É–∑–∫–∞ —Å–µ—Ä–≤–µ—Ä–∞...</span>
            <Button 
              variant="ghost" 
              size="sm" 
              onClick={checkHealth}
              className="h-6 w-6 p-0"
            >
              <RefreshCw className="h-3 w-3" />
            </Button>
          </div>

          <div className="space-y-1 text-sm">
            <div className="flex items-center gap-2">
              {status.database ? (
                <CheckCircle className="h-3 w-3 text-green-600" />
              ) : (
                <Loader2 className="h-3 w-3 animate-spin text-orange-500" />
              )}
              <span className={status.database ? 'text-green-700 dark:text-green-300' : 'text-orange-600 dark:text-orange-400'}>
                –ë–∞–∑–∞ –¥–∞–Ω–Ω—ã—Ö
              </span>
            </div>

            <div className="flex items-center gap-2">
              {status.templates ? (
                <CheckCircle className="h-3 w-3 text-green-600" />
              ) : (
                <Loader2 className="h-3 w-3 animate-spin text-orange-500" />
              )}
              <span className={status.templates ? 'text-green-700 dark:text-green-300' : 'text-orange-600 dark:text-orange-400'}>
                –®–∞–±–ª–æ–Ω—ã
              </span>
            </div>

            <div className="flex items-center gap-2">
              {status.telegram ? (
                <CheckCircle className="h-3 w-3 text-green-600" />
              ) : (
                <Loader2 className="h-3 w-3 animate-spin text-orange-500" />
              )}
              <span className={status.telegram ? 'text-green-700 dark:text-green-300' : 'text-orange-600 dark:text-orange-400'}>
                Telegram
              </span>
            </div>
          </div>

          {!status.ready && (
            <div className="text-xs text-muted-foreground mt-2">
              –ù–µ–∫–æ—Ç–æ—Ä—ã–µ —Ñ—É–Ω–∫—Ü–∏–∏ –º–æ–≥—É—Ç –±—ã—Ç—å –≤—Ä–µ–º–µ–Ω–Ω–æ –Ω–µ–¥–æ—Å—Ç—É–ø–Ω—ã
            </div>
          )}
        </div>
      </AlertDescription>
    </Alert>
  );
}

scripts/migrate.ts.content:
import { Pool } from 'pg';
import { drizzle } from 'drizzle-orm/node-postgres';
import { migrate } from 'drizzle-orm/node-postgres/migrator';
import * as schema from '../shared/schema';

async function runMigrations() {
  const databaseUrl = process.env.DATABASE_URL;

  if (!databaseUrl) {
    console.error('DATABASE_URL must be set');
    process.exit(1);
  }

  console.log('Initializing database connection...');

  const pool = new Pool({ 
    connectionString: databaseUrl,
    ssl: process.env.NODE_ENV === 'production' ? { rejectUnauthorized: false } : false,
  });

  const db = drizzle(pool, { schema });

  try {
    console.log('Running migrations...');
    await migrate(db, { migrationsFolder: './migrations' });
    console.log('Migrations completed successfully!');
  } catch (error) {
    console.error('Migration failed:', error);
    process.exit(1);
  } finally {
    await pool.end();
  }
}

runMigrations().catch(console.error);

