import React, { useState } from 'react';
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";
import { Switch } from "@/components/ui/switch";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Progress } from "@/components/ui/progress";
import { useToast } from "@/hooks/use-toast";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import type { MediaFile } from "@shared/schema";
import { 
  Download, 
  LinkIcon, 
  CheckCircle, 
  XCircle, 
  AlertTriangle, 
  FileText, 
  Image, 
  Play, 
  Volume2,
  Loader2,
  Info,
  ExternalLink,
  Copy,
  Trash2,
  Plus
} from "lucide-react";

interface UrlDownloaderProps {
  projectId: number;
  onDownloadComplete?: (files: MediaFile[]) => void;
  onClose?: () => void;
  maxUrls?: number;
}

interface UrlData {
  id: string;
  url: string;
  fileName?: string;
  description?: string;
  status: 'pending' | 'checking' | 'valid' | 'invalid' | 'downloading' | 'success' | 'error';
  fileInfo?: {
    mimeType?: string;
    size?: number;
    fileName?: string;
    fileType?: string;
    category?: string;
    sizeMB?: number;
  };
  error?: string;
  progress?: number;
}

export function UrlDownloader({ 
  projectId, 
  onDownloadComplete, 
  onClose,
  maxUrls = 10
}: UrlDownloaderProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [urls, setUrls] = useState<UrlData[]>([{
    id: Math.random().toString(36).substr(2, 9),
    url: '',
    status: 'pending'
  }]);
  const [isPublic, setIsPublic] = useState(false);
  const [defaultDescription, setDefaultDescription] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);

  // –ú—É—Ç–∞—Ü–∏—è –¥–ª—è –ø—Ä–æ–≤–µ—Ä–∫–∏ URL
  const checkUrlMutation = useMutation({
    mutationFn: async (url: string) => {
      const response = await apiRequest('POST', '/api/media/check-url', { url });
      return response;
    }
  });

  // –ú—É—Ç–∞—Ü–∏—è –¥–ª—è –∑–∞–≥—Ä—É–∑–∫–∏ –æ–¥–Ω–æ–≥–æ —Ñ–∞–π–ª–∞
  const downloadUrlMutation = useMutation({
    mutationFn: async ({ url, description, customFileName }: { 
      url: string; 
      description?: string; 
      customFileName?: string; 
    }) => {
      const response = await apiRequest('POST', `/api/media/download-url/${projectId}`, { 
        url, 
        description, 
        customFileName,
        isPublic 
      });
      return response;
    }
  });

  // –ú—É—Ç–∞—Ü–∏—è –¥–ª—è –ø–∞–∫–µ—Ç–Ω–æ–π –∑–∞–≥—Ä—É–∑–∫–∏
  const downloadUrlsMutation = useMutation({
    mutationFn: async (urlsData: { url: string; fileName?: string; description?: string }[]) => {
      const response = await apiRequest('POST', `/api/media/download-urls/${projectId}`, { 
        urls: urlsData,
        isPublic,
        defaultDescription 
      });
      return response;
    }
  });

  const getFileIcon = (fileType?: string) => {
    switch (fileType) {
      case 'photo': return <Image className="w-4 h-4 text-blue-500" />;
      case 'video': return <Play className="w-4 h-4 text-purple-500" />;
      case 'audio': return <Volume2 className="w-4 h-4 text-green-500" />;
      case 'document': return <FileText className="w-4 h-4 text-orange-500" />;
      default: return <FileText className="w-4 h-4 text-gray-500" />;
    }
  };

  const addUrlField = () => {
    if (urls.length < maxUrls) {
      setUrls(prev => [...prev, {
        id: Math.random().toString(36).substr(2, 9),
        url: '',
        status: 'pending'
      }]);
    }
  };

  const removeUrlField = (id: string) => {
    if (urls.length > 1) {
      setUrls(prev => prev.filter(u => u.id !== id));
    }
  };

  const updateUrl = (id: string, url: string) => {
    setUrls(prev => prev.map(u => 
      u.id === id 
        ? { ...u, url, status: 'pending' as const, fileInfo: undefined, error: undefined }
        : u
    ));
  };

  const updateUrlField = (id: string, field: keyof UrlData, value: any) => {
    setUrls(prev => prev.map(u => 
      u.id === id 
        ? { ...u, [field]: value }
        : u
    ));
  };

  const checkUrl = async (id: string, url: string) => {
    if (!url.trim()) return;

    updateUrlField(id, 'status', 'checking');

    try {
      const result = await checkUrlMutation.mutateAsync(url);

      if (result.accessible) {
        updateUrlField(id, 'status', 'valid');
        updateUrlField(id, 'fileInfo', result.fileInfo);

        // –ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ –∑–∞–ø–æ–ª–Ω—è–µ–º –∏–º—è —Ñ–∞–π–ª–∞ –µ—Å–ª–∏ –æ–Ω–æ –Ω–µ –∑–∞–¥–∞–Ω–æ
        if (result.fileInfo?.fileName && !urls.find(u => u.id === id)?.fileName) {
          updateUrlField(id, 'fileName', result.fileInfo.fileName);
        }
      } else {
        updateUrlField(id, 'status', 'invalid');
        updateUrlField(id, 'error', result.error);
      }
    } catch (error) {
      updateUrlField(id, 'status', 'invalid');
      updateUrlField(id, 'error', error instanceof Error ? error.message : '–û—à–∏–±–∫–∞ –ø—Ä–æ–≤–µ—Ä–∫–∏ URL');
    }
  };

  const downloadSingle = async (urlData: UrlData) => {
    if (!urlData.url.trim() || urlData.status !== 'valid') return;

    updateUrlField(urlData.id, 'status', 'downloading');
    updateUrlField(urlData.id, 'progress', 0);

    try {
      const result = await downloadUrlMutation.mutateAsync({
        url: urlData.url,
        description: urlData.description || defaultDescription,
        customFileName: urlData.fileName
      });

      updateUrlField(urlData.id, 'status', 'success');
      updateUrlField(urlData.id, 'progress', 100);

      toast({
        title: "–§–∞–π–ª –∑–∞–≥—Ä—É–∂–µ–Ω",
        description: `${result.fileName} —É—Å–ø–µ—à–Ω–æ –∑–∞–≥—Ä—É–∂–µ–Ω`,
      });

      // –û–±–Ω–æ–≤–ª—è–µ–º –∫—ç—à –º–µ–¥–∏–∞—Ñ–∞–π–ª–æ–≤
      queryClient.invalidateQueries({ queryKey: ['/api/media/project', projectId] });

      onDownloadComplete?.([result]);

    } catch (error) {
      updateUrlField(urlData.id, 'status', 'error');
      updateUrlField(urlData.id, 'error', error instanceof Error ? error.message : '–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏');

      toast({
        title: "–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏",
        description: error instanceof Error ? error.message : '–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–∞—è –æ—à–∏–±–∫–∞',
        variant: "destructive",
      });
    }
  };

  const downloadAll = async () => {
    const validUrls = urls.filter(u => u.status === 'valid' && u.url.trim());

    if (validUrls.length === 0) {
      toast({
        title: "–ù–µ—Ç –≤–∞–ª–∏–¥–Ω—ã—Ö URL",
        description: "–î–æ–±–∞–≤—å—Ç–µ –∏ –ø—Ä–æ–≤–µ—Ä—å—Ç–µ URL –¥–ª—è –∑–∞–≥—Ä—É–∑–∫–∏",
        variant: "destructive",
      });
      return;
    }

    setIsProcessing(true);

    try {
      // –û–±–Ω–æ–≤–ª—è–µ–º —Å—Ç–∞—Ç—É—Å –≤—Å–µ—Ö URL
      validUrls.forEach(urlData => {
        updateUrlField(urlData.id, 'status', 'downloading');
        updateUrlField(urlData.id, 'progress', 0);
      });

      const urlsData = validUrls.map(u => ({
        url: u.url,
        fileName: u.fileName,
        description: u.description
      }));

      const result = await downloadUrlsMutation.mutateAsync(urlsData);

      // –û–±–Ω–æ–≤–ª—è–µ–º —Å—Ç–∞—Ç—É—Å—ã –Ω–∞ –æ—Å–Ω–æ–≤–µ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–∞
      validUrls.forEach((urlData, index) => {
        const wasSuccessful = result.downloadedFiles.some((f: any) => f.sourceUrl === urlData.url);
        const error = result.errorDetails.find((e: any) => e.url === urlData.url);

        if (wasSuccessful) {
          updateUrlField(urlData.id, 'status', 'success');
          updateUrlField(urlData.id, 'progress', 100);
        } else {
          updateUrlField(urlData.id, 'status', 'error');
          updateUrlField(urlData.id, 'error', error?.error || '–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–∞—è –æ—à–∏–±–∫–∞');
        }
      });

      toast({
        title: "–ü–∞–∫–µ—Ç–Ω–∞—è –∑–∞–≥—Ä—É–∑–∫–∞ –∑–∞–≤–µ—Ä—à–µ–Ω–∞",
        description: `–£—Å–ø–µ—à–Ω–æ: ${result.success}, –û—à–∏–±–æ–∫: ${result.errors}`,
      });

      // –û–±–Ω–æ–≤–ª—è–µ–º –∫—ç—à –º–µ–¥–∏–∞—Ñ–∞–π–ª–æ–≤
      queryClient.invalidateQueries({ queryKey: ['/api/media/project', projectId] });

      if (result.downloadedFiles.length > 0) {
        onDownloadComplete?.(result.downloadedFiles);
      }

      // –ó–∞–∫—Ä—ã–≤–∞–µ–º —á–µ—Ä–µ–∑ 3 —Å–µ–∫—É–Ω–¥—ã –µ—Å–ª–∏ –≤—Å–µ —É—Å–ø–µ—à–Ω–æ
      if (result.errors === 0) {
        setTimeout(() => {
          onClose?.();
        }, 3000);
      }

    } catch (error) {
      validUrls.forEach(urlData => {
        updateUrlField(urlData.id, 'status', 'error');
        updateUrlField(urlData.id, 'error', '–û—à–∏–±–∫–∞ –ø–∞–∫–µ—Ç–Ω–æ–π –∑–∞–≥—Ä—É–∑–∫–∏');
      });

      toast({
        title: "–û—à–∏–±–∫–∞ –ø–∞–∫–µ—Ç–Ω–æ–π –∑–∞–≥—Ä—É–∑–∫–∏",
        description: error instanceof Error ? error.message : '–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–∞—è –æ—à–∏–±–∫–∞',
        variant: "destructive",
      });
    } finally {
      setIsProcessing(false);
    }
  };

  const pasteFromClipboard = async (id: string) => {
    try {
      const text = await navigator.clipboard.readText();
      if (text) {
        updateUrl(id, text);
        // –ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ –ø—Ä–æ–≤–µ—Ä—è–µ–º URL –ø–æ—Å–ª–µ –≤—Å—Ç–∞–≤–∫–∏
        setTimeout(() => checkUrl(id, text), 100);
      }
    } catch (error) {
      toast({
        title: "–û—à–∏–±–∫–∞ –≤—Å—Ç–∞–≤–∫–∏",
        description: "–ù–µ —É–¥–∞–ª–æ—Å—å –ø–æ–ª—É—á–∏—Ç—å —Ç–µ–∫—Å—Ç –∏–∑ –±—É—Ñ–µ—Ä–∞ –æ–±–º–µ–Ω–∞",
        variant: "destructive",
      });
    }
  };

  const validUrlsCount = urls.filter(u => u.status === 'valid').length;
  const totalUrlsCount = urls.filter(u => u.url.trim()).length;

  return (
    <div className="space-y-6">
      {/* –ó–∞–≥–æ–ª–æ–≤–æ–∫ */}
      <div className="flex items-center justify-between">
        <div>
          <h3 className="text-lg font-semibold flex items-center gap-2">
            <LinkIcon className="w-5 h-5" />
            –ó–∞–≥—Ä—É–∑–∫–∞ —Ñ–∞–π–ª–æ–≤ –ø–æ —Å—Å—ã–ª–∫–∞–º
          </h3>
          <p className="text-sm text-muted-foreground">
            –ó–∞–≥—Ä—É–∂–∞–π—Ç–µ —Ñ–∞–π–ª—ã –Ω–∞–ø—Ä—è–º—É—é –∏–∑ –∏–Ω—Ç–µ—Ä–Ω–µ—Ç–∞ –ø–æ URL
          </p>
        </div>
        <Badge variant="outline">
          {validUrlsCount}/{totalUrlsCount} –≥–æ—Ç–æ–≤–æ
        </Badge>
      </div>

      {/* –û–±—â–∏–µ –Ω–∞—Å—Ç—Ä–æ–π–∫–∏ */}
      <Card>
        <CardHeader>
          <CardTitle className="text-base">–û–±—â–∏–µ –Ω–∞—Å—Ç—Ä–æ–π–∫–∏</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="space-y-2">
            <Label htmlFor="defaultDescription">–û–ø–∏—Å–∞–Ω–∏–µ –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é</Label>
            <Textarea
              id="defaultDescription"
              placeholder="–û–ø–∏—Å–∞–Ω–∏–µ –¥–ª—è –≤—Å–µ—Ö –∑–∞–≥—Ä—É–∂–∞–µ–º—ã—Ö —Ñ–∞–π–ª–æ–≤..."
              value={defaultDescription}
              onChange={(e) => setDefaultDescription(e.target.value)}
              rows={2}
            />
          </div>

          <div className="flex items-center space-x-2">
            <Switch
              id="isPublic"
              checked={isPublic}
              onCheckedChange={setIsPublic}
            />
            <Label htmlFor="isPublic">–ü—É–±–ª–∏—á–Ω—ã–µ —Ñ–∞–π–ª—ã</Label>
          </div>
        </CardContent>
      </Card>

      {/* URL –ø–æ–ª—è */}
      <Card>
        <CardHeader>
          <CardTitle className="text-base flex items-center justify-between">
            URL –¥–ª—è –∑–∞–≥—Ä—É–∑–∫–∏
            <Button
              onClick={addUrlField}
              size="sm"
              variant="outline"
              disabled={urls.length >= maxUrls}
            >
              <Plus className="w-4 h-4 mr-1" />
              –î–æ–±–∞–≤–∏—Ç—å URL
            </Button>
          </CardTitle>
          <CardDescription>
            –ü–æ–¥–¥–µ—Ä–∂–∏–≤–∞—é—Ç—Å—è –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è, –≤–∏–¥–µ–æ, –∞—É–¥–∏–æ –∏ –¥–æ–∫—É–º–µ–Ω—Ç—ã –¥–æ 200–ú–ë
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          {urls.map((urlData, index) => (
            <div key={urlData.id} className="space-y-3 p-4 border rounded-lg">
              {/* URL –≤–≤–æ–¥ */}
              <div className="flex gap-2">
                <div className="flex-1">
                  <Input
                    placeholder="https://example.com/file.jpg"
                    value={urlData.url}
                    onChange={(e) => updateUrl(urlData.id, e.target.value)}
                    onBlur={() => urlData.url.trim() && checkUrl(urlData.id, urlData.url)}
                  />
                </div>
                <Button
                  size="sm"
                  variant="outline"
                  onClick={() => pasteFromClipboard(urlData.id)}
                >
                  <Copy className="w-4 h-4" />
                </Button>
                <Button
                  size="sm"
                  variant="outline"
                  onClick={() => urlData.url.trim() && checkUrl(urlData.id, urlData.url)}
                  disabled={!urlData.url.trim() || urlData.status === 'checking'}
                >
                  {urlData.status === 'checking' ? (
                    <Loader2 className="w-4 h-4 animate-spin" />
                  ) : (
                    <ExternalLink className="w-4 h-4" />
                  )}
                </Button>
                {urls.length > 1 && (
                  <Button
                    size="sm"
                    variant="outline"
                    onClick={() => removeUrlField(urlData.id)}
                  >
                    <Trash2 className="w-4 h-4" />
                  </Button>
                )}
              </div>

              {/* –°—Ç–∞—Ç—É—Å */}
              {urlData.status !== 'pending' && (
                <div className="flex items-center gap-2">
                  {urlData.status === 'checking' && (
                    <div className="flex items-center gap-2 text-blue-600">
                      <Loader2 className="w-4 h-4 animate-spin" />
                      <span className="text-sm">–ü—Ä–æ–≤–µ—Ä–∫–∞...</span>
                    </div>
                  )}

                  {urlData.status === 'valid' && (
                    <div className="flex items-center gap-2 text-green-600">
                      <CheckCircle className="w-4 h-4" />
                      <span className="text-sm">URL –≤–∞–ª–∏–¥–µ–Ω</span>
                    </div>
                  )}

                  {urlData.status === 'invalid' && (
                    <div className="flex items-center gap-2 text-red-600">
                      <XCircle className="w-4 h-4" />
                      <span className="text-sm">–û—à–∏–±–∫–∞: {urlData.error}</span>
                    </div>
                  )}

                  {urlData.status === 'downloading' && (
                    <div className="flex items-center gap-2 text-blue-600">
                      <Download className="w-4 h-4" />
                      <span className="text-sm">–ó–∞–≥—Ä—É–∑–∫–∞... {urlData.progress || 0}%</span>
                      <Progress value={urlData.progress || 0} className="w-20" />
                    </div>
                  )}

                  {urlData.status === 'success' && (
                    <div className="flex items-center gap-2 text-green-600">
                      <CheckCircle className="w-4 h-4" />
                      <span className="text-sm">–ó–∞–≥—Ä—É–∂–µ–Ω–æ —É—Å–ø–µ—à–Ω–æ</span>
                    </div>
                  )}

                  {urlData.status === 'error' && (
                    <div className="flex items-center gap-2 text-red-600">
                      <XCircle className="w-4 h-4" />
                      <span className="text-sm">–û—à–∏–±–∫–∞: {urlData.error}</span>
                    </div>
                  )}
                </div>
              )}

              {/* –ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ —Ñ–∞–π–ª–µ */}
              {urlData.fileInfo && (
                <div className="flex items-center gap-3 p-2 bg-muted rounded text-sm">
                  {getFileIcon(urlData.fileInfo.fileType)}
                  <div className="flex-1">
                    <div className="font-medium">{urlData.fileInfo.fileName}</div>
                    <div className="text-muted-foreground">
                      {urlData.fileInfo.category} ‚Ä¢ {urlData.fileInfo.sizeMB}–ú–ë
                    </div>
                  </div>
                  <Badge variant="secondary">{urlData.fileInfo.fileType}</Badge>
                </div>
              )}

              {/* –î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–µ –ø–æ–ª—è –¥–ª—è –≤–∞–ª–∏–¥–Ω—ã—Ö URL */}
              {urlData.status === 'valid' && (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                  <div className="space-y-1">
                    <Label htmlFor={`fileName-${urlData.id}`}>–ò–º—è —Ñ–∞–π–ª–∞</Label>
                    <Input
                      id={`fileName-${urlData.id}`}
                      placeholder="–û—Å—Ç–∞–≤—å—Ç–µ –ø—É—Å—Ç—ã–º –¥–ª—è –∞–≤—Ç–æ–æ–ø—Ä–µ–¥–µ–ª–µ–Ω–∏—è"
                      value={urlData.fileName || ''}
                      onChange={(e) => updateUrlField(urlData.id, 'fileName', e.target.value)}
                    />
                  </div>
                  <div className="space-y-1">
                    <Label htmlFor={`description-${urlData.id}`}>–û–ø–∏—Å–∞–Ω–∏–µ</Label>
                    <Input
                      id={`description-${urlData.id}`}
                      placeholder="–ò–Ω–¥–∏–≤–∏–¥—É–∞–ª—å–Ω–æ–µ –æ–ø–∏—Å–∞–Ω–∏–µ —Ñ–∞–π–ª–∞"
                      value={urlData.description || ''}
                      onChange={(e) => updateUrlField(urlData.id, 'description', e.target.value)}
                    />
                  </div>
                </div>
              )}

              {/* –ö–Ω–æ–ø–∫–∞ –∏–Ω–¥–∏–≤–∏–¥—É–∞–ª—å–Ω–æ–π –∑–∞–≥—Ä—É–∑–∫–∏ */}
              {urlData.status === 'valid' && (
                <Button
                  onClick={() => downloadSingle(urlData)}
                  size="sm"
                  className="w-full"
                  disabled={urlData.status === 'downloading'}
                >
                  <Download className="w-4 h-4 mr-2" />
                  –ó–∞–≥—Ä—É–∑–∏—Ç—å —ç—Ç–æ—Ç —Ñ–∞–π–ª
                </Button>
              )}
            </div>
          ))}
        </CardContent>
      </Card>

      {/* –ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏–æ–Ω–Ω–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ */}
      <Alert>
        <Info className="h-4 w-4" />
        <AlertDescription>
          <strong>–°–æ–≤–µ—Ç:</strong> –°–Ω–∞—á–∞–ª–∞ –¥–æ–±–∞–≤—å—Ç–µ –≤—Å–µ URL –∏ –¥–æ–∂–¥–∏—Ç–µ—Å—å –∏—Ö –ø—Ä–æ–≤–µ—Ä–∫–∏, 
          –∑–∞—Ç–µ–º –∏—Å–ø–æ–ª—å–∑—É–π—Ç–µ "–ó–∞–≥—Ä—É–∑–∏—Ç—å –≤—Å–µ —Ñ–∞–π–ª—ã" –¥–ª—è —ç—Ñ—Ñ–µ–∫—Ç–∏–≤–Ω–æ–π –ø–∞–∫–µ—Ç–Ω–æ–π –∑–∞–≥—Ä—É–∑–∫–∏.
        </AlertDescription>
      </Alert>

      {/* –ö–Ω–æ–ø–∫–∏ –¥–µ–π—Å—Ç–≤–∏–π */}
      <div className="flex gap-2">
        <Button
          onClick={downloadAll}
          disabled={validUrlsCount === 0 || isProcessing}
          className="flex-1"
        >
          {isProcessing ? (
            <Loader2 className="w-4 h-4 mr-2 animate-spin" />
          ) : (
            <Download className="w-4 h-4 mr-2" />
          )}
          –ó–∞–≥—Ä—É–∑–∏—Ç—å –≤—Å–µ —Ñ–∞–π–ª—ã ({validUrlsCount})
        </Button>

        {onClose && (
          <Button variant="outline" onClick={onClose}>
            –ó–∞–∫—Ä—ã—Ç—å
          </Button>
        )}
      </div>
    </div>
  );
}

