import React, { useState, useMemo } from 'react';
import { useQuery } from '@tanstack/react-query';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { Separator } from '@/components/ui/separator';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { apiRequest } from '@/lib/queryClient';
import { 
  MessageSquare, 
  Search, 
  Filter, 
  Download, 
  Eye, 
  Calendar,
  RefreshCw,
  User,
  FileText,
  Clock,
  Hash,
  MessageCircle,
  Settings
} from 'lucide-react';

interface ResponsesData {
  user_id: string;
  username: string;
  first_name: string;
  last_name: string;
  registered_at: string;
  last_interaction: string;
  responses: Array<{
    key: string;
    value: string;
    type: string;
    timestamp: string;
    nodeId?: string;
    prompt?: string;
    variable?: string;
  }>;
  responseCount: number;
}

interface ResponsesPanelProps {
  projectId: number;
  projectName: string;
}

type SortField = 'last_interaction' | 'responseCount' | 'registered_at' | 'first_name';
type SortDirection = 'asc' | 'desc';

export function ResponsesPanel({ projectId, projectName }: ResponsesPanelProps) {
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedUser, setSelectedUser] = useState<ResponsesData | null>(null);
  const [showResponseDetails, setShowResponseDetails] = useState(false);
  const [sortField, setSortField] = useState<SortField>('last_interaction');
  const [sortDirection, setSortDirection] = useState<SortDirection>('desc');
  const [filterResponseType, setFilterResponseType] = useState<string>('all');
  const [showTechnicalDetails, setShowTechnicalDetails] = useState<boolean>(false);

  // Fetch responses data
  const { data: responsesData = [], isLoading, refetch } = useQuery<ResponsesData[]>({
    queryKey: [`/api/projects/${projectId}/responses`],
    staleTime: 10000, // –ö–µ—à–∏—Ä—É–µ–º –¥–∞–Ω–Ω—ã–µ –Ω–∞ 10 —Å–µ–∫—É–Ω–¥
    refetchInterval: 30000, // –û–±–Ω–æ–≤–ª—è–µ–º –∫–∞–∂–¥—ã–µ 30 —Å–µ–∫—É–Ω–¥ –≤–º–µ—Å—Ç–æ 5
    refetchIntervalInBackground: false, // –ù–µ –æ–ø—Ä–∞—à–∏–≤–∞–µ–º –≤ —Ñ–æ–Ω–µ
  });

  // Debug logging
  React.useEffect(() => {
    console.log('Responses data updated:', responsesData);
  }, [responsesData]);

  // Filter and sort responses
  const filteredAndSortedResponses = useMemo(() => {
    let result = responsesData;

    // Apply search filter
    if (searchQuery.length > 0) {
      const query = searchQuery.toLowerCase();
      result = result.filter(user => 
        (user.first_name?.toLowerCase().includes(query)) ||
        (user.last_name?.toLowerCase().includes(query)) ||
        (user.username?.toLowerCase().includes(query)) ||
        (user.user_id?.includes(query)) ||
        user.responses.some(response => response.value?.toLowerCase().includes(query))
      );
    }

    // Apply response type filter
    if (filterResponseType !== 'all') {
      result = result.filter(user => 
        user.responses.some(response => response.type === filterResponseType)
      );
    }

    // Sort
    result = [...result].sort((a, b) => {
      let aValue: any, bValue: any;

      switch (sortField) {
        case 'last_interaction':
          aValue = new Date(a.last_interaction || 0).getTime();
          bValue = new Date(b.last_interaction || 0).getTime();
          break;
        case 'registered_at':
          aValue = new Date(a.registered_at || 0).getTime();
          bValue = new Date(b.registered_at || 0).getTime();
          break;
        case 'responseCount':
          aValue = a.responseCount;
          bValue = b.responseCount;
          break;
        case 'first_name':
          aValue = (a.first_name || '').toLowerCase();
          bValue = (b.first_name || '').toLowerCase();
          break;
        default:
          aValue = a[sortField];
          bValue = b[sortField];
      }

      if (sortDirection === 'asc') {
        return aValue > bValue ? 1 : -1;
      } else {
        return aValue < bValue ? 1 : -1;
      }
    });

    return result;
  }, [responsesData, searchQuery, filterResponseType, sortField, sortDirection]);

  const formatDate = (date: string | null) => {
    if (!date) return '–ù–∏–∫–æ–≥–¥–∞';
    return new Date(date).toLocaleString('ru-RU', {
      timeZone: 'Europe/Moscow',
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const formatUserName = (user: ResponsesData) => {
    const parts = [user.first_name, user.last_name].filter(Boolean);
    if (parts.length > 0) return parts.join(' ');
    if (user.username) return `@${user.username}`;
    return `ID: ${user.user_id}`;
  };

  const getResponseTypeColor = (type: string) => {
    switch (type) {
      case 'text': return 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200';
      case 'button': return 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200';
      case 'number': return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200';
      case 'email': return 'bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200';
      case 'phone': return 'bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-200';
      case 'inline_button': return 'bg-indigo-100 text-indigo-800 dark:bg-indigo-900 dark:text-indigo-200';
      case 'reply_button': return 'bg-cyan-100 text-cyan-800 dark:bg-cyan-900 dark:text-cyan-200';
      case 'button_choice': return 'bg-teal-100 text-teal-800 dark:bg-teal-900 dark:text-teal-200';
      default: return 'bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-200';
    }
  };

  const formatResponseKey = (key: string, type?: string) => {
    if (key === 'button_click') return '–î–µ–π—Å—Ç–≤–∏–µ';
    if (key.startsWith('response_')) return key.replace('response_', '–û—Ç–≤–µ—Ç ');
    if (type === 'inline_button' || type === 'button') return '–í—ã–±–æ—Ä';
    if (key === '–∏—Å—Ç–æ—á–Ω–∏–∫') return '–ò—Å—Ç–æ—á–Ω–∏–∫ –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–∏';
    if (key === '–∂–µ–ª–∞–Ω–∏–µ') return '–ñ–µ–ª–∞–Ω–∏–µ –ø—Ä–æ–¥–æ–ª–∂–∏—Ç—å';
    if (key === '–ø–æ–ª') return '–ü–æ–ª';
    if (key === '–∏–º—è') return '–ò–º—è';
    if (key === '–≤–æ–∑—Ä–∞—Å—Ç') return '–í–æ–∑—Ä–∞—Å—Ç';
    return key;
  };

  const formatResponseValue = (value: string, type?: string, key?: string) => {
    // –ï—Å–ª–∏ —ç—Ç–æ –ø–æ—Ö–æ–∂–µ –Ω–∞ node ID, –∑–∞–º–µ–Ω—è–µ–º –Ω–∞ –ø–æ–Ω—è—Ç–Ω–æ–µ –∑–Ω–∞—á–µ–Ω–∏–µ
    if (typeof value === 'string') {
      if (value.match(/^--[a-zA-Z0-9_-]{10,}$/) ||
          value.match(/^[a-zA-Z0-9_-]{15,}$/) ||
          value.match(/^[a-zA-Z0-9-]{20,}$/)) {
        return '–ü—Ä–æ–¥–æ–ª–∂–∏–ª –¥–∏–∞–ª–æ–≥';
      }
      // –£–ª—É—á—à–∞–µ–º –æ—Ç–æ–±—Ä–∞–∂–µ–Ω–∏–µ –¥–ª—è —Å–ø–µ—Ü–∏—Ñ–∏—á–µ—Å–∫–∏—Ö –∑–Ω–∞—á–µ–Ω–∏–π
      if ((value === '–ù–∞–∂–∞—Ç–∏–µ –∫–Ω–æ–ø–∫–∏' || value === '–ü–µ—Ä–µ—Ö–æ–¥ –∫ —Å–ª–µ–¥—É—é—â–µ–º—É —à–∞–≥—É') && key === 'button_click') {
        return '–ü—Ä–æ–¥–æ–ª–∂–∏–ª –¥–∏–∞–ª–æ–≥';
      }
    }
    return value;
  };

  // Get unique response types for filter
  const responseTypes = useMemo(() => {
    const types = new Set<string>();
    responsesData.forEach(user => {
      user.responses.forEach(response => {
        if (response.type) types.add(response.type);
      });
    });
    return Array.from(types);
  }, [responsesData]);

  const exportResponses = () => {
    const csvContent = [
      ['User ID', 'Name', 'Username', 'Response Key', 'Response', 'Type', 'Timestamp', 'Node ID'].join(','),
      ...filteredAndSortedResponses.flatMap(user =>
        user.responses.map(response => [
          user.user_id,
          `"${formatUserName(user)}"`,
          user.username || '',
          formatResponseKey(response.key, response.type),
          `"${response.value}"`,
          response.type,
          response.timestamp || '',
          response.nodeId || ''
        ].join(','))
      )
    ].join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const link = document.createElement('a');
    link.href = URL.createObjectURL(blob);
    link.download = `responses_${projectName}_${new Date().toISOString().split('T')[0]}.csv`;
    link.click();
  };

  if (isLoading) {
    return (
      <div className="h-full flex items-center justify-center">
        <div className="text-center">
          <RefreshCw className="w-8 h-8 animate-spin text-muted-foreground mx-auto mb-2" />
          <p className="text-muted-foreground">–ó–∞–≥—Ä—É–∑–∫–∞ –æ—Ç–≤–µ—Ç–æ–≤...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="h-full flex flex-col bg-background">
      <div className="border-b bg-card">
        <div className="p-4">
          <div className="flex items-center justify-between mb-4">
            <div>
              <h2 className="text-xl font-semibold flex items-center gap-2">
                <MessageSquare className="w-5 h-5" />
                –û—Ç–≤–µ—Ç—ã –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π
              </h2>
              <p className="text-sm text-muted-foreground">
                –ü—Ä–æ—Å–º–æ—Ç—Ä –≤—Å–µ—Ö –æ—Ç–≤–µ—Ç–æ–≤ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π –¥–ª—è –±–æ—Ç–∞ "{projectName}"
              </p>
            </div>
            <div className="flex items-center gap-2">
              <Button onClick={() => refetch()} variant="outline" size="sm">
                <RefreshCw className="w-4 h-4 mr-1" />
                –û–±–Ω–æ–≤–∏—Ç—å
              </Button>
              <Button onClick={exportResponses} variant="outline" size="sm">
                <Download className="w-4 h-4 mr-1" />
                –≠–∫—Å–ø–æ—Ä—Ç CSV
              </Button>
              <Button 
                onClick={() => setShowTechnicalDetails(!showTechnicalDetails)} 
                variant={showTechnicalDetails ? "default" : "outline"} 
                size="sm"
              >
                <Settings className="w-4 h-4 mr-1" />
                {showTechnicalDetails ? '–°–∫—Ä—ã—Ç—å –¥–µ—Ç–∞–ª–∏' : '–ü–æ–∫–∞–∑–∞—Ç—å –¥–µ—Ç–∞–ª–∏'}
              </Button>
            </div>
          </div>

          {/* Stats */}
          <div className="grid grid-cols-2 md:grid-cols-4 gap-3 mb-4">
            <Card className="p-3">
              <div className="flex items-center gap-2">
                <User className="w-4 h-4 text-blue-500" />
                <div>
                  <p className="text-xs text-muted-foreground">–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π</p>
                  <p className="text-sm font-semibold">{responsesData.length}</p>
                </div>
              </div>
            </Card>
            <Card className="p-3">
              <div className="flex items-center gap-2">
                <MessageCircle className="w-4 h-4 text-green-500" />
                <div>
                  <p className="text-xs text-muted-foreground">–í—Å–µ–≥–æ –æ—Ç–≤–µ—Ç–æ–≤</p>
                  <p className="text-sm font-semibold">
                    {responsesData.reduce((sum, user) => sum + user.responseCount, 0)}
                  </p>
                </div>
              </div>
            </Card>
            <Card className="p-3">
              <div className="flex items-center gap-2">
                <Hash className="w-4 h-4 text-purple-500" />
                <div>
                  <p className="text-xs text-muted-foreground">–¢–∏–ø–æ–≤ –æ—Ç–≤–µ—Ç–æ–≤</p>
                  <p className="text-sm font-semibold">{responseTypes.length}</p>
                </div>
              </div>
            </Card>
            <Card className="p-3">
              <div className="flex items-center gap-2">
                <Clock className="w-4 h-4 text-orange-500" />
                <div>
                  <p className="text-xs text-muted-foreground">–°—Ä–µ–¥. –æ—Ç–≤–µ—Ç–æ–≤</p>
                  <p className="text-sm font-semibold">
                    {responsesData.length > 0 
                      ? Math.round(responsesData.reduce((sum, user) => sum + user.responseCount, 0) / responsesData.length)
                      : 0}
                  </p>
                </div>
              </div>
            </Card>
          </div>

          {/* Search and Filters */}
          <div className="flex flex-col sm:flex-row gap-3">
            <div className="flex-1 relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-muted-foreground" />
              <Input
                placeholder="–ü–æ–∏—Å–∫ –ø–æ –∏–º–µ–Ω–∏, username, ID –∏–ª–∏ —Ç–µ–∫—Å—Ç—É –æ—Ç–≤–µ—Ç–∞..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-10"
              />
            </div>
            <div className="flex gap-2">
              <Select value={filterResponseType} onValueChange={setFilterResponseType}>
                <SelectTrigger className="w-40">
                  <SelectValue placeholder="–¢–∏–ø –æ—Ç–≤–µ—Ç–∞" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">–í—Å–µ —Ç–∏–ø—ã</SelectItem>
                  {responseTypes.map(type => (
                    <SelectItem key={type} value={type}>
                      {type === 'text' ? '–¢–µ–∫—Å—Ç' : 
                       type === 'button' ? '–ö–Ω–æ–ø–∫–∞' :
                       type === 'number' ? '–ß–∏—Å–ª–æ' :
                       type === 'email' ? 'Email' :
                       type === 'phone' ? '–¢–µ–ª–µ—Ñ–æ–Ω' :
                       type === 'inline_button' ? '–ò–Ω–ª–∞–π–Ω –∫–Ω–æ–ø–∫–∞' :
                       type === 'reply_button' ? 'Reply –∫–Ω–æ–ø–∫–∞' :
                       type === 'button_choice' ? '–í—ã–±–æ—Ä –∫–Ω–æ–ø–∫–∏' :
                       type}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>

              <Select value={`${sortField}-${sortDirection}`} onValueChange={(value) => {
                const [field, direction] = value.split('-') as [SortField, SortDirection];
                setSortField(field);
                setSortDirection(direction);
              }}>
                <SelectTrigger className="w-48">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="last_interaction-desc">–ü–æ—Å–ª–µ–¥–Ω—è—è –∞–∫—Ç–∏–≤–Ω–æ—Å—Ç—å ‚Üì</SelectItem>
                  <SelectItem value="last_interaction-asc">–ü–æ—Å–ª–µ–¥–Ω—è—è –∞–∫—Ç–∏–≤–Ω–æ—Å—Ç—å ‚Üë</SelectItem>
                  <SelectItem value="responseCount-desc">–ë–æ–ª—å—à–µ –æ—Ç–≤–µ—Ç–æ–≤ ‚Üì</SelectItem>
                  <SelectItem value="responseCount-asc">–ú–µ–Ω—å—à–µ –æ—Ç–≤–µ—Ç–æ–≤ ‚Üë</SelectItem>
                  <SelectItem value="registered_at-desc">–î–∞—Ç–∞ —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏–∏ ‚Üì</SelectItem>
                  <SelectItem value="registered_at-asc">–î–∞—Ç–∞ —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏–∏ ‚Üë</SelectItem>
                  <SelectItem value="first_name-asc">–ü–æ –∏–º–µ–Ω–∏ ‚Üë</SelectItem>
                  <SelectItem value="first_name-desc">–ü–æ –∏–º–µ–Ω–∏ ‚Üì</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>
        </div>
      </div>

      <div className="flex-1 overflow-hidden">
        <ScrollArea className="h-full">
          <div className="p-4">
            {filteredAndSortedResponses.length === 0 ? (
              <div className="text-center py-12">
                <MessageSquare className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
                <h3 className="text-lg font-medium text-muted-foreground mb-2">
                  {searchQuery ? '–û—Ç–≤–µ—Ç—ã –Ω–µ –Ω–∞–π–¥–µ–Ω—ã' : '–ü–æ–∫–∞ –Ω–µ—Ç –æ—Ç–≤–µ—Ç–æ–≤'}
                </h3>
                <p className="text-sm text-muted-foreground">
                  {searchQuery 
                    ? '–ü–æ–ø—Ä–æ–±—É–π—Ç–µ –∏–∑–º–µ–Ω–∏—Ç—å –∫—Ä–∏—Ç–µ—Ä–∏–∏ –ø–æ–∏—Å–∫–∞' 
                    : '–û—Ç–≤–µ—Ç—ã –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π –ø–æ—è–≤—è—Ç—Å—è –∑–¥–µ—Å—å, –∫–æ–≥–¥–∞ –æ–Ω–∏ –Ω–∞—á–Ω—É—Ç –≤–∑–∞–∏–º–æ–¥–µ–π—Å—Ç–≤–æ–≤–∞—Ç—å —Å –±–æ—Ç–æ–º'}
                </p>
              </div>
            ) : (
              <div className="grid gap-4">
