import { Node, Connection } from '@shared/schema';

export interface ConnectionSuggestion {
  source: string;
  target: string;
  confidence: number;
  reason: string;
}

// –õ–æ–≥–∏–∫–∞ –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏—Ö —Å–æ–µ–¥–∏–Ω–µ–Ω–∏–π –Ω–∞ –æ—Å–Ω–æ–≤–µ —Ç–∏–ø–æ–≤ —É–∑–ª–æ–≤
export function generateAutoConnections(nodes: Node[], existingConnections: Connection[]): ConnectionSuggestion[] {
  const suggestions: ConnectionSuggestion[] = [];
  const existingPairs = new Set(existingConnections.map(c => `${c.source}-${c.target}`));

  // –¢–∏–ø—ã —É–∑–ª–æ–≤, –∫–æ—Ç–æ—Ä—ã–µ –¥–æ–ª–∂–Ω—ã –±—ã—Ç—å —Å–æ–µ–¥–∏–Ω–µ–Ω—ã –≤ –æ–ø—Ä–µ–¥–µ–ª–µ–Ω–Ω–æ–º –ø–æ—Ä—è–¥–∫–µ
  const flowPatterns = [
    { from: 'start', to: 'message', confidence: 0.9, reason: '–°—Ç–∞—Ä—Ç–æ–≤—ã–π —É–∑–µ–ª –æ–±—ã—á–Ω–æ –≤–µ–¥–µ—Ç –∫ —Å–æ–æ–±—â–µ–Ω–∏—é' },
    { from: 'start', to: 'keyboard', confidence: 0.8, reason: '–°—Ç–∞—Ä—Ç–æ–≤—ã–π —É–∑–µ–ª –º–æ–∂–µ—Ç –ø–æ–∫–∞–∑–∞—Ç—å –∫–ª–∞–≤–∏–∞—Ç—É—Ä—É' },
    { from: 'command', to: 'message', confidence: 0.9, reason: '–ö–æ–º–∞–Ω–¥–∞ –æ–±—ã—á–Ω–æ –æ—Ç–ø—Ä–∞–≤–ª—è–µ—Ç —Å–æ–æ–±—â–µ–Ω–∏–µ' },
    { from: 'command', to: 'keyboard', confidence: 0.8, reason: '–ö–æ–º–∞–Ω–¥–∞ –º–æ–∂–µ—Ç –ø–æ–∫–∞–∑–∞—Ç—å –∫–ª–∞–≤–∏–∞—Ç—É—Ä—É' },
    { from: 'message', to: 'keyboard', confidence: 0.7, reason: '–°–æ–æ–±—â–µ–Ω–∏–µ –º–æ–∂–µ—Ç —Å–æ–¥–µ—Ä–∂–∞—Ç—å –∫–ª–∞–≤–∏–∞—Ç—É—Ä—É' },
    { from: 'keyboard', to: 'message', confidence: 0.6, reason: '–ö–ª–∞–≤–∏–∞—Ç—É—Ä–∞ –º–æ–∂–µ—Ç –≤–µ—Å—Ç–∏ –∫ —Å–æ–æ–±—â–µ–Ω–∏—é' },
    { from: 'input', to: 'message', confidence: 0.8, reason: '–í–≤–æ–¥ –¥–∞–Ω–Ω—ã—Ö –æ–±—ã—á–Ω–æ –≤–µ–¥–µ—Ç –∫ —Å–æ–æ–±—â–µ–Ω–∏—é' },
    { from: 'condition', to: 'message', confidence: 0.7, reason: '–£—Å–ª–æ–≤–∏–µ –º–æ–∂–µ—Ç –≤–µ—Å—Ç–∏ –∫ —Å–æ–æ–±—â–µ–Ω–∏—é' },
    { from: 'photo', to: 'keyboard', confidence: 0.6, reason: '–§–æ—Ç–æ –º–æ–∂–µ—Ç —Å–æ–¥–µ—Ä–∂–∞—Ç—å –∫–ª–∞–≤–∏–∞—Ç—É—Ä—É' },
  ];

  // –ù–∞–π—Ç–∏ —É–∑–ª—ã –ø–æ —Ç–∏–ø–∞–º
  const nodesByType = nodes.reduce((acc, node) => {
    if (!acc[node.type]) acc[node.type] = [];
    acc[node.type].push(node);
    return acc;
  }, {} as Record<string, Node[]>);

  // –ì–µ–Ω–µ—Ä–∞—Ü–∏—è –ø—Ä–µ–¥–ª–æ–∂–µ–Ω–∏–π –Ω–∞ –æ—Å–Ω–æ–≤–µ —à–∞–±–ª–æ–Ω–æ–≤
  for (const pattern of flowPatterns) {
    const sourceNodes = nodesByType[pattern.from] || [];
    const targetNodes = nodesByType[pattern.to] || [];

    for (const sourceNode of sourceNodes) {
      for (const targetNode of targetNodes) {
        const pairKey = `${sourceNode.id}-${targetNode.id}`;
        if (!existingPairs.has(pairKey) && sourceNode.id !== targetNode.id) {
          // –ü—Ä–æ–≤–µ—Ä—è–µ–º —Ä–∞—Å—Å—Ç–æ—è–Ω–∏–µ –º–µ–∂–¥—É —É–∑–ª–∞–º–∏ –¥–ª—è —É–ª—É—á—à–µ–Ω–∏—è —Ä–µ–ª–µ–≤–∞–Ω—Ç–Ω–æ—Å—Ç–∏
          const distance = Math.sqrt(
            Math.pow(targetNode.position.x - sourceNode.position.x, 2) +
            Math.pow(targetNode.position.y - sourceNode.position.y, 2)
          );

          // –£–º–µ–Ω—å—à–∞–µ–º confidence –¥–ª—è —É–¥–∞–ª–µ–Ω–Ω—ã—Ö —É–∑–ª–æ–≤
          const distanceModifier = Math.max(0.5, 1 - distance / 1000);

          suggestions.push({
            source: sourceNode.id,
            target: targetNode.id,
            confidence: pattern.confidence * distanceModifier,
            reason: pattern.reason
          });
        }
      }
    }
  }

  // –ü–æ–∏—Å–∫ –∏–∑–æ–ª–∏—Ä–æ–≤–∞–Ω–Ω—ã—Ö —É–∑–ª–æ–≤
  const connectedNodes = new Set([
    ...existingConnections.map(c => c.source),
    ...existingConnections.map(c => c.target)
  ]);

  const isolatedNodes = nodes.filter(node => !connectedNodes.has(node.id));

  // –ü—Ä–µ–¥–ª–æ–∂–µ–Ω–∏—è –¥–ª—è –∏–∑–æ–ª–∏—Ä–æ–≤–∞–Ω–Ω—ã—Ö —É–∑–ª–æ–≤
  for (const isolated of isolatedNodes) {
    const nearbyNodes = nodes
      .filter(node => node.id !== isolated.id && !existingPairs.has(`${isolated.id}-${node.id}`))
      .map(node => ({
        node,
        distance: Math.sqrt(
          Math.pow(node.position.x - isolated.position.x, 2) +
          Math.pow(node.position.y - isolated.position.y, 2)
        )
      }))
      .sort((a, b) => a.distance - b.distance)
      .slice(0, 3); // –ë–µ—Ä–µ–º 3 –±–ª–∏–∂–∞–π—à–∏—Ö

    for (const { node: nearbyNode, distance } of nearbyNodes) {
      const confidence = Math.max(0.3, 0.8 - distance / 500);
      suggestions.push({
        source: isolated.id,
        target: nearbyNode.id,
        confidence,
        reason: '–ò–∑–æ–ª–∏—Ä–æ–≤–∞–Ω–Ω—ã–π —É–∑–µ–ª —Ä—è–¥–æ–º —Å –¥—Ä—É–≥–∏–º–∏'
      });
    }
  }

  // –°–æ—Ä—Ç–∏—Ä–æ–≤–∫–∞ –ø–æ —É–≤–µ—Ä–µ–Ω–Ω–æ—Å—Ç–∏
  return suggestions.sort((a, b) => b.confidence - a.confidence);
}

// –ü—Ä–æ–≤–µ—Ä–∫–∞ –≤–∞–ª–∏–¥–Ω–æ—Å—Ç–∏ —Å–æ–µ–¥–∏–Ω–µ–Ω–∏—è
export function isValidConnection(
  sourceNode: Node,
  targetNode: Node,
  existingConnections: Connection[]
): { valid: boolean; reason?: string } {
  // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ –Ω–µ —Å–æ–µ–¥–∏–Ω—è–µ–º —É–∑–µ–ª —Å–∞–º —Å —Å–æ–±–æ–π
  if (sourceNode.id === targetNode.id) {
    return { valid: false, reason: '–ù–µ–ª—å–∑—è —Å–æ–µ–¥–∏–Ω–∏—Ç—å —É–∑–µ–ª —Å–∞–º —Å —Å–æ–±–æ–π' };
  }

  // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ —Å–æ–µ–¥–∏–Ω–µ–Ω–∏–µ –Ω–µ —Å—É—â–µ—Å—Ç–≤—É–µ—Ç
  const existingConnection = existingConnections.find(
    c => c.source === sourceNode.id && c.target === targetNode.id
  );
  if (existingConnection) {
    return { valid: false, reason: '–°–æ–µ–¥–∏–Ω–µ–Ω–∏–µ —É–∂–µ —Å—É—â–µ—Å—Ç–≤—É–µ—Ç' };
  }

  // –ü—Ä–æ–≤–µ—Ä—è–µ–º –æ–±—Ä–∞—Ç–Ω–æ–µ —Å–æ–µ–¥–∏–Ω–µ–Ω–∏–µ (—á—Ç–æ–±—ã –∏–∑–±–µ–∂–∞—Ç—å —Ü–∏–∫–ª–æ–≤)
  const reverseConnection = existingConnections.find(
    c => c.source === targetNode.id && c.target === sourceNode.id
  );
  if (reverseConnection) {
    return { valid: false, reason: '–û–±—Ä–∞—Ç–Ω–æ–µ —Å–æ–µ–¥–∏–Ω–µ–Ω–∏–µ —É–∂–µ —Å—É—â–µ—Å—Ç–≤—É–µ—Ç' };
  }

  return { valid: true };
}

// –ü–æ–ª—É—á–µ–Ω–∏–µ —Ä–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–π –¥–ª—è –∫–æ–Ω–∫—Ä–µ—Ç–Ω–æ–≥–æ —É–∑–ª–∞
export function getNodeConnectionSuggestions(
  node: Node,
  allNodes: Node[],
  existingConnections: Connection[]
): ConnectionSuggestion[] {
  const allSuggestions = generateAutoConnections(allNodes, existingConnections);
  return allSuggestions.filter(s => s.source === node.id || s.target === node.id);
}

