          <TabsTrigger value="settings">–ù–∞—Å—Ç—Ä–æ–π–∫–∏</TabsTrigger>
        </TabsList>

        <TabsContent value="connections" className="flex-1 mt-4">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Network className="h-5 w-5" />
                –£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ —Å–≤—è–∑—è–º–∏
              </CardTitle>
              <CardDescription>
                –í—Å–µ–≥–æ —Å–≤—è–∑–µ–π: {connectionStats.total} | –° –∫–Ω–æ–ø–∫–∞–º–∏: {connectionStats.withButtons} | –ü—Ä—è–º—ã–µ: {connectionStats.direct}
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {/* –§–∏–ª—å—Ç—Ä—ã */}
                <div className="flex flex-wrap gap-2">
                  <Select value={filterType} onValueChange={(value: typeof filterType) => setFilterType(value)}>
                    <SelectTrigger className="w-32">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">–í—Å–µ</SelectItem>
                      <SelectItem value="button">–° –∫–Ω–æ–ø–∫–∞–º–∏</SelectItem>
                      <SelectItem value="direct">–ü—Ä—è–º—ã–µ</SelectItem>
                    </SelectContent>
                  </Select>

                  <Input
                    placeholder="–ü–æ–∏—Å–∫..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="flex-1 min-w-0"
                  />
                </div>

                {/* –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ */}
                <div className="grid grid-cols-2 gap-2">
                  <div className="flex items-center gap-2 p-2 bg-green-50 dark:bg-green-900/20 rounded">
                    <CheckCircle className="h-4 w-4 text-green-600 dark:text-green-400" />
                    <span className="text-sm">–° –∫–Ω–æ–ø–∫–∞–º–∏: {connectionStats.withButtons}</span>
