                        <span className="text-sm">–ü–æ–≤—Ä–µ–∂–¥–µ–Ω–Ω—ã–µ</span>
                      </div>
                      <p className="text-2xl font-bold text-red-600">
                        {connectionStats.orphaned}
                      </p>
                    </div>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

