              <div className="grid grid-cols-2 gap-2">
                {currentReplyKeyboard.map((button, index) => (
                  <button
                    key={index}
                    onClick={() => handleButtonClick(button.text, button.target, button.action)}
                    className="bg-white border border-gray-300 text-gray-700 text-sm font-medium py-2 px-3 rounded-lg hover:bg-gray-100 transition-colors flex items-center justify-center"
                  >
                    {button.text}
                  </button>
                ))}
              </div>
            </div>
          </div>
        )}

        {/* Text Input Area */}
        <div className="p-4 border-t border-gray-200 bg-white">
          {/* Media Input Buttons */}
          {(waitingForPhoto || waitingForVideo || waitingForAudio || waitingForDocument) && (
            <div className="mb-3">
              <p className="text-xs text-gray-600 mb-2">–ë–æ—Ç –æ–∂–∏–¥–∞–µ—Ç –≤–≤–æ–¥:</p>
              <div className="grid grid-cols-2 gap-2">
                {waitingForPhoto && (
                  <Button
                    onClick={() => handleMediaInput('photo')}
                    variant="outline"
                    className="flex items-center justify-center gap-2"
                    data-testid="button-send-photo"
                  >
                    üì∑ –û—Ç–ø—Ä–∞–≤–∏—Ç—å —Ñ–æ—Ç–æ
                  </Button>
                )}
                {waitingForVideo && (
                  <Button
                    onClick={() => handleMediaInput('video')}
                    variant="outline"
                    className="flex items-center justify-center gap-2"
                    data-testid="button-send-video"
                  >
                    üé• –û—Ç–ø—Ä–∞–≤–∏—Ç—å –≤–∏–¥–µ–æ
                  </Button>
                )}
                {waitingForAudio && (
                  <Button
                    onClick={() => handleMediaInput('audio')}
                    variant="outline"
                    className="flex items-center justify-center gap-2"
                    data-testid="button-send-audio"
                  >
                    üéµ –û—Ç–ø—Ä–∞–≤–∏—Ç—å –∞—É–¥–∏–æ
                  </Button>
                )}
                {waitingForDocument && (
                  <Button
                    onClick={() => handleMediaInput('document')}
                    variant="outline"
                    className="flex items-center justify-center gap-2"
                    data-testid="button-send-document"
                  >
                    üìÑ –û—Ç–ø—Ä–∞–≤–∏—Ç—å –¥–æ–∫—É–º–µ–Ω—Ç
                  </Button>
                )}
              </div>
            </div>
          )}

          {/* Text Input */}
          <div className="flex items-center space-x-2 mb-3">
            <Input
              ref={inputRef}
              value={textInput}
              onChange={(e) => setTextInput(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder={waitingForInput ? "–ë–æ—Ç –æ–∂–∏–¥–∞–µ—Ç –≤–≤–æ–¥..." : "–í–≤–µ–¥–∏—Ç–µ —Å–æ–æ–±—â–µ–Ω–∏–µ..."}
              className="flex-1"
              disabled={!messageHistory.length}
              data-testid="input-message"
            />
            <Button
              onClick={handleSendText}
              disabled={!textInput.trim() || !messageHistory.length}
              size="sm"
              data-testid="button-send-message"
            >
              <i className="fas fa-paper-plane"></i>
            </Button>
          </div>

          {/* Controls */}
          <div className="flex items-center space-x-2">
            <button
              onClick={reset}
              className="px-3 py-2 text-sm text-gray-600 hover:text-gray-800 transition-colors"
            >
              –°–±—Ä–æ—Å–∏—Ç—å
            </button>
            <div className="flex-1"></div>
            <span className="text-xs text-gray-500">
              {messageHistory.length} —Å–æ–æ–±—â–µ–Ω–∏–π
            </span>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

