  const html = document.documentElement;
  console.log('   HTML classes:', html.className);
  console.log('   Color scheme:', getComputedStyle(html).colorScheme);

  // –ü—Ä–æ–≤–µ—Ä–∫–∞ 2: –ù–∞–ª–∏—á–∏–µ –ø–µ—Ä–µ–∫–ª—é—á–∞—Ç–µ–ª—è —Ç–µ–º—ã
