import { Button } from '@/components/ui/button';
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from '@/components/ui/tooltip';
import { 
  PanelTop, 
  PanelLeft, 
  LayoutGrid,
  PanelRight,
  EyeOff,
  Eye
} from 'lucide-react';

interface LayoutButtonsProps {
  onToggleCanvas?: () => void;
  onToggleHeader?: () => void;
  onToggleSidebar?: () => void;
  onToggleProperties?: () => void;
  onShowFullLayout?: () => void;
  canvasVisible?: boolean;
  headerVisible?: boolean;
  sidebarVisible?: boolean;
  propertiesVisible?: boolean;
  className?: string;
}

export function LayoutButtons({ 
  onToggleCanvas, 
  onToggleHeader, 
  onToggleSidebar,
  onToggleProperties,
  onShowFullLayout,
  canvasVisible = true,
  headerVisible = true,
  sidebarVisible = true,
  propertiesVisible = true,
  className = "" 
}: LayoutButtonsProps) {
  return (
    <TooltipProvider>
      <div className={`flex items-center gap-1 p-2 bg-background border border-border rounded-lg ${className}`}>
        {/* –ö–Ω–æ–ø–∫–∞ –ø–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏—è —à–∞–ø–∫–∏ - –≤—Å–µ–≥–¥–∞ –≤–∏–¥–Ω–∞ */}
        {onToggleHeader && (
          <Tooltip>
            <TooltipTrigger asChild>
              <Button
                variant="ghost"
                size="sm"
                onClick={onToggleHeader}
                className={`h-8 w-8 p-0 rounded-md ${
                  headerVisible 
                    ? 'bg-gray-600 hover:bg-gray-700 text-white' 
                    : 'bg-gray-300 hover:bg-gray-400 text-gray-600'
                }`}
              >
                <PanelTop className="h-4 w-4" />
              </Button>
            </TooltipTrigger>
            <TooltipContent>
              <p>{headerVisible ? '–°–∫—Ä—ã—Ç—å —à–∞–ø–∫—É' : '–ü–æ–∫–∞–∑–∞—Ç—å —à–∞–ø–∫—É'}</p>
            </TooltipContent>
          </Tooltip>
        )}

        {/* –ö–Ω–æ–ø–∫–∞ –ø–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏—è –±–æ–∫–æ–≤–æ–π –ø–∞–Ω–µ–ª–∏ - –≤—Å–µ–≥–¥–∞ –≤–∏–¥–Ω–∞ */}
        {onToggleSidebar && (
          <Tooltip>
            <TooltipTrigger asChild>
              <Button
                variant="ghost"
                size="sm"
                onClick={onToggleSidebar}
                className={`h-8 w-8 p-0 rounded-md ${
                  sidebarVisible 
                    ? 'bg-gray-600 hover:bg-gray-700 text-white' 
                    : 'bg-gray-300 hover:bg-gray-400 text-gray-600'
                }`}
              >
                <PanelLeft className="h-4 w-4" />
              </Button>
            </TooltipTrigger>
            <TooltipContent>
              <p>{sidebarVisible ? '–°–∫—Ä—ã—Ç—å –±–æ–∫–æ–≤—É—é –ø–∞–Ω–µ–ª—å' : '–ü–æ–∫–∞–∑–∞—Ç—å –±–æ–∫–æ–≤—É—é –ø–∞–Ω–µ–ª—å'}</p>
            </TooltipContent>
          </Tooltip>
        )}

        {/* –ö–Ω–æ–ø–∫–∞ –ø–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏—è —Ö–æ–ª—Å—Ç–∞ - –≤—Å–µ–≥–¥–∞ –≤–∏–¥–Ω–∞ */}
        {onToggleCanvas && (
          <Tooltip>
            <TooltipTrigger asChild>
              <Button
                variant="ghost"
                size="sm"
                onClick={onToggleCanvas}
                className={`h-8 w-8 p-0 rounded-md ${
                  canvasVisible 
                    ? 'bg-gray-600 hover:bg-gray-700 text-white' 
                    : 'bg-gray-300 hover:bg-gray-400 text-gray-600'
                }`}
              >
                <LayoutGrid className="h-4 w-4" />
              </Button>
            </TooltipTrigger>
            <TooltipContent>
              <p>{canvasVisible ? '–°–∫—Ä—ã—Ç—å —Ö–æ–ª—Å—Ç' : '–ü–æ–∫–∞–∑–∞—Ç—å —Ö–æ–ª—Å—Ç'}</p>
            </TooltipContent>
          </Tooltip>
        )}

        {/* –ö–Ω–æ–ø–∫–∞ –ø–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏—è –ø–∞–Ω–µ–ª–∏ —Å–≤–æ–π—Å—Ç–≤ - –≤—Å–µ–≥–¥–∞ –≤–∏–¥–Ω–∞ */}
        {onToggleProperties && (
          <Tooltip>
            <TooltipTrigger asChild>
              <Button
                variant="ghost"
                size="sm"
                onClick={onToggleProperties}
                className={`h-8 w-8 p-0 rounded-md ${
                  propertiesVisible 
                    ? 'bg-gray-600 hover:bg-gray-700 text-white' 
                    : 'bg-gray-300 hover:bg-gray-400 text-gray-600'
                }`}
              >
                <PanelRight className="h-4 w-4" />
              </Button>
            </TooltipTrigger>
            <TooltipContent>
              <p>{propertiesVisible ? '–°–∫—Ä—ã—Ç—å –ø–∞–Ω–µ–ª—å —Å–≤–æ–π—Å—Ç–≤' : '–ü–æ–∫–∞–∑–∞—Ç—å –ø–∞–Ω–µ–ª—å —Å–≤–æ–π—Å—Ç–≤'}</p>
            </TooltipContent>
          </Tooltip>
        )}

        {/* –î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–µ –∫–Ω–æ–ø–∫–∏ —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è */}
        {(() => {
          const visiblePanels = [headerVisible, sidebarVisible, canvasVisible, propertiesVisible].filter(Boolean).length;
          const hiddenPanels = 4 - visiblePanels;

          // –ï—Å–ª–∏ –≤–∏–¥–Ω–∞ —Ç–æ–ª—å–∫–æ –æ–¥–Ω–∞ –ø–∞–Ω–µ–ª—å - –ø–æ–∫–∞–∑—ã–≤–∞–µ–º –∫–Ω–æ–ø–∫—É "–°–∫—Ä—ã—Ç—å —Ç–µ–∫—É—â—É—é"
          if (visiblePanels === 1) {
            let currentVisibleAction;
            let currentVisibleIcon;
            let currentVisibleTooltip;

            if (sidebarVisible) {
              currentVisibleAction = onToggleSidebar;
              currentVisibleIcon = <EyeOff className="h-4 w-4" />;
              currentVisibleTooltip = "–°–∫—Ä—ã—Ç—å –±–æ–∫–æ–≤—É—é –ø–∞–Ω–µ–ª—å";
            } else if (headerVisible) {
              currentVisibleAction = onToggleHeader;
              currentVisibleIcon = <EyeOff className="h-4 w-4" />;
              currentVisibleTooltip = "–°–∫—Ä—ã—Ç—å —à–∞–ø–∫—É";
            } else if (canvasVisible) {
              currentVisibleAction = onToggleCanvas;
              currentVisibleIcon = <EyeOff className="h-4 w-4" />;
              currentVisibleTooltip = "–°–∫—Ä—ã—Ç—å —Ö–æ–ª—Å—Ç";
            } else if (propertiesVisible) {
              currentVisibleAction = onToggleProperties;
              currentVisibleIcon = <EyeOff className="h-4 w-4" />;
              currentVisibleTooltip = "–°–∫—Ä—ã—Ç—å –ø–∞–Ω–µ–ª—å —Å–≤–æ–π—Å—Ç–≤";
            }

            return currentVisibleAction && (
              <Tooltip>
                <TooltipTrigger asChild>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={currentVisibleAction}
                    className="h-8 w-8 p-0 bg-orange-600 hover:bg-orange-700 text-white rounded-md"
                  >
                    {currentVisibleIcon}
                  </Button>
                </TooltipTrigger>
                <TooltipContent>
                  <p>{currentVisibleTooltip}</p>
                </TooltipContent>
              </Tooltip>
            );
          }

          // –ï—Å–ª–∏ —Å–∫—Ä—ã—Ç–æ –Ω–µ—Å–∫–æ–ª—å–∫–æ –ø–∞–Ω–µ–ª–µ–π - –ø–æ–∫–∞–∑—ã–≤–∞–µ–º –∫–Ω–æ–ø–∫—É "–ü–æ–∫–∞–∑–∞—Ç—å –≤—Å—ë"
          if (hiddenPanels > 1 && onShowFullLayout) {
            return (
              <Tooltip>
                <TooltipTrigger asChild>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={onShowFullLayout}
                    className="h-8 w-8 p-0 bg-blue-600 hover:bg-blue-700 text-white rounded-md"
                  >
                    <Eye className="h-4 w-4" />
                  </Button>
                </TooltipTrigger>
                <TooltipContent>
                  <p>–ü–æ–∫–∞–∑–∞—Ç—å –≤—Å–µ –ø–∞–Ω–µ–ª–∏</p>
                </TooltipContent>
              </Tooltip>
            );
          }

          return null;
        })()}
      </div>
    </TooltipProvider>
  );
}

