import { QueryClient, QueryFunction } from "@tanstack/react-query";

async function throwIfResNotOk(res: Response) {
  if (!res.ok) {
    let errorData;
    try {
      // –ü—ã—Ç–∞–µ–º—Å—è —Ä–∞—Å–ø–∞—Ä—Å–∏—Ç—å JSON –æ—Ç–≤–µ—Ç —Å –æ—à–∏–±–∫–æ–π
      errorData = await res.json();
    } catch (parseError) {
      // –ï—Å–ª–∏ –Ω–µ —É–¥–∞–ª–æ—Å—å —Ä–∞—Å–ø–∞—Ä—Å–∏—Ç—å JSON, –∏—Å–ø–æ–ª—å–∑—É–µ–º statusText
      throw new Error(`${res.status}: ${res.statusText}`);
    }

    // –°–æ–∑–¥–∞–µ–º –æ—à–∏–±–∫—É —Å —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ–º –≤—Å–µ—Ö —Å–≤–æ–π—Å—Ç–≤ (–≤–∫–ª—é—á–∞—è requiresClientApi)
    const error = new Error(errorData.message || `${res.status}: ${res.statusText}`);
    // –î–æ–±–∞–≤–ª—è–µ–º –≤—Å–µ —Å–≤–æ–π—Å—Ç–≤–∞ –∏–∑ –æ—Ç–≤–µ—Ç–∞ –∫ –æ—à–∏–±–∫–µ
    Object.assign(error, errorData);
    throw error;
  }
}

export async function apiRequest(
  method: string,
  url: string,
  data?: unknown | undefined,
): Promise<any> {
  const res = await fetch(url, {
    method,
    headers: data ? { "Content-Type": "application/json" } : {},
    body: data ? JSON.stringify(data) : undefined,
    credentials: "include",
  });

  await throwIfResNotOk(res);
  return await res.json();
}

type UnauthorizedBehavior = "returnNull" | "throw";
export const getQueryFn: <T>(options: {
  on401: UnauthorizedBehavior;
}) => QueryFunction<T> =
  ({ on401: unauthorizedBehavior }) =>
  async ({ queryKey }) => {
    const res = await fetch(queryKey[0] as string, {
      credentials: "include",
    });

    if (unauthorizedBehavior === "returnNull" && res.status === 401) {
      return null;
    }

    await throwIfResNotOk(res);
    return await res.json();
  };

export const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      queryFn: async ({ queryKey }) => {
        const queryFn = getQueryFn({ on401: "throw" });
        return queryFn({ queryKey } as any);
      },
      refetchInterval: false,
      refetchOnWindowFocus: false,
      staleTime: (query) => {
        // –®–∞–±–ª–æ–Ω—ã –∏ –ø—Ä–æ–µ–∫—Ç—ã –∏–º–µ—é—Ç –∫–æ—Ä–æ—á–µ –≤—Ä–µ–º—è –∂–∏–∑–Ω–∏ –∫–µ—à–∞ –¥–ª—è –¥–∏–Ω–∞–º–∏—á–Ω–æ—Å—Ç–∏
        const key = query.queryKey[0];
        if (typeof key === 'string' && (key.includes('/api/templates') || key.includes('/api/projects'))) {
          return 1 * 60 * 1000; // 1 –º–∏–Ω—É—Ç–∞ –¥–ª—è —à–∞–±–ª–æ–Ω–æ–≤ –∏ –ø—Ä–æ–µ–∫—Ç–æ–≤
        }
        return 10 * 60 * 1000; // 10 –º–∏–Ω—É—Ç –¥–ª—è –æ—Å—Ç–∞–ª—å–Ω–æ–≥–æ
      },
      gcTime: 30 * 60 * 1000, // 30 –º–∏–Ω—É—Ç –≤ garbage collection
      retry: (failureCount, error: any) => {
        if (error?.message?.includes('503') || error?.message?.includes('–∑–∞–≥—Ä—É–∂–∞–µ—Ç—Å—è')) {
          return failureCount < 5;
        }
        return failureCount < 1;
      },
      retryDelay: (attemptIndex, error: any) => {
        if (error?.message?.includes('503') || error?.message?.includes('–∑–∞–≥—Ä—É–∂–∞–µ—Ç—Å—è')) {
          return Math.min(1000 * Math.pow(1.5, attemptIndex), 3000);
        }
        return Math.min(1000 * Math.pow(2, attemptIndex), 30000);
      },
    },
    mutations: {
      retry: false,
    },
  },
});

