  const [redoStack, setRedoStack] = useState<string[]>([]);
  const [savedTemplates, setSavedTemplates] = useState<{name: string, content: string}[]>([]);
  const [wordCount, setWordCount] = useState(0);
  const [charCount, setCharCount] = useState(0);
