  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è —Ä–µ–Ω–¥–µ—Ä–∏–Ω–≥–∞ –∂–∏–≤–æ–≥–æ –ø—Ä–µ–¥–ø—Ä–æ—Å–º–æ—Ç—Ä–∞ —Å —Ä–µ–∞–ª—å–Ω—ã–º–∏ —ç—Ñ—Ñ–µ–∫—Ç–∞–º–∏
  const renderLivePreview = (text: string) => {
    let processedText = text;

    if (enableMarkdown) {
      // –û–±—Ä–∞–±–æ—Ç–∫–∞ Markdown —Ñ–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏—è
      processedText = processedText
        .replace(/\*\*(.*?)\*\*/g, '<strong class="live-preview-bold">$1</strong>') // –ñ–∏—Ä–Ω—ã–π
        .replace(/_(.*?)_/g, '<em class="live-preview-italic">$1</em>') // –ö—É—Ä—Å–∏–≤
        .replace(/__(.*?)__/g, '<u class="live-preview-underline">$1</u>') // –ü–æ–¥—á–µ—Ä–∫–Ω—É—Ç—ã–π
        .replace(/~(.*?)~/g, '<s class="live-preview-strikethrough">$1</s>') // –ó–∞—á–µ—Ä–∫–Ω—É—Ç—ã–π
        .replace(/`(.*?)`/g, '<code class="live-preview-code">$1</code>') // –ö–æ–¥
