        .replace(/^\d+\. (.*)$/gm, '<li class="live-preview-list-item-ordered">$1</li>') // –ù—É–º–µ—Ä–æ–≤–∞–Ω–Ω—ã–π —Å–ø–∏—Å–æ–∫
        .replace(/---/g, '<hr class="live-preview-separator">') // –†–∞–∑–¥–µ–ª–∏—Ç–µ–ª—å
