        {/* –ù–∏–∂–Ω—è—è –æ–±–ª–∞—Å—Ç—å */}
        {bottomElements.length > 0 && (
          <div className="flex-shrink-0 border-t border-gray-200 dark:border-gray-700">
            <div className="flex">
              {bottomElements.map(element => (
                <DraggableElement
                  key={element.id}
                  element={element}
                  isPreview={previewMode}
                  onMove={handleElementMove}
