      description: '–°–∂–∞—Ç—ã–π –∏–Ω—Ç–µ—Ä—Ñ–µ–π—Å',
      config: {
        ...DEFAULT_LAYOUT,
        compactMode: true,
        panelSizes: { sidebar: 15, properties: 20, canvas: 65 }
      }
    },
    {
      name: '–§–æ–∫—É—Å –Ω–∞ —Ö–æ–ª—Å—Ç–µ',
      description: '–ú–∞–∫—Å–∏–º—É–º –º–µ—Å—Ç–∞ –¥–ª—è —Ö–æ–ª—Å—Ç–∞',
      config: {
        ...DEFAULT_LAYOUT,
        headerPosition: 'bottom' as const,
        panelSizes: { sidebar: 12, properties: 15, canvas: 73 }
      }
    },
    {
      name: '–õ–µ–≤–æ—Å—Ç–æ—Ä–æ–Ω–Ω–∏–π',
      description: '–í—Å–µ –ø–∞–Ω–µ–ª–∏ —Å–ª–µ–≤–∞',
      config: {
        ...DEFAULT_LAYOUT,
        sidebarPosition: 'left' as const,
        propertiesPosition: 'left' as const,
        panelSizes: { sidebar: 25, properties: 25, canvas: 50 }
      }
    },
    {
      name: '–ü—Ä–∞–≤–æ—Å—Ç–æ—Ä–æ–Ω–Ω–∏–π',
      description: '–í—Å–µ –ø–∞–Ω–µ–ª–∏ —Å–ø—Ä–∞–≤–∞',
      config: {
        ...DEFAULT_LAYOUT,
        sidebarPosition: 'right' as const,
        propertiesPosition: 'right' as const,
        panelSizes: { sidebar: 25, properties: 25, canvas: 50 }
      }
    }
  ];

  if (!isVisible) {
    return (
      <Button
        variant="outline"
        size="sm"
        onClick={() => setIsVisible(true)}
