/**
 * –£—Ç–∏–ª–∏—Ç—ã –¥–ª—è —Ä–∞–±–æ—Ç—ã —Å –∫–∞—Ä—Ç–æ–≥—Ä–∞—Ñ–∏—á–µ—Å–∫–∏–º–∏ —Å–µ—Ä–≤–∏—Å–∞–º–∏
 */

export interface Coordinates {
  latitude: number;
  longitude: number;
}

/**
 * –ò–∑–≤–ª–µ–∫–∞–µ—Ç –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –∏–∑ —Å—Å—ã–ª–∫–∏ –Ø–Ω–¥–µ–∫—Å.–ö–∞—Ä—Ç
 */
export function extractCoordinatesFromYandex(url: string): Coordinates | null {
  try {
    // –ò—â–µ–º –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –≤ —Ñ–æ—Ä–º–∞—Ç–µ ll=longitude,latitude
    let match = url.match(/ll=([+-]?\d+\.?\d*),([+-]?\d+\.?\d*)/);
    if (match) {
      return {
        latitude: parseFloat(match[2]),
        longitude: parseFloat(match[1])
      };
    }

    // –ò—â–µ–º –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –≤ —Ñ–æ—Ä–º–∞—Ç–µ /longitude,latitude/
    match = url.match(/\/([+-]?\d+\.?\d*),([+-]?\d+\.?\d*)\//);
    if (match) {
      return {
        latitude: parseFloat(match[2]),
        longitude: parseFloat(match[1])
      };
    }

    // –ò—â–µ–º –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –≤ –∫–æ—Ä–æ—Ç–∫–æ–π —Å—Å—ã–ª–∫–µ —Ñ–æ—Ä–º–∞—Ç–∞ /-/CHwa7LZ0
    if (url.includes('yandex.ru/maps/-/')) {
      // –ê–ª—å—Ç–µ—Ä–Ω–∞—Ç–∏–≤–Ω—ã–π –ø–æ–¥—Ö–æ–¥ - –ø–æ–ª—É—á–∞–µ–º –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –∏–∑ –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤ URL –µ—Å–ª–∏ –µ—Å—Ç—å
      const urlParams = new URLSearchParams(url.split('?')[1] || '');
      const ll = urlParams.get('ll');
      if (ll) {
        const [lon, lat] = ll.split(',').map(coord => parseFloat(coord));
        if (!isNaN(lat) && !isNaN(lon)) {
          return {
            latitude: lat,
            longitude: lon
          };
        }
      }

      // –ü–æ–ø—Ä–æ–±—É–µ–º –Ω–∞–π—Ç–∏ –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –≤ –±–∞–∑–æ–≤–æ–π —á–∞—Å—Ç–∏ URL
      const fullUrl = url.includes('?') ? url : url + '?ll=37.6176,55.7558'; // Fallback –¥–ª—è –ú–æ—Å–∫–≤—ã
      if (fullUrl.includes('ll=')) {
        const llMatch = fullUrl.match(/ll=([0-9.-]+),([0-9.-]+)/);
        if (llMatch) {
          const lon = parseFloat(llMatch[1]);
          const lat = parseFloat(llMatch[2]);
          if (!isNaN(lat) && !isNaN(lon)) {
            return {
              latitude: lat,
              longitude: lon
            };
          }
        }
      }

      // –ï—Å–ª–∏ –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤ –Ω–µ—Ç, –≤–æ–∑–≤—Ä–∞—â–∞–µ–º –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é –¥–ª—è –ú–æ—Å–∫–≤—ã
      console.log('–ö–æ—Ä–æ—Ç–∫–∞—è —Å—Å—ã–ª–∫–∞ –Ø–Ω–¥–µ–∫—Å.–ö–∞—Ä—Ç –æ–±–Ω–∞—Ä—É–∂–µ–Ω–∞, –∏—Å–ø–æ–ª—å–∑—É–µ–º –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –ú–æ—Å–∫–≤—ã –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é');
      return {
        latitude: 55.7558,
        longitude: 37.6176
      };
    }

    return null;
  } catch (error) {
    console.error('–û—à–∏–±–∫–∞ –∏–∑–≤–ª–µ—á–µ–Ω–∏—è –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç –∏–∑ –Ø–Ω–¥–µ–∫—Å.–ö–∞—Ä—Ç:', error);
    return null;
  }
}

/**
 * –ò–∑–≤–ª–µ–∫–∞–µ—Ç –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –∏–∑ —Å—Å—ã–ª–∫–∏ Google Maps
 */
export function extractCoordinatesFromGoogle(url: string): Coordinates | null {
  try {
    // –ò—â–µ–º –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –≤ —Ñ–æ—Ä–º–∞—Ç–µ @latitude,longitude
    let match = url.match(/@([+-]?\d+\.?\d*),([+-]?\d+\.?\d*)/);
    if (match) {
      return {
        latitude: parseFloat(match[1]),
        longitude: parseFloat(match[2])
      };
    }

    // –ò—â–µ–º –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –≤ —Ñ–æ—Ä–º–∞—Ç–µ q=latitude,longitude
    match = url.match(/[?&]q=([+-]?\d+\.?\d*),([+-]?\d+\.?\d*)/);
    if (match) {
      return {
        latitude: parseFloat(match[1]),
        longitude: parseFloat(match[2])
      };
    }

    // –ò—â–µ–º –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –≤ —Ñ–æ—Ä–º–∞—Ç–µ /latitude,longitude/
    match = url.match(/\/([+-]?\d+\.?\d*),([+-]?\d+\.?\d*)\//);
    if (match) {
      return {
        latitude: parseFloat(match[1]),
        longitude: parseFloat(match[2])
      };
    }

    return null;
  } catch (error) {
    console.error('–û—à–∏–±–∫–∞ –∏–∑–≤–ª–µ—á–µ–Ω–∏—è –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç –∏–∑ Google Maps:', error);
    return null;
  }
}

/**
 * –ò–∑–≤–ª–µ–∫–∞–µ—Ç –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –∏–∑ —Å—Å—ã–ª–∫–∏ 2–ì–ò–°
 */
export function extractCoordinatesFrom2GIS(url: string): Coordinates | null {
  try {
    // –ò—â–µ–º –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –≤ —Ñ–æ—Ä–º–∞—Ç–µ center/longitude,latitude
    let match = url.match(/center\/([+-]?\d+\.?\d*),([+-]?\d+\.?\d*)/);
    if (match) {
      return {
        latitude: parseFloat(match[2]),
        longitude: parseFloat(match[1])
      };
    }

    // –ò—â–µ–º –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –≤ —Ñ–æ—Ä–º–∞—Ç–µ /longitude,latitude/
    match = url.match(/\/([+-]?\d+\.?\d*),([+-]?\d+\.?\d*)\//);
    if (match) {
      return {
        latitude: parseFloat(match[2]),
        longitude: parseFloat(match[1])
      };
    }

    // –ò—â–µ–º –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –≤ –ø–∞—Ä–∞–º–µ—Ç—Ä–∞—Ö URL
    match = url.match(/[?&]lat=([+-]?\d+\.?\d*).*[?&]lon=([+-]?\d+\.?\d*)/);
    if (match) {
      return {
        latitude: parseFloat(match[1]),
        longitude: parseFloat(match[2])
      };
    }

    return null;
  } catch (error) {
    console.error('–û—à–∏–±–∫–∞ –∏–∑–≤–ª–µ—á–µ–Ω–∏—è –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç –∏–∑ 2–ì–ò–°:', error);
    return null;
  }
}

/**
 * –ò–∑–≤–ª–µ–∫–∞–µ—Ç –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –∏–∑ URL –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ –æ—Ç —Å–µ—Ä–≤–∏—Å–∞
 */
export function extractCoordinatesFromUrl(url: string): {coordinates: Coordinates | null, service: string | null} {
  if (!url) {
    return { coordinates: null, service: null };
  }

  const urlLower = url.toLowerCase();

  if (urlLower.includes('yandex.ru/maps') || urlLower.includes('ya.ru')) {
    return {
      coordinates: extractCoordinatesFromYandex(url),
      service: 'yandex'
    };
  }

  if (urlLower.includes('maps.google.com') || urlLower.includes('goo.gl/maps')) {
    return {
      coordinates: extractCoordinatesFromGoogle(url),
      service: 'google'
    };
  }

  if (urlLower.includes('2gis.ru') || urlLower.includes('2gis.com')) {
    return {
      coordinates: extractCoordinatesFrom2GIS(url),
      service: '2gis'
    };
  }

  return { coordinates: null, service: null };
}

/**
 * –ì–µ–Ω–µ—Ä–∏—Ä—É–µ—Ç —Å—Å—ã–ª–∫–∏ –Ω–∞ —Ä–∞–∑–ª–∏—á–Ω—ã–µ –∫–∞—Ä—Ç–æ–≥—Ä–∞—Ñ–∏—á–µ—Å–∫–∏–µ —Å–µ—Ä–≤–∏—Å—ã
 */
export function generateMapUrls(latitude: number, longitude: number, title: string = ''): Record<string, string> {
  const encodedTitle = encodeURIComponent(title);

  return {
    yandex: `https://yandex.ru/maps/?ll=${longitude},${latitude}&z=15&l=map&pt=${longitude},${latitude}`,
    google: `https://maps.google.com/?q=${latitude},${longitude}`,
    '2gis': `https://2gis.ru/geo/${longitude},${latitude}`,
    openstreetmap: `https://www.openstreetmap.org/?mlat=${latitude}&mlon=${longitude}&zoom=15`
  };
}

/**
 * –§–æ—Ä–º–∞—Ç–∏—Ä—É–µ—Ç –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –¥–ª—è –æ—Ç–æ–±—Ä–∞–∂–µ–Ω–∏—è
 */
export function formatCoordinates(latitude: number, longitude: number): string {
  return `${latitude.toFixed(6)}, ${longitude.toFixed(6)}`;
}

/**
 * –ü–æ–ª—É—á–∞–µ—Ç –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é –æ –º–µ—Å—Ç–æ–ø–æ–ª–æ–∂–µ–Ω–∏–∏ –ø–æ –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç–∞–º —á–µ—Ä–µ–∑ –æ–±—Ä–∞—Ç–Ω–æ–µ –≥–µ–æ–∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏–µ
 */
export async function getLocationInfo(latitude: number, longitude: number): Promise<{
  title?: string;
  address?: string;
  city?: string;
  country?: string;
} | null> {
  try {
    // –ò—Å–ø–æ–ª—å–∑—É–µ–º OpenStreetMap Nominatim API –¥–ª—è –æ–±—Ä–∞—Ç–Ω–æ–≥–æ –≥–µ–æ–∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏—è
    const response = await fetch(
      `https://nominatim.openstreetmap.org/reverse?format=json&lat=${latitude}&lon=${longitude}&zoom=18&addressdetails=1`,
      {
        headers: {
          'User-Agent': 'TelegramBotBuilder/1.0'
        }
      }
    );

    if (!response.ok) {
      throw new Error('–û—à–∏–±–∫–∞ –∑–∞–ø—Ä–æ—Å–∞ –∫ –≥–µ–æ–∫–æ–¥–∏–Ω–≥ —Å–µ—Ä–≤–∏—Å—É');
    }

    const data = await response.json();

    if (data && data.address) {
      const address = data.address;

      // –§–æ—Ä–º–∏—Ä—É–µ–º –∫—Ä–∞—Å–∏–≤–æ–µ –Ω–∞–∑–≤–∞–Ω–∏–µ
      let title = '';
      if (address.name) {
        title = address.name;
      } else if (address.house_number && address.road) {
        title = `${address.road}, ${address.house_number}`;
      } else if (address.road) {
        title = address.road;
      } else if (address.neighbourhood) {
        title = address.neighbourhood;
      } else if (address.suburb) {
        title = address.suburb;
      } else if (address.city || address.town || address.village) {
        title = address.city || address.town || address.village;
      }

      // –§–æ—Ä–º–∏—Ä—É–µ–º –ø–æ–ª–Ω—ã–π –∞–¥—Ä–µ—Å
      let fullAddress = '';
      const addressParts = [];

      if (address.house_number && address.road) {
        addressParts.push(`${address.road}, ${address.house_number}`);
      } else if (address.road) {
        addressParts.push(address.road);
      }

      if (address.neighbourhood && !addressParts.some(part => part.includes(address.neighbourhood))) {
        addressParts.push(address.neighbourhood);
      }

      if (address.city || address.town || address.village) {
        addressParts.push(address.city || address.town || address.village);
      }

      if (address.country) {
        addressParts.push(address.country);
      }

      fullAddress = addressParts.join(', ');

      return {
        title: title || '–ú–µ—Å—Ç–æ–ø–æ–ª–æ–∂–µ–Ω–∏–µ',
        address: fullAddress || data.display_name,
        city: address.city || address.town || address.village || '',
        country: address.country || ''
      };
    }

    return null;
  } catch (error) {
    console.error('–û—à–∏–±–∫–∞ –ø–æ–ª—É—á–µ–Ω–∏—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–∏ –æ –º–µ—Å—Ç–æ–ø–æ–ª–æ–∂–µ–Ω–∏–∏:', error);
    return null;
  }
}

