  };

  // –û–ø—Ä–µ–¥–µ–ª—è–µ–º –º–æ–±–∏–ª—å–Ω–æ–µ —É—Å—Ç—Ä–æ–π—Å—Ç–≤–æ
  const isMobile = useIsMobile();

  // –û–ø—Ä–µ–¥–µ–ª—è–µ–º –æ—Ä–∏–µ–Ω—Ç–∞—Ü–∏—é –∑–∞–≥–æ–ª–æ–≤–∫–∞
  const isVertical = config.headerPosition === 'left' || config.headerPosition === 'right';
