      inset 0 1px 0 hsl(var(--primary) / 0.1),
      0 0 0 1px hsl(var(--primary) / 0.1);
  }
}

@keyframes border-glow {
  0% {
    opacity: 0.3;
    transform: scale(1);
  }
  100% {
    opacity: 0.6;
    transform: scale(1.02);
  }
}

@keyframes drop-hint-hover {
  0% {
    box-shadow: 
      0 32px 40px -12px hsl(var(--background) / 0.25),
