/* Grid Background Zoom Effects */
.canvas-grid-zoomed {
  background-attachment: fixed;
  background-repeat: repeat;
}

.canvas-grid-zoomed::before {
  content: '';
  position: absolute;
  inset: 0;
  background: radial-gradient(circle at 50% 50%, 
    hsl(var(--primary) / 0.03) 0%, 
    transparent 50%
  );
  pointer-events: none;
  opacity: 0;
  transition: opacity 0.3s ease;
}

.canvas-grid-zoomed:hover::before {
  opacity: 1;
}

/* Minimap Styles */
.canvas-minimap {
  position: absolute;
  bottom: 20px;
  right: 20px;
  width: 200px;
  height: 150px;
  background: hsl(var(--card) / 0.9);
  border: 1px solid hsl(var(--border));
  border-radius: 8px;
  backdrop-filter: blur(8px);
  overflow: hidden;
  transition: all 0.3s ease;
}

.canvas-minimap:hover {
  transform: scale(1.1);
  box-shadow: 0 8px 24px -4px rgba(0, 0, 0, 0.15);
}

.canvas-minimap-viewport {
  position: absolute;
  border: 2px solid hsl(var(--primary));
  background: hsl(var(--primary) / 0.1);
  cursor: move;
  transition: all 0.2s ease;
}

.canvas-minimap-viewport:hover {
  background: hsl(var(--primary) / 0.2);
  border-color: hsl(var(--primary) / 0.8);
}

@keyframes dash-border {
  to {
    stroke-dashoffset: 20;
  }
}

.dark .canvas-selection {
  border-color: hsl(var(--primary) / 0.8);
  background: hsl(var(--primary) / 0.15);
  box-shadow: 0 0 10px hsl(var(--primary) / 0.3);
}

