/* Touch styles –¥–ª—è –º–æ–±–∏–ª—å–Ω—ã—Ö —É—Å—Ç—Ä–æ–π—Å—Ç–≤ */
@media (pointer: coarse) {
  .canvas-node {
    touch-action: none;
  }

  .component-item {
    touch-action: none;
    user-select: none;
  }

  /* Touch drag feedback */
  .component-item:active {
    transform: scale(0.95);
    opacity: 0.8;
  }

  /* Prevent text selection during touch drag */
  .components-sidebar * {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
  }

  /* –£–ª—É—á—à–µ–Ω–Ω–æ–µ –ø–µ—Ä–µ—Ç–∞—Å–∫–∏–≤–∞–Ω–∏–µ –Ω–∞ touch —É—Å—Ç—Ä–æ–π—Å—Ç–≤–∞—Ö */
  [draggable="true"] {
    -webkit-user-drag: element;
    -webkit-touch-callout: none;
    -webkit-user-select: none;
    -khtml-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
  }

  /* Touch feedback –¥–ª—è —ç–ª–µ–º–µ–Ω—Ç–æ–≤ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–æ–≤ */
  .components-sidebar [draggable="true"]:active {
    transform: scale(0.98);
    transition: transform 0.1s ease;
  }

  /* –£–≤–µ–ª–∏—á–µ–Ω–Ω—ã–µ –æ–±–ª–∞—Å—Ç–∏ –¥–ª—è touch –≤–∑–∞–∏–º–æ–¥–µ–π—Å—Ç–≤–∏—è */
  .components-sidebar button,
  .components-sidebar [draggable="true"] {
    min-height: 44px;
    min-width: 44px;
  }
}

/* –£–ª—É—á—à–µ–Ω–Ω–∞—è –ø–æ–¥–¥–µ—Ä–∂–∫–∞ touch —Å–æ–±—ã—Ç–∏–π –¥–ª—è –º–æ–±–∏–ª—å–Ω—ã—Ö —É—Å—Ç—Ä–æ–π—Å—Ç–≤ */
.touch-action-none {
  touch-action: none;
  -webkit-touch-callout: none;
  -webkit-user-select: none;
  -khtml-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;
}

.touch-action-manipulation {
  touch-action: manipulation;
}

/* –ü—Ä–µ–¥–æ—Ç–≤—Ä–∞—â–µ–Ω–∏–µ –≤—ã–¥–µ–ª–µ–Ω–∏—è —Ç–µ–∫—Å—Ç–∞ –ø—Ä–∏ drag and drop */
.no-select {
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;
  -webkit-touch-callout: none;
  -webkit-tap-highlight-color: transparent;
}

/* –î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–µ —Å—Ç–∏–ª–∏ –¥–ª—è touch drag and drop */
.touch-dragging {
  opacity: 0.7;
  transform: scale(0.95);
  transition: all 0.2s ease;
  pointer-events: none;
}

/* –ú–æ–±–∏–ª—å–Ω—ã–µ –æ–ø—Ç–∏–º–∏–∑–∞—Ü–∏–∏ */
@media (pointer: coarse) {
  .component-item {
    min-height: 44px; /* –ú–∏–Ω–∏–º–∞–ª—å–Ω—ã–π —Ä–∞–∑–º–µ—Ä –¥–ª—è —É–¥–æ–±–Ω–æ–≥–æ touch –≤–∑–∞–∏–º–æ–¥–µ–π—Å—Ç–≤–∏—è */
    padding: 12px 16px;
  }

  .canvas-container {
