    const target = e.target as HTMLElement;
    const isOnNode = target.closest('[data-canvas-node]');

    // –ï—Å–ª–∏ –∫–∞—Å–∞–Ω–∏–µ –Ω–∞ —É–∑–ª–µ –∏–ª–∏ —É–∂–µ –ø–µ—Ä–µ—Ç–∞—Å–∫–∏–≤–∞–µ—Ç—Å—è —É–∑–µ–ª, –Ω–µ –Ω–∞—á–∏–Ω–∞–µ–º –ø–∞–Ω–æ—Ä–∞–º–∏—Ä–æ–≤–∞–Ω–∏–µ —Ö–æ–ª—Å—Ç–∞
