import React, { useState, useRef, useCallback } from 'react';
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { useUploadMedia } from "@/hooks/use-media";
import { Camera, X, RotateCcw, Zap, Loader2 } from "lucide-react";

interface CameraCaptureProps {
  projectId: number;
  onCapture?: (file: File) => void;
  onClose?: () => void;
  isOpen: boolean;
}

export function CameraCapture({ projectId, onCapture, onClose, isOpen }: CameraCaptureProps) {
  const { toast } = useToast();
  const uploadMutation = useUploadMedia(projectId);
  const videoRef = useRef<HTMLVideoElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const streamRef = useRef<MediaStream | null>(null);

  const [isStreaming, setIsStreaming] = useState(false);
  const [capturedImage, setCapturedImage] = useState<string | null>(null);
  const [facing, setFacing] = useState<'user' | 'environment'>('environment');

  const startCamera = useCallback(async () => {
    try {
      // –û—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –ø—Ä–µ–¥—ã–¥—É—â–∏–π –ø–æ—Ç–æ–∫, –µ—Å–ª–∏ –æ–Ω –µ—Å—Ç—å
      if (streamRef.current) {
        streamRef.current.getTracks().forEach(track => track.stop());
      }

      const stream = await navigator.mediaDevices.getUserMedia({
        video: {
          facingMode: facing,
          width: { ideal: 1280 },
          height: { ideal: 720 }
        },
        audio: false
      });

      streamRef.current = stream;
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        videoRef.current.play();
        setIsStreaming(true);
      }
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –¥–æ—Å—Ç—É–ø–∞ –∫ –∫–∞–º–µ—Ä–µ:', error);
      toast({
        title: "–û—à–∏–±–∫–∞ –∫–∞–º–µ—Ä—ã",
        description: "–ù–µ —É–¥–∞–ª–æ—Å—å –ø–æ–ª—É—á–∏—Ç—å –¥–æ—Å—Ç—É–ø –∫ –∫–∞–º–µ—Ä–µ. –ü—Ä–æ–≤–µ—Ä—å—Ç–µ —Ä–∞–∑—Ä–µ—à–µ–Ω–∏—è.",
        variant: "destructive",
      });
    }
  }, [facing, toast]);

  const stopCamera = useCallback(() => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
    }
    setIsStreaming(false);
    setCapturedImage(null);
  }, []);

  const capturePhoto = useCallback(() => {
    if (videoRef.current && canvasRef.current) {
      const video = videoRef.current;
      const canvas = canvasRef.current;
      const context = canvas.getContext('2d');

      if (context) {
        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;
        context.drawImage(video, 0, 0, canvas.width, canvas.height);

        const dataUrl = canvas.toDataURL('image/jpeg', 0.8);
        setCapturedImage(dataUrl);
      }
    }
  }, []);

  const retakePhoto = useCallback(() => {
    setCapturedImage(null);
  }, []);

  const switchCamera = useCallback(() => {
    setFacing(prev => prev === 'user' ? 'environment' : 'user');
    setCapturedImage(null);
    if (isStreaming) {
      startCamera();
    }
  }, [isStreaming, startCamera]);

  const savePhoto = useCallback(async () => {
    if (capturedImage && canvasRef.current) {
      try {
        // –ö–æ–Ω–≤–µ—Ä—Ç–∏—Ä—É–µ–º canvas –≤ Blob
        canvasRef.current.toBlob(async (blob) => {
          if (blob) {
            const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
            const file = new File([blob], `camera-photo-${timestamp}.jpg`, {
              type: 'image/jpeg',
              lastModified: Date.now()
            });

            if (onCapture) {
              onCapture(file);
            } else {
              // –ó–∞–≥—Ä—É–∂–∞–µ–º —Ñ–∞–π–ª –Ω–∞–ø—Ä—è–º—É—é
              uploadMutation.mutate({
                file,
                description: '–§–æ—Ç–æ —Å –∫–∞–º–µ—Ä—ã',
                tags: ['–∫–∞–º–µ—Ä–∞', '—Ñ–æ—Ç–æ']
              }, {
                onSuccess: () => {
                  toast({
                    title: "–§–æ—Ç–æ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–æ",
                    description: "–§–æ—Ç–æ —É—Å–ø–µ—à–Ω–æ –∑–∞–≥—Ä—É–∂–µ–Ω–æ –≤ –±–∏–±–ª–∏–æ—Ç–µ–∫—É",
                  });
                  handleClose();
                },
                onError: (error) => {
                  toast({
                    title: "–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏",
                    description: error.message,
                    variant: "destructive",
                  });
                }
              });
            }
          }
        }, 'image/jpeg', 0.8);
      } catch (error) {
        console.error('–û—à–∏–±–∫–∞ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è —Ñ–æ—Ç–æ:', error);
        toast({
          title: "–û—à–∏–±–∫–∞",
          description: "–ù–µ —É–¥–∞–ª–æ—Å—å —Å–æ—Ö—Ä–∞–Ω–∏—Ç—å —Ñ–æ—Ç–æ",
          variant: "destructive",
        });
      }
    }
  }, [capturedImage, onCapture, uploadMutation, toast]);

  const handleClose = useCallback(() => {
    stopCamera();
    if (onClose) {
      onClose();
    }
  }, [stopCamera, onClose]);

  // –≠—Ñ—Ñ–µ–∫—Ç –¥–ª—è –∑–∞–ø—É—Å–∫–∞ –∫–∞–º–µ—Ä—ã –ø—Ä–∏ –æ—Ç–∫—Ä—ã—Ç–∏–∏ –¥–∏–∞–ª–æ–≥–∞
  React.useEffect(() => {
    if (isOpen && !isStreaming && !capturedImage) {
      startCamera();
    } else if (!isOpen) {
      stopCamera();
    }
  }, [isOpen, isStreaming, capturedImage, startCamera, stopCamera]);

  // –û—á–∏—Å—Ç–∫–∞ –ø—Ä–∏ —Ä–∞–∑–º–æ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏
  React.useEffect(() => {
    return () => {
      stopCamera();
    };
  }, [stopCamera]);

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="max-w-md">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Camera className="w-5 h-5" />
            –°—ä–µ–º–∫–∞ —Å –∫–∞–º–µ—Ä—ã
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          {/* Camera Preview */}
          <div className="relative bg-black rounded-lg overflow-hidden aspect-video">
            {!capturedImage ? (
              <>
                <video
                  ref={videoRef}
                  className="w-full h-full object-cover"
                  autoPlay
                  playsInline
                  muted
                />
                {isStreaming && (
                  <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex items-center gap-2">
                    <Button
                      onClick={switchCamera}
                      variant="secondary"
                      size="sm"
                      className="rounded-full w-10 h-10 p-0"
                    >
                      <RotateCcw className="w-4 h-4" />
                    </Button>
                    <Button
                      onClick={capturePhoto}
                      className="rounded-full w-16 h-16 bg-white border-4 border-white hover:bg-gray-100"
                    >
                      <div className="w-full h-full rounded-full bg-white" />
                    </Button>
                  </div>
                )}
              </>
            ) : (
              <img
                src={capturedImage}
                alt="Captured"
                className="w-full h-full object-cover"
              />
            )}

            {!isStreaming && !capturedImage && (
              <div className="absolute inset-0 flex items-center justify-center">
                <Button onClick={startCamera} className="flex items-center gap-2">
                  <Camera className="w-4 h-4" />
                  –í–∫–ª—é—á–∏—Ç—å –∫–∞–º–µ—Ä—É
                </Button>
              </div>
            )}
          </div>

          {/* Hidden Canvas */}
          <canvas ref={canvasRef} className="hidden" />

          {/* Controls */}
          {capturedImage && (
            <div className="flex justify-between gap-2">
              <Button
                onClick={retakePhoto}
                variant="outline"
                className="flex-1"
              >
                –ü–µ—Ä–µ—Å–Ω—è—Ç—å
              </Button>
              <Button
                onClick={savePhoto}
                disabled={uploadMutation.isPending}
                className="flex-1 flex items-center gap-2"
              >
                {uploadMutation.isPending ? (
                  <Loader2 className="w-4 h-4 animate-spin" />
                ) : (
                  <Zap className="w-4 h-4" />
                )}
                –°–æ—Ö—Ä–∞–Ω–∏—Ç—å
              </Button>
            </div>
          )}

          {/* Info */}
          <div className="text-xs text-gray-500 text-center">
            {!isStreaming && !capturedImage && "–ù–∞–∂–º–∏—Ç–µ '–í–∫–ª—é—á–∏—Ç—å –∫–∞–º–µ—Ä—É' –¥–ª—è –Ω–∞—á–∞–ª–∞ —Å—ä–µ–º–∫–∏"}
            {isStreaming && !capturedImage && "–ù–∞–∂–º–∏—Ç–µ –Ω–∞ –±–µ–ª—É—é –∫–Ω–æ–ø–∫—É –¥–ª—è —Å—ä–µ–º–∫–∏"}
            {capturedImage && "–§–æ—Ç–æ –≥–æ—Ç–æ–≤–æ! –°–æ—Ö—Ä–∞–Ω–∏—Ç–µ –∏–ª–∏ –ø–µ—Ä–µ—Å–Ω–∏–º–∏—Ç–µ"}
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

