import React, { useState } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogFooter } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { Save, Loader2 } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { useTelegramAuth } from '@/hooks/use-telegram-auth';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import type { BotData } from '@/types/bot';

interface SaveTemplateModalProps {
  isOpen: boolean;
  onClose: () => void;
  botData: BotData;
  projectName?: string;
}

interface TemplateFormData {
  name: string;
  description: string;
  category: string;
  isPublic: boolean;
  isAnonymous: boolean;
}

export function SaveTemplateModal({ isOpen, onClose, botData, projectName }: SaveTemplateModalProps) {
  const [formData, setFormData] = useState<TemplateFormData>({
    name: projectName ? `${projectName} - –®–∞–±–ª–æ–Ω` : '–ù–æ–≤—ã–π —à–∞–±–ª–æ–Ω',
    description: '',
    category: 'custom',
    isPublic: false,
    isAnonymous: false,
  });
  const { toast } = useToast();
  const { user } = useTelegramAuth();
  const queryClient = useQueryClient();

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –≤—ã—á–∏—Å–ª–µ–Ω–∏—è —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫–∏ —Å –ø–æ–¥–¥–µ—Ä–∂–∫–æ–π –º–Ω–æ–≥–æ–ª–∏—Å—Ç–æ–≤—ã—Ö —à–∞–±–ª–æ–Ω–æ–≤
  const getStats = (data: BotData | any) => {
    let nodes: any[] = [];
    let connections: any[] = [];

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —ç—Ç–æ –º–Ω–æ–≥–æ–ª–∏—Å—Ç–æ–≤–æ–π —à–∞–±–ª–æ–Ω –∏–ª–∏ –æ–±—ã—á–Ω—ã–π
    if (data.sheets && Array.isArray(data.sheets)) {
      // –ú–Ω–æ–≥–æ–ª–∏—Å—Ç–æ–≤–æ–π —à–∞–±–ª–æ–Ω - —Å–æ–±–∏—Ä–∞–µ–º –≤—Å–µ —É–∑–ª—ã –∏ —Å–≤—è–∑–∏ –∏–∑ –≤—Å–µ—Ö –ª–∏—Å—Ç–æ–≤
      data.sheets.forEach((sheet: any) => {
        if (sheet.nodes) nodes.push(...sheet.nodes);
        if (sheet.connections) connections.push(...sheet.connections);
      });
      // –î–æ–±–∞–≤–ª—è–µ–º –º–µ–∂–ª–∏—Å—Ç–æ–≤—ã–µ —Å–≤—è–∑–∏
      if (data.interSheetConnections) {
        connections.push(...data.interSheetConnections);
      }
    } else {
      // –û–±—ã—á–Ω—ã–π —à–∞–±–ª–æ–Ω
      nodes = data.nodes || [];
      connections = data.connections || [];
    }

    return {
      nodes: nodes.length,
      connections: connections.length,
      commands: nodes.filter(node => node.data?.command).length,
      buttons: nodes.reduce((acc, node) => acc + (node.data?.buttons?.length || 0), 0),
    };
  };

  const stats = getStats(botData);

  const saveTemplateMutation = useMutation({
    mutationFn: async (data: TemplateFormData) => {
      return await apiRequest('POST', '/api/templates', {
        name: data.name,
        description: data.description,
        category: data.category,
        tags: [],
        isPublic: data.isPublic ? 1 : 0,
        difficulty: 'easy',
        language: 'ru',
        requiresToken: 1,
        complexity: 1,
        estimatedTime: 5,
        authorName: data.isAnonymous ? null : (user?.username || '–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å'),
        featured: 0,
        data: botData,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/templates'] });
      queryClient.invalidateQueries({ queryKey: ['/api/templates/category/custom'] });
      toast({
        title: '–®–∞–±–ª–æ–Ω —Å–æ—Ö—Ä–∞–Ω–µ–Ω',
        description: '–í–∞—à —à–∞–±–ª–æ–Ω –±–æ—Ç–∞ —É—Å–ø–µ—à–Ω–æ —Å–æ—Ö—Ä–∞–Ω–µ–Ω',
      });
      onClose();
      resetForm();
    },
    onError: () => {
      toast({
        title: '–û—à–∏–±–∫–∞',
        description: '–ù–µ —É–¥–∞–ª–æ—Å—å —Å–æ—Ö—Ä–∞–Ω–∏—Ç—å —à–∞–±–ª–æ–Ω',
        variant: 'destructive',
      });
    },
  });

  const resetForm = () => {
    setFormData({
      name: projectName ? `${projectName} - –®–∞–±–ª–æ–Ω` : '–ù–æ–≤—ã–π —à–∞–±–ª–æ–Ω',
      description: '',
      category: 'custom',
      isPublic: false,
      isAnonymous: false,
    });
  };


  const handleSave = () => {
    if (!formData.name.trim()) {
      toast({
        title: '–û—à–∏–±–∫–∞',
        description: '–ù–∞–∑–≤–∞–Ω–∏–µ —à–∞–±–ª–æ–Ω–∞ –æ–±—è–∑–∞—Ç–µ–ª—å–Ω–æ',
        variant: 'destructive',
      });
      return;
    }
    saveTemplateMutation.mutate(formData);
  };

  const categories = [
    { value: 'custom', label: '–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å—Å–∫–∏–π' },
    { value: 'business', label: '–ë–∏–∑–Ω–µ—Å' },
    { value: 'utility', label: '–£—Ç–∏–ª–∏—Ç—ã' },
    { value: 'games', label: '–ò–≥—Ä—ã' },
  ];

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Save className="h-5 w-5" />
            –°–æ—Ö—Ä–∞–Ω–∏—Ç—å –∫–∞–∫ —à–∞–±–ª–æ–Ω
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-6 py-4">
          {/* –ù–∞–∑–≤–∞–Ω–∏–µ */}
          <div className="space-y-2">
            <Label htmlFor="name">–ù–∞–∑–≤–∞–Ω–∏–µ —à–∞–±–ª–æ–Ω–∞</Label>
            <Input
              id="name"
              value={formData.name}
              onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value }))}
              placeholder="–í–≤–µ–¥–∏—Ç–µ –Ω–∞–∑–≤–∞–Ω–∏–µ —à–∞–±–ª–æ–Ω–∞"
            />
          </div>

          {/* –û–ø–∏—Å–∞–Ω–∏–µ */}
          <div className="space-y-2">
            <Label htmlFor="description">–û–ø–∏—Å–∞–Ω–∏–µ</Label>
            <Textarea
              id="description"
              value={formData.description}
              onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
              placeholder="–ö—Ä–∞—Ç–∫–æ–µ –æ–ø–∏—Å–∞–Ω–∏–µ —Ç–æ–≥–æ, –¥–ª—è —á–µ–≥–æ –ø—Ä–µ–¥–Ω–∞–∑–Ω–∞—á–µ–Ω —ç—Ç–æ—Ç —à–∞–±–ª–æ–Ω"
              rows={3}
            />
          </div>

          {/* –ö–∞—Ç–µ–≥–æ—Ä–∏—è */}
          <div className="space-y-2">
            <Label>–ö–∞—Ç–µ–≥–æ—Ä–∏—è</Label>
            <Select
              value={formData.category}
              onValueChange={(value) => setFormData(prev => ({ ...prev, category: value }))}
            >
              <SelectTrigger>
                <SelectValue placeholder="–í—ã–±–µ—Ä–∏—Ç–µ –∫–∞—Ç–µ–≥–æ—Ä–∏—é" />
              </SelectTrigger>
              <SelectContent>
                {categories.map((category) => (
                  <SelectItem key={category.value} value={category.value}>
                    {category.label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {/* –ü—É–±–ª–∏—á–Ω–æ—Å—Ç—å */}
          <div className="space-y-3">
            <div className="flex items-center space-x-2">
              <input
                id="isPublic"
                type="checkbox"
                checked={formData.isPublic}
                onChange={(e) => setFormData(prev => ({ ...prev, isPublic: e.target.checked }))}
                className="h-4 w-4"
              />
              <Label htmlFor="isPublic">
                –°–¥–µ–ª–∞—Ç—å —à–∞–±–ª–æ–Ω –ø—É–±–ª–∏—á–Ω—ã–º (–¥—Ä—É–≥–∏–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–∏ —Å–º–æ–≥—É—Ç –µ–≥–æ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å)
              </Label>
            </div>

            {/* –ü—Ä–µ–¥—É–ø—Ä–µ–∂–¥–µ–Ω–∏–µ –æ –≤–∏–¥–∏–º–æ—Å—Ç–∏ */}
            {formData.isPublic && !formData.isAnonymous && (
              <div className="p-3 bg-yellow-50 dark:bg-yellow-900/20 border border-yellow-200 dark:border-yellow-800 rounded-lg text-sm text-yellow-800 dark:text-yellow-200">
                ‚ö†Ô∏è <strong>–í–Ω–∏–º–∞–Ω–∏–µ!</strong> –í–∞—à —é–∑–µ—Ä–Ω–µ–π–º Telegram –±—É–¥–µ—Ç –≤–∏–¥–µ–Ω –≤—Å–µ–º, –∫—Ç–æ –∏—Å–ø–æ–ª—å–∑—É–µ—Ç —ç—Ç–æ—Ç —à–∞–±–ª–æ–Ω.
              </div>
            )}

            {/* –ê–Ω–æ–Ω–∏–º–Ω–æ—Å—Ç—å */}
            <div className="flex items-center space-x-2">
              <input
                id="isAnonymous"
                type="checkbox"
                checked={formData.isAnonymous}
                onChange={(e) => setFormData(prev => ({ ...prev, isAnonymous: e.target.checked }))}
                className="h-4 w-4"
              />
              <Label htmlFor="isAnonymous">
                –°–æ—Ö—Ä–∞–Ω–∏—Ç—å –∞–Ω–æ–Ω–∏–º–Ω–æ (—Å–∫—Ä—ã—Ç—å –º–æ–π —é–∑–µ—Ä–Ω–µ–π–º)
              </Label>
            </div>

            {/* –ò–Ω—Ñ–æ –æ–± –∞–Ω–æ–Ω–∏–º–Ω–æ—Å—Ç–∏ */}
            {formData.isAnonymous && (
              <div className="p-3 bg-blue-50 dark:bg-blue-900/20 border border-blue-200 dark:border-blue-800 rounded-lg text-sm text-blue-800 dark:text-blue-200">
                ‚úì –®–∞–±–ª–æ–Ω –±—É–¥–µ—Ç —Å–æ—Ö—Ä–∞–Ω—ë–Ω –∫–∞–∫ "–°–æ—Ö—Ä–∞–Ω–µ–Ω–æ –æ—Ç —Å–æ–æ–±—â–µ—Å—Ç–≤–∞"
              </div>
            )}
          </div>

          {/* –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ –±–æ—Ç–∞ */}
          <div className="p-4 bg-muted rounded-lg">
            <h4 className="font-medium mb-2">–ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ —à–∞–±–ª–æ–Ω–µ:</h4>
            <div className="grid grid-cols-2 gap-4 text-sm">
              <div>
                <span className="text-muted-foreground">–£–∑–ª–æ–≤:</span>
                <span className="ml-2 font-medium">{stats.nodes}</span>
              </div>
              <div>
                <span className="text-muted-foreground">–°–≤—è–∑–µ–π:</span>
                <span className="ml-2 font-medium">{stats.connections}</span>
              </div>
              <div>
                <span className="text-muted-foreground">–ö–æ–º–∞–Ω–¥:</span>
                <span className="ml-2 font-medium">{stats.commands}</span>
              </div>
              <div>
                <span className="text-muted-foreground">–ö–Ω–æ–ø–æ–∫:</span>
                <span className="ml-2 font-medium">{stats.buttons}</span>
              </div>
            </div>
          </div>
        </div>

        <DialogFooter>
          <Button variant="outline" onClick={onClose}>
            –û—Ç–º–µ–Ω–∞
          </Button>
          <Button
            onClick={handleSave}
            disabled={saveTemplateMutation.isPending || !formData.name.trim()}
          >
            {saveTemplateMutation.isPending && <Loader2 className="h-4 w-4 mr-2 animate-spin" />}
            –°–æ—Ö—Ä–∞–Ω–∏—Ç—å —à–∞–±–ª–æ–Ω
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

