import { Button } from '@/components/ui/button';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Separator } from '@/components/ui/separator';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { useToast } from '@/hooks/use-toast';
import { useIsMobile } from '@/hooks/use-mobile';
import { BotData, BotGroup } from '@shared/schema';
import { useState, useEffect, useMemo, useRef } from 'react';
import { useQuery } from '@tanstack/react-query';
import Editor from '@monaco-editor/react';
import { Loader2 } from 'lucide-react';
import { CodeFormat, useCodeGenerator } from '@/hooks/use-code-generator';

// –î–∏–Ω–∞–º–∏—á–µ—Å–∫–∏–π –∏–º–ø–æ—Ä—Ç –¥–ª—è –≥–µ–Ω–µ—Ä–∞—Ü–∏–∏ –∫–æ–º–∞–Ω–¥ BotFather
const loadCommands = () => import('@/lib/commands');

interface ExportPanelProps {
  botData: BotData;
  projectName: string;
  projectId: number;
  userDatabaseEnabled?: boolean;
}

export function ExportPanel({ botData, projectName, projectId, userDatabaseEnabled = false }: ExportPanelProps) {
  const [selectedFormat, setSelectedFormat] = useState<CodeFormat>('python');
  const [theme, setTheme] = useState<'light' | 'dark'>('light');
  const [showFullCode, setShowFullCode] = useState(false);
  const [areAllCollapsed, setAreAllCollapsed] = useState(true);
  const [botFatherCommands, setBotFatherCommands] = useState('');
  const [validationResult, setValidationResult] = useState<{ isValid: boolean; errors: string[] }>({ isValid: true, errors: [] });
  const editorRef = useRef<any>(null);
  const { toast } = useToast();
  const isMobile = useIsMobile();

  const { data: groups = [] } = useQuery<BotGroup[]>({
    queryKey: ['/api/groups'],
  });

  // –ò—Å–ø–æ–ª—å–∑—É–µ–º –æ–±—â–∏–π –≥–µ–Ω–µ—Ä–∞—Ç–æ—Ä –∫–æ–¥–∞
  const { codeContent, isLoading, loadContent, generateContent } = useCodeGenerator(botData, projectName, groups, userDatabaseEnabled, projectId);

  // –û–ø—Ä–µ–¥–µ–ª—è–µ–º —Ç–µ–º—É –∏–∑ DOM
  useEffect(() => {
    const checkTheme = () => {
      const isDark = document.documentElement.classList.contains('dark');
      setTheme(isDark ? 'dark' : 'light');
    };

    checkTheme();

    const observer = new MutationObserver(checkTheme);
    observer.observe(document.documentElement, {
      attributes: true,
      attributeFilter: ['class']
    });

    return () => observer.disconnect();
  }, []);

  // –ó–∞–≥—Ä—É–∂–∞–µ–º –∫–æ–Ω—Ç–µ–Ω—Ç –¥–ª—è –≤—ã–±—Ä–∞–Ω–Ω–æ–≥–æ —Ñ–æ—Ä–º–∞—Ç–∞
  useEffect(() => {
    loadContent(selectedFormat);
  }, [selectedFormat, loadContent]);

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è —Å–≤–æ—Ä–∞—á–∏–≤–∞–Ω–∏—è/—Ä–∞–∑–≤–æ—Ä–∞—á–∏–≤–∞–Ω–∏—è –≤—Å–µ—Ö —Ñ—É–Ω–∫—Ü–∏–π
  const toggleAllFunctions = () => {
    if (editorRef.current) {
      const editor = editorRef.current;
      if (areAllCollapsed) {
        editor.getAction('editor.unfoldAll')?.run();
        setAreAllCollapsed(false);
      } else {
        editor.getAction('editor.foldAll')?.run();
        setAreAllCollapsed(true);
      }
    }
  };

  // –ó–∞–≥—Ä—É–∑–∫–∞ –∏ –≤–∞–ª–∏–¥–∞—Ü–∏—è –¥–ª—è Python
  useEffect(() => {
    async function loadValidation() {
      if (selectedFormat === 'python') {
        try {
          const { validateBotStructure } = await import('@/lib/bot-generator');
          const validation = validateBotStructure(botData);
          setValidationResult(validation || { isValid: false, errors: [] });
        } catch (error) {
          console.error('Error validating bot structure:', error);
        }
      }
    }
    loadValidation();
  }, [selectedFormat, botData]);

  // –ó–∞–≥—Ä—É–∑–∫–∞ –∫–æ–º–∞–Ω–¥ BotFather
  useEffect(() => {
    async function loadBotFatherCommands() {
      try {
        const commands = await loadCommands();

        let nodes: any[] = [];
        if ((botData as any).sheets && Array.isArray((botData as any).sheets)) {
          (botData as any).sheets.forEach((sheet: any) => {
            if (sheet.nodes && Array.isArray(sheet.nodes)) {
              nodes = nodes.concat(sheet.nodes);
            }
          });
        } else {
          nodes = botData.nodes || [];
        }

        const commandNodes = nodes.filter((node: any) => 
          (node.type === 'start' || node.type === 'command') && 
          node.data?.command &&
          (node.data?.showInMenu !== false)
        );

        const botFatherCmds = commands.generateBotFatherCommands(commandNodes);
        setBotFatherCommands(botFatherCmds);
      } catch (error) {
        console.error('Error loading BotFather commands:', error);
      }
    }

    loadBotFatherCommands();
  }, [botData]);

  // –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ –±–æ—Ç–∞
  const botStats = useMemo(() => {
    const allNodes = Array.isArray((botData as any).sheets)
      ? (botData as any).sheets.reduce((acc: any[], sheet: any) => 
          acc.concat(sheet.nodes || []), [])
      : botData.nodes || [];

    return {
      totalNodes: allNodes.length,
      commandNodes: allNodes.filter((node: any) => node.type === 'start' || node.type === 'command').length,
      messageNodes: allNodes.filter((node: any) => node.type === 'message').length,
      photoNodes: allNodes.filter((node: any) => node.type === 'photo').length,
      keyboardNodes: allNodes.filter((node: any) => node.data?.keyboardType !== 'none').length,
      totalButtons: allNodes.reduce((sum: number, node: any) => sum + (node.data?.buttons?.length || 0), 0),
      commandsInMenu: allNodes.filter((node: any) => 
        (node.type === 'start' || node.type === 'command') && node.data?.showInMenu
      ).length,
      adminOnlyCommands: allNodes.filter((node: any) => 
        (node.type === 'start' || node.type === 'command') && node.data?.adminOnly
      ).length,
    };
  }, [botData]);

  const getCurrentContent = () => codeContent[selectedFormat] || '';

  const content = getCurrentContent();
  const lines = content.split('\n');
  const lineCount = lines.length;
  const MAX_VISIBLE_LINES = 1000;

  const displayContent = useMemo(() => {
    if (!showFullCode && lines.length > MAX_VISIBLE_LINES) {
      return lines.slice(0, MAX_VISIBLE_LINES).join('\n');
    }
    return content;
  }, [content, showFullCode]);

  const codeStats = useMemo(() => {
    return {
      totalLines: lineCount,
      truncated: !showFullCode && lineCount > 1000
    };
  }, [content, showFullCode]);

  const copyToClipboard = async () => {
    try {
      await navigator.clipboard.writeText(getCurrentContent());
      toast({
        title: "–°–æ–¥–µ—Ä–∂–∏–º–æ–µ —Å–∫–æ–ø–∏—Ä–æ–≤–∞–Ω–æ!",
        description: "–°–æ–¥–µ—Ä–∂–∏–º–æ–µ —Å–∫–æ–ø–∏—Ä–æ–≤–∞–Ω–æ –≤ –±—É—Ñ–µ—Ä –æ–±–º–µ–Ω–∞",
      });
    } catch (error) {
      toast({
        title: "–û—à–∏–±–∫–∞ –∫–æ–ø–∏—Ä–æ–≤–∞–Ω–∏—è",
        description: "–ù–µ —É–¥–∞–ª–æ—Å—å —Å–∫–æ–ø–∏—Ä–æ–≤–∞—Ç—å —Å–æ–¥–µ—Ä–∂–∏–º–æ–µ –≤ –±—É—Ñ–µ—Ä –æ–±–º–µ–Ω–∞",
        variant: "destructive",
      });
    }
  };

  const downloadFile = async () => {
    const fileExtensions: Record<CodeFormat, string> = {
      python: '.py',
      json: '.json',
      requirements: '.txt',
      readme: '.md',
      dockerfile: '',
      config: '.yaml'
    };

    const fileNames: Record<CodeFormat, string> = {
      python: `${projectName.replace(/\s+/g, '_')}_bot`,
      json: `${projectName.replace(/\s+/g, '_')}_data`,
      requirements: 'requirements',
      readme: 'README',
      dockerfile: 'Dockerfile',
      config: 'config'
    };

    const blob = new Blob([getCurrentContent()], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = fileNames[selectedFormat] + fileExtensions[selectedFormat];
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);

    toast({
      title: "–§–∞–π–ª –∑–∞–≥—Ä—É–∂–µ–Ω!",
      description: `–§–∞–π–ª ${link.download} —É—Å–ø–µ—à–Ω–æ –∑–∞–≥—Ä—É–∂–µ–Ω`,
    });
  };

  const downloadAllFiles = () => {
    const formats: CodeFormat[] = ['python', 'json', 'requirements', 'readme', 'dockerfile', 'config'];
    formats.forEach((format, index) => {
      setTimeout(async () => {
        const fileExtensions: Record<CodeFormat, string> = {
          python: '.py',
          json: '.json',
          requirements: '.txt',
          readme: '.md',
          dockerfile: '',
          config: '.yaml'
        };

        const fileNames: Record<CodeFormat, string> = {
          python: `${projectName.replace(/\s+/g, '_')}_bot`,
          json: `${projectName.replace(/\s+/g, '_')}_data`,
          requirements: 'requirements',
          readme: 'README',
          dockerfile: 'Dockerfile',
          config: 'config'
        };

        const content = await generateContent(format);
        const blob = new Blob([content], { type: 'text/plain' });
        const url = URL.createObjectURL(blob);
        const link = document.createElement('a');
        link.href = url;
        link.download = fileNames[format] + fileExtensions[format];
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
        URL.revokeObjectURL(url);
      }, index * 100);
    });

    toast({
      title: "–í—Å–µ —Ñ–∞–π–ª—ã –∑–∞–≥—Ä—É–∂–µ–Ω—ã!",
      description: "–ü–æ–ª–Ω—ã–π –ø—Ä–æ–µ–∫—Ç –±–æ—Ç–∞ –∑–∞–≥—Ä—É–∂–µ–Ω",
    });
  };

  const getFormatIcon = (format: CodeFormat) => {
    const icons = {
      python: 'fab fa-python text-blue-500',
      json: 'fas fa-database text-green-500',
      requirements: 'fas fa-list text-orange-500',
      readme: 'fas fa-file-alt text-purple-500',
      dockerfile: 'fab fa-docker text-cyan-500',
      config: 'fas fa-cog text-yellow-500'
    };
    return icons[format];
  };

  const getFormatLabel = (format: CodeFormat) => {
    const labels = {
      python: 'Python –∫–æ–¥',
      json: 'JSON –¥–∞–Ω–Ω—ã–µ',
      requirements: 'Requirements.txt',
      readme: 'README.md',
      dockerfile: 'Dockerfile',
      config: 'Config YAML'
    };
    return labels[format];
  };

  return (
    <div className="h-full bg-background overflow-auto">
      <div className="p-4 md:p-6">
        <div className="max-w-7xl mx-auto space-y-6">
          {/* –ó–∞–≥–æ–ª–æ–≤–æ–∫ */}
          <div>
            <h1 className="text-2xl font-bold flex items-center space-x-3">
              <div className="w-8 h-8 rounded-lg bg-gradient-to-br from-blue-100 to-cyan-100 dark:from-blue-900/30 dark:to-cyan-900/30 flex items-center justify-center">
                <i className="fas fa-download text-blue-600 dark:text-blue-400"></i>
              </div>
              <span>–≠–∫—Å–ø–æ—Ä—Ç –∫–æ–¥–∞ –±–æ—Ç–∞</span>
            </h1>
            <p className="text-muted-foreground mt-1">–ó–∞–≥—Ä—É–∑–∏—Ç–µ –≥–æ—Ç–æ–≤—ã–π –∫–æ–¥ –±–æ—Ç–∞ –∏ –¥–æ–∫—É–º–µ–Ω—Ç–∞—Ü–∏—é –¥–ª—è —Ä–∞–∑–≤–µ—Ä—Ç—ã–≤–∞–Ω–∏—è</p>
          </div>

          {/* –¢–∞–±—ã */}
          <Tabs defaultValue="stats" className="w-full">
            <TabsList className={`grid w-full ${isMobile ? 'grid-cols-2 gap-1 h-auto' : 'grid-cols-4'}`}>
              <TabsTrigger value="stats" className={isMobile ? 'text-xs py-2' : ''}>–°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞</TabsTrigger>
              <TabsTrigger value="validation" className={isMobile ? 'text-xs py-2' : ''}>–í–∞–ª–∏–¥–∞—Ü–∏—è</TabsTrigger>
              <TabsTrigger value="code" className={isMobile ? 'text-xs py-2' : ''}>–ö–æ–¥</TabsTrigger>
              <TabsTrigger value="export" className={isMobile ? 'text-xs py-2' : ''}>–≠–∫—Å–ø–æ—Ä—Ç</TabsTrigger>
            </TabsList>

            {/* –¢–∞–± —Å–æ —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫–æ–π */}
            <TabsContent value="stats" className="space-y-4 mt-4">
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center space-x-2">
                    <i className="fas fa-chart-bar text-blue-500"></i>
                    <span>–°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ –±–æ—Ç–∞</span>
                  </CardTitle>
                  <CardDescription>–û–±–∑–æ—Ä —Å—Ç—Ä—É–∫—Ç—É—Ä—ã –∏ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–æ–≤ –≤–∞—à–µ–≥–æ –±–æ—Ç–∞</CardDescription>
                </CardHeader>
                <CardContent>
                  <div className={`grid ${isMobile ? 'grid-cols-2 gap-3' : 'grid-cols-3 gap-4'}`}>
                    <div className="bg-blue-50 dark:bg-blue-900/20 border border-blue-200 dark:border-blue-800 rounded-lg p-3 text-center">
                      <div className="text-2xl font-bold text-blue-600 dark:text-blue-400">{botStats.totalNodes}</div>
                      <div className="text-xs text-blue-700 dark:text-blue-300">–í—Å–µ–≥–æ —É–∑–ª–æ–≤</div>
                    </div>
                    <div className="bg-green-50 dark:bg-green-900/20 border border-green-200 dark:border-green-800 rounded-lg p-3 text-center">
                      <div className="text-2xl font-bold text-green-600 dark:text-green-400">{botStats.commandNodes}</div>
                      <div className="text-xs text-green-700 dark:text-green-300">–ö–æ–º–∞–Ω–¥</div>
                    </div>
                    <div className="bg-purple-50 dark:bg-purple-900/20 border border-purple-200 dark:border-purple-800 rounded-lg p-3 text-center">
                      <div className="text-2xl font-bold text-purple-600 dark:text-purple-400">{botStats.totalButtons}</div>
                      <div className="text-xs text-purple-700 dark:text-purple-300">–ö–Ω–æ–ø–æ–∫</div>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </TabsContent>

            {/* –¢–∞–± —Å –≤–∞–ª–∏–¥–∞—Ü–∏–µ–π */}
            <TabsContent value="validation" className="space-y-4 mt-4">
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center space-x-2">
                    {validationResult.isValid ? (
                      <i className="fas fa-check-circle text-green-500"></i>
                    ) : (
                      <i className="fas fa-exclamation-triangle text-red-500"></i>
                    )}
                    <span>–ü—Ä–æ–≤–µ—Ä–∫–∞ —Å—Ç—Ä—É–∫—Ç—É—Ä—ã</span>
                  </CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  {validationResult.isValid ? (
                    <div className="flex items-center space-x-2 text-green-600 dark:text-green-400 p-4 bg-green-50 dark:bg-green-950/30 rounded-lg border border-green-200 dark:border-green-800/40">
                      <i className="fas fa-check-circle"></i>
                      <span className="font-medium">–°—Ç—Ä—É–∫—Ç—É—Ä–∞ –±–æ—Ç–∞ –∫–æ—Ä—Ä–µ–∫—Ç–Ω–∞ –∏ –≥–æ—Ç–æ–≤–∞ –∫ —ç–∫—Å–ø–æ—Ä—Ç—É!</span>
                    </div>
                  ) : (
                    <div className="space-y-2">
                      <div className="flex items-center space-x-2 text-red-600 dark:text-red-400 p-3 bg-red-50 dark:bg-red-950/30 rounded-lg border border-red-200 dark:border-red-800/40">
                        <i className="fas fa-exclamation-triangle"></i>
                        <span className="font-medium">–ù–∞–π–¥–µ–Ω—ã –æ—à–∏–±–∫–∏ –≤ —Å—Ç—Ä—É–∫—Ç—É—Ä–µ –±–æ—Ç–∞:</span>
                      </div>
                      <div className="space-y-2">
                        {(validationResult.errors || []).map((error, index) => (
                          <div key={index} className="flex items-start space-x-2 p-3 bg-red-50 dark:bg-red-950/20 rounded border-l-4 border-red-200 dark:border-red-800/60">
                            <i className="fas fa-times-circle text-red-500 dark:text-red-400 mt-0.5"></i>
                            <span className="text-sm text-red-700 dark:text-red-300">{error}</span>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}
                  {botFatherCommands && (
                    <>
                      <Separator />
                      <div className="bg-blue-50 dark:bg-blue-950/30 p-4 rounded-lg border border-blue-200 dark:border-blue-800/40">
                        <h5 className="font-medium text-blue-800 dark:text-blue-200 mb-2">–ö–æ–º–∞–Ω–¥—ã –¥–ª—è @BotFather:</h5>
                        <pre className="bg-background p-3 rounded text-xs overflow-auto max-h-32">
                          {botFatherCommands}
                        </pre>
                        <Button onClick={() => copyToClipboard()} variant="outline" size="sm" className="mt-2">
                          <i className="fas fa-copy mr-2"></i>
                          –ö–æ–ø–∏—Ä–æ–≤–∞—Ç—å –∫–æ–º–∞–Ω–¥—ã
                        </Button>
                      </div>
                    </>
                  )}
                </CardContent>
              </Card>
            </TabsContent>

            {/* –¢–∞–± —Å –∫–æ–¥–æ–º */}
            <TabsContent value="code" className="space-y-4 mt-4">
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center space-x-2">
                    <i className={getFormatIcon(selectedFormat)}></i>
                    <span>{getFormatLabel(selectedFormat)}</span>
                  </CardTitle>
                  <CardDescription>–í—ã–±–µ—Ä–∏—Ç–µ —Ñ–æ—Ä–º–∞—Ç –¥–ª—è –ø—Ä–æ—Å–º–æ—Ç—Ä–∞</CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                  {/* –í—ã–±–æ—Ä —Ñ–æ—Ä–º–∞—Ç–∞ */}
                  <Select value={selectedFormat} onValueChange={(value) => setSelectedFormat(value as CodeFormat)}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="python">Python –∫–æ–¥ (.py)</SelectItem>
                      <SelectItem value="json">JSON –¥–∞–Ω–Ω—ã–µ (.json)</SelectItem>
                      <SelectItem value="requirements">Requirements.txt</SelectItem>
                      <SelectItem value="readme">README.md</SelectItem>
                      <SelectItem value="dockerfile">Dockerfile</SelectItem>
                      <SelectItem value="config">Config YAML (.yaml)</SelectItem>
                    </SelectContent>
                  </Select>

                  {/* –ö–Ω–æ–ø–∫–∏ –¥–µ–π—Å—Ç–≤–∏–π */}
                  <div className="grid grid-cols-2 gap-2">
                    <Button
                      onClick={copyToClipboard}
                      variant="outline"
                      className="w-full"
                    >
                      <i className="fas fa-copy mr-2"></i>
                      –ö–æ–ø–∏—Ä–æ–≤–∞—Ç—å
                    </Button>
                    <Button
                      onClick={downloadFile}
                      className="w-full"
                    >
                      <i className="fas fa-download mr-2"></i>
                      –°–∫–∞—á–∞—Ç—å
                    </Button>
                  </div>

                  <Separator />

                  {/* –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ –∫–æ–¥–∞ */}
                  {codeStats.totalLines > 0 && (
                    <div className={`grid ${isMobile ? 'grid-cols-2' : 'grid-cols-2 md:grid-cols-4'} gap-2`}>
                      <div className="bg-blue-50 dark:bg-blue-900/20 border border-blue-200 dark:border-blue-800 rounded-lg p-2 text-center">
                        <div className="text-lg font-bold text-blue-600 dark:text-blue-400">{codeStats.totalLines}</div>
                        <div className="text-xs text-blue-700 dark:text-blue-300">–°—Ç—Ä–æ–∫</div>
                      </div>
                      <div className="bg-orange-50 dark:bg-orange-900/20 border border-orange-200 dark:border-orange-800 rounded-lg p-2 text-center">
                        <div className="text-lg font-bold text-orange-600 dark:text-orange-400">{Math.round(getCurrentContent().length / 1024)}</div>
                        <div className="text-xs text-orange-700 dark:text-orange-300">KB</div>
                      </div>
                      {selectedFormat === 'python' && (
                        <>
                          <div className="bg-green-50 dark:bg-green-900/20 border border-green-200 dark:border-green-800 rounded-lg p-2 text-center">
                            <div className="text-lg font-bold text-green-600 dark:text-green-400">{(getCurrentContent().match(/^def |^async def /gm) || []).length}</div>
                            <div className="text-xs text-green-700 dark:text-green-300">–§—É–Ω–∫—Ü–∏–π</div>
                          </div>
                          <div className="bg-purple-50 dark:bg-purple-900/20 border border-purple-200 dark:border-purple-800 rounded-lg p-2 text-center">
                            <div className="text-lg font-bold text-purple-600 dark:text-purple-400">{(getCurrentContent().match(/^class /gm) || []).length}</div>
                            <div className="text-xs text-purple-700 dark:text-purple-300">–ö–ª–∞—Å—Å–æ–≤</div>
                          </div>
                        </>
                      )}
                      {selectedFormat === 'json' && (
                        <div className="bg-cyan-50 dark:bg-cyan-900/20 border border-cyan-200 dark:border-cyan-800 rounded-lg p-2 text-center">
                          <div className="text-lg font-bold text-cyan-600 dark:text-cyan-400">{(getCurrentContent().match(/"/g) || []).length / 2}</div>
                          <div className="text-xs text-cyan-700 dark:text-cyan-300">–ö–ª—é—á–µ–π</div>
                        </div>
                      )}
                    </div>
                  )}

                  <Separator />

                  {/* –ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –∫–æ–¥–µ */}
                  {codeStats.totalLines > 0 && (
                    <div className="flex items-center justify-between text-sm">
                      <div className="flex items-center gap-2">
                        {(selectedFormat === 'python' || selectedFormat === 'json') && (
                          <Button 
                            size="sm" 
                            variant="ghost"
                            onClick={toggleAllFunctions}
                            className="h-6 px-2 text-xs"
                          >
                            <i className={`fas ${areAllCollapsed ? 'fa-expand' : 'fa-compress'} mr-1`}></i>
                            {areAllCollapsed ? '–†–∞–∑–≤–µ—Ä–Ω—É—Ç—å' : '–°–≤–µ—Ä–Ω—É—Ç—å'}
                          </Button>
                        )}
                      </div>
                      {codeStats.truncated && (
                        <Button 
                          size="sm" 
                          variant="outline"
                          onClick={() => setShowFullCode(true)}
                        >
                          –ü–æ–∫–∞–∑–∞—Ç—å –≤—Å—ë ({codeStats.totalLines})
                        </Button>
                      )}
                    </div>
                  )}

                  <div className={`${isMobile ? 'h-48' : 'h-[500px]'} rounded border border-slate-300 dark:border-slate-700 overflow-hidden`}>
                    {isLoading ? (
                      <div className="flex items-center justify-center h-full">
                        <div className="flex flex-col items-center gap-3">
                          <Loader2 className="h-8 w-8 animate-spin text-primary" />
                          <p className="text-sm text-muted-foreground">–ì–µ–Ω–µ—Ä–∞—Ü–∏—è –∫–æ–¥–∞...</p>
                        </div>
                      </div>
                    ) : (
                      <Editor
                        value={displayContent}
                        language={
                          selectedFormat === 'python' ? 'python' :
                          selectedFormat === 'json' ? 'json' :
                          selectedFormat === 'readme' ? 'markdown' :
                          selectedFormat === 'dockerfile' ? 'dockerfile' :
                          selectedFormat === 'config' ? 'yaml' :
                          'plaintext'
                        }
                        theme={theme === 'dark' ? 'vs-dark' : 'vs-light'}
                        onMount={(editor) => {
                          editorRef.current = editor;
                          if ((selectedFormat === 'python' || selectedFormat === 'json') && codeStats.totalLines > 0) {
                            setTimeout(() => {
                              editor.getAction('editor.foldAll')?.run();
                              setAreAllCollapsed(true);
                            }, 100);
                          }
                        }}
                        options={{
                          readOnly: true,
                          lineNumbers: 'on',
                          wordWrap: 'on',
                          fontSize: 12,
                          lineHeight: 1.5,
                          minimap: { enabled: codeStats.totalLines > 500 },
                          folding: true,
                          foldingHighlight: true,
                          foldingStrategy: 'auto',
                          showFoldingControls: 'always',
                          glyphMargin: true,
                          scrollBeyondLastLine: false,
                          padding: { top: 8, bottom: 8 },
                          automaticLayout: true,
                          contextmenu: false,
                          bracketPairColorization: {
                            enabled: selectedFormat === 'json'
                          },
                          formatOnPaste: false,
                          formatOnType: false
                        }}
                      />
                    )}
                  </div>
                </CardContent>
              </Card>
            </TabsContent>

            {/* –¢–∞–± —Å –±—ã—Å—Ç—Ä—ã–º —ç–∫—Å–ø–æ—Ä—Ç–æ–º */}
            <TabsContent value="export" className="space-y-4 mt-4">
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center space-x-2">
                    <i className="fas fa-download text-blue-500"></i>
                    <span>–ë—ã—Å—Ç—Ä—ã–π —ç–∫—Å–ø–æ—Ä—Ç</span>
                  </CardTitle>
                  <CardDescription>–°–∫–∞—á–∞–π—Ç–µ –≥–æ—Ç–æ–≤—ã–µ —Ñ–∞–π–ª—ã –ø—Ä–æ–µ–∫—Ç–∞</CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                    <Button 
                      size="lg" 
                      onClick={async () => { setSelectedFormat('python'); await downloadFile(); }} 
                      variant="outline" 
                      className="h-auto p-4 flex-col space-y-2"
                    >
                      <i className="fas fa-code text-2xl text-blue-500"></i>
                      <span className="font-medium">Python –∫–æ–¥</span>
                    </Button>

                    <Button 
                      size="lg" 
                      onClick={async () => { setSelectedFormat('json'); await downloadFile(); }} 
                      variant="outline" 
                      className="h-auto p-4 flex-col space-y-2"
                    >
                      <i className="fas fa-database text-2xl text-green-500"></i>
                      <span className="font-medium">JSON –¥–∞–Ω–Ω—ã–µ</span>
                    </Button>

                    <Button 
