                      <Button size="sm" variant="ghost" onClick={async () => { setSelectedFormat('readme'); await downloadFile(); }} className="h-auto p-3 flex-col space-y-1 border border-muted">
                        <i className="fas fa-file-alt text-lg text-purple-500"></i>
