import https from 'https';
import http from 'http';
import { createWriteStream, existsSync, mkdirSync } from 'fs';
import { join, normalize, relative, isAbsolute } from 'path';
import { pipeline } from 'stream/promises';

interface DownloadedPhoto {
  filePath: string;
  fileSize: number;
  mimeType: string;
  fileName: string;
}

/**
 * –°–∫–∞—á–∏–≤–∞–µ—Ç —Ñ–æ—Ç–æ –∏–∑ Telegram —á–µ—Ä–µ–∑ Bot API
 * @param botToken - –¢–æ–∫–µ–Ω –±–æ—Ç–∞
 * @param fileId - file_id —Ñ–æ—Ç–æ –∏–∑ Telegram
 * @param projectId - ID –ø—Ä–æ–µ–∫—Ç–∞ –¥–ª—è –æ—Ä–≥–∞–Ω–∏–∑–∞—Ü–∏–∏ —Ñ–∞–π–ª–æ–≤
 * @returns –ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ —Å–∫–∞—á–∞–Ω–Ω–æ–º —Ñ–∞–π–ª–µ
 */
export async function downloadTelegramPhoto(
  botToken: string,
  fileId: string,
  projectId: number
): Promise<DownloadedPhoto> {
  try {
    // –®–∞–≥ 1: –ü–æ–ª—É—á–∞–µ–º file_path —á–µ—Ä–µ–∑ getFile API
    const getFileUrl = `https://api.telegram.org/bot${botToken}/getFile?file_id=${fileId}`;

    const fileInfo = await new Promise<any>((resolve, reject) => {
      const request = https.get(getFileUrl, { timeout: 30000 }, (res) => {
        let data = '';
        res.on('data', (chunk) => data += chunk);
        res.on('end', () => {
          try {
            const parsed = JSON.parse(data);
            if (parsed.ok) {
              resolve(parsed.result);
            } else {
              reject(new Error(`Telegram API error: ${parsed.description || 'Unknown error'}`));
            }
          } catch (err) {
            reject(err);
          }
        });
      }).on('error', reject);

      request.on('timeout', () => {
        request.destroy();
        reject(new Error('Request timeout'));
      });
    });

    const filePath = fileInfo.file_path;
    const fileSize = fileInfo.file_size || 0;

    // –°–∞–Ω–∏—Ç–∏–∑–∏—Ä–æ–≤–∞—Ç—å fileId (–æ—Å—Ç–∞–≤–∏—Ç—å —Ç–æ–ª—å–∫–æ –±–µ–∑–æ–ø–∞—Å–Ω—ã–µ —Å–∏–º–≤–æ–ª—ã)
    const safeFileId = fileId.replace(/[^a-zA-Z0-9_-]/g, '').substring(0, 20);

    // –û–ø—Ä–µ–¥–µ–ª—è–µ–º MIME —Ç–∏–ø –Ω–∞ –æ—Å–Ω–æ–≤–µ —Ä–∞—Å—à–∏—Ä–µ–Ω–∏—è —Ñ–∞–π–ª–∞
    const extension = filePath.split('.').pop()?.toLowerCase() || '';

    // –í–∞–ª–∏–¥–∏—Ä–æ–≤–∞—Ç—å —Ä–∞—Å—à–∏—Ä–µ–Ω–∏–µ (—Ç–æ–ª—å–∫–æ –±–µ–∑–æ–ø–∞—Å–Ω—ã–µ —Ñ–æ—Ä–º–∞—Ç—ã –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–π)
    const allowedExtensions = ['jpg', 'jpeg', 'png', 'gif', 'webp'];
    if (!allowedExtensions.includes(extension)) {
      throw new Error('Invalid file type');
    }

    const mimeTypeMap: { [key: string]: string } = {
      'jpg': 'image/jpeg',
      'jpeg': 'image/jpeg',
      'png': 'image/png',
      'gif': 'image/gif',
      'webp': 'image/webp',
    };
    const mimeType = mimeTypeMap[extension] || 'image/jpeg';

    // –®–∞–≥ 2: –°–æ–∑–¥–∞–µ–º –¥–∏—Ä–µ–∫—Ç–æ—Ä–∏—é –¥–ª—è —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è
    const date = new Date().toISOString().split('T')[0]; // YYYY-MM-DD
    const uploadDir = join(process.cwd(), 'uploads', String(projectId), date);

    if (!existsSync(uploadDir)) {
      mkdirSync(uploadDir, { recursive: true });
    }

    // –®–∞–≥ 3: –ì–µ–Ω–µ—Ä–∏—Ä—É–µ–º —É–Ω–∏–∫–∞–ª—å–Ω–æ–µ –∏–º—è —Ñ–∞–π–ª–∞
    const timestamp = Date.now();
    const randomSuffix = Math.round(Math.random() * 1E9);
    const fileName = `${timestamp}-${randomSuffix}-${safeFileId}.${extension}`;
    const localFilePath = join(uploadDir, fileName);

    // –í–∞–ª–∏–¥–∏—Ä–æ–≤–∞—Ç—å –ø—É—Ç—å (–∑–∞—â–∏—Ç–∞ –æ—Ç directory traversal)
    const normalizedPath = normalize(localFilePath);
    const rel = relative(uploadDir, normalizedPath);
    if (rel.startsWith('..') || isAbsolute(rel)) {
      throw new Error('Invalid file path - potential directory traversal');
    }

    // –®–∞–≥ 4: –°–∫–∞—á–∏–≤–∞–µ–º —Ñ–∞–π–ª
    const downloadUrl = `https://api.telegram.org/file/bot${botToken}/${filePath}`;

    await new Promise<void>((resolve, reject) => {
      const request = https.get(downloadUrl, { timeout: 30000 }, (res) => {
        if (res.statusCode !== 200) {
          reject(new Error(`Failed to download file: HTTP ${res.statusCode}`));
          return;
        }

        const fileStream = createWriteStream(localFilePath);
        pipeline(res, fileStream)
          .then(() => resolve())
          .catch(reject);
      }).on('error', reject);

      request.on('timeout', () => {
        request.destroy();
        reject(new Error('Request timeout'));
      });
    });

    // –í–æ–∑–≤—Ä–∞—â–∞–µ–º –æ—Ç–Ω–æ—Å–∏—Ç–µ–ª—å–Ω—ã–π –ø—É—Ç—å –æ—Ç –∫–æ—Ä–Ω—è –ø—Ä–æ–µ–∫—Ç–∞
    const relativeFilePath = join('uploads', String(projectId), date, fileName);

    return {
      filePath: relativeFilePath,
      fileSize,
      mimeType,
      fileName,
    };
  } catch (error) {
    console.error('Error downloading Telegram photo:', error);
    throw new Error(`Failed to download photo: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

/**
 * –°–∫–∞—á–∏–≤–∞–µ—Ç –≤–∏–¥–µ–æ –∏–∑ Telegram —á–µ—Ä–µ–∑ Bot API
