import { useState, useEffect } from 'react';
import { queryClient } from '@/lib/queryClient';

export interface TelegramUser {
  id: number;
  firstName: string;
  lastName?: string;
  username?: string;
  photoUrl?: string;
}

const STORAGE_KEY = 'telegramUser';
const AUTH_EVENT = 'telegram-auth-change';

export function useTelegramAuth() {
  const [user, setUser] = useState<TelegramUser | null>(null);
  const [isLoading, setIsLoading] = useState(true);

  // –ó–∞–≥—Ä—É–∂–∞–µ–º –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –∏–∑ localStorage –ø—Ä–∏ –º–æ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏
  useEffect(() => {
    const loadUser = () => {
      try {
        const savedUser = localStorage.getItem(STORAGE_KEY);
        if (savedUser) {
          const parsedUser = JSON.parse(savedUser);
          setUser(parsedUser);
        } else {
          setUser(null);
        }
      } catch (e) {
        console.error('Error loading user from localStorage:', e);
      }
      setIsLoading(false);
    };

    loadUser();

    // –°–ª—É—à–∞–µ–º custom event –æ—Ç TelegramLoginWidget
    const handleAuthChange = (e: any) => {
      try {
        if (e.detail.user) {
          setUser(e.detail.user);
          // –ò–Ω–≤–∞–ª–∏–¥–∏—Ä—É–µ–º –∫–µ—à —à–∞–±–ª–æ–Ω–æ–≤ –ø—Ä–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
          queryClient.invalidateQueries({ queryKey: ['/api/templates/category/custom'] });
          queryClient.invalidateQueries({ queryKey: ['/api/templates'] });
          queryClient.invalidateQueries({ queryKey: ['/api/projects'] });
        } else {
          setUser(null);
          // –û—á–∏—â–∞–µ–º –∫–µ—à –ø—Ä–∏ –≤—ã—Ö–æ–¥–µ
          queryClient.invalidateQueries({ queryKey: ['/api/templates/category/custom'] });
          queryClient.invalidateQueries({ queryKey: ['/api/templates'] });
          queryClient.invalidateQueries({ queryKey: ['/api/projects'] });
        }
      } catch (err) {
        console.error('Error in auth change handler:', err);
      }
    };

    // –°–ª—É—à–∞–µ–º –∏–∑–º–µ–Ω–µ–Ω–∏—è –≤ localStorage –æ—Ç –¥—Ä—É–≥–∏—Ö –≤–∫–ª–∞–¥–æ–∫
    const handleStorageChange = (e: StorageEvent) => {
      if (e.key === STORAGE_KEY) {
        try {
          if (e.newValue) {
            const parsedUser = JSON.parse(e.newValue);
            setUser(parsedUser);
            queryClient.invalidateQueries({ queryKey: ['/api/templates/category/custom'] });
            queryClient.invalidateQueries({ queryKey: ['/api/templates'] });
            queryClient.invalidateQueries({ queryKey: ['/api/projects'] });
          } else {
            setUser(null);
            queryClient.invalidateQueries({ queryKey: ['/api/templates/category/custom'] });
            queryClient.invalidateQueries({ queryKey: ['/api/templates'] });
            queryClient.invalidateQueries({ queryKey: ['/api/projects'] });
          }
        } catch (err) {
          console.error('Error parsing user from storage event:', err);
        }
      }
    };

    window.addEventListener(AUTH_EVENT, handleAuthChange);
    window.addEventListener('storage', handleStorageChange);
    return () => {
      window.removeEventListener(AUTH_EVENT, handleAuthChange);
      window.removeEventListener('storage', handleStorageChange);
    };
  }, []);

  const login = (userData: TelegramUser) => {
    setUser(userData);
    try {
      localStorage.setItem(STORAGE_KEY, JSON.stringify(userData));
      // –ö–†–ò–¢–ò–ß–ù–û: –£–¥–∞–ª—è–µ–º —Å—Ç–∞—Ä—ã–π –∫–µ—à –≥–æ—Å—Ç—è –∏ –∏–Ω–≤–∞–ª–∏–¥–∏—Ä—É–µ–º –Ω–æ–≤—ã–π –¥–ª—è –∞–≤—Ç–æ—Ä–∏–∑–æ–≤–∞–Ω–Ω–æ–≥–æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
      queryClient.removeQueries({ queryKey: ['/api/templates/category/custom', 'guest'] });
      queryClient.invalidateQueries({ queryKey: ['/api/templates/category/custom', userData.id] });
      queryClient.invalidateQueries({ queryKey: ['/api/templates'] });
      queryClient.invalidateQueries({ queryKey: ['/api/projects'] });
    } catch (e) {
      console.error('Error saving user to localStorage:', e);
    }
  };

  const logout = async () => {
    try {
      await fetch('/api/auth/telegram/logout', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
      });
    } catch (e) {
      console.error('Logout error:', e);
    }
    setUser(null);
    try {
      localStorage.removeItem(STORAGE_KEY);
      // –ò–Ω–≤–∞–ª–∏–¥–∏—Ä—É–µ–º –∫–µ—à –ø—Ä–∏ –≤—ã—Ö–æ–¥–µ
      queryClient.invalidateQueries({ queryKey: ['/api/templates/category/custom'] });
      queryClient.invalidateQueries({ queryKey: ['/api/templates'] });
      queryClient.invalidateQueries({ queryKey: ['/api/projects'] });
    } catch (e) {
      console.error('Error removing user from localStorage:', e);
    }
  };

  return { user, login, logout, isLoading };
}

