export function useProjects(options: UseUserDataOptions) {
  const mode: UserDataMode = options.isAuthenticated && options.userId ? 'server' : 'local';

  return useQuery({
    queryKey: ['/api/projects', mode],
    queryFn: async () => {
      if (mode === 'local') {
        return LocalStorageService.getProjects();
      }
